void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 bg = uv;
	float t = iTime;

    float carre = 40.;

    bg.x *= iResolution.x/iResolution.y;

    uv.x -= cos(t*2.)*.3+.5;
    uv.y -= abs(cos(t*3.))*.4+.25;

    uv.x *= iResolution.x/iResolution.y;

    vec3 rgb;

    float c = length(uv);
    if(c<.25){

        float nimp = 4.;
        // following line is stolen from https://www.shadertoy.com/view/XstXzs
        float nah = asin(uv.x*nimp / cos(asin(uv.y*nimp)))*.25;

        vec2 plop;
        plop.x= cos(carre*nah+t*carre*.5)+.5;
        plop.y= cos(uv.y*carre)+.5;


        if(plop.x < 0.5 ^^ plop.y < 0.5 ){
            rgb=vec3(1,0,0);
        }
        else rgb=vec3(1,1,1);
    }
    else{
        vec2 plop = cos(bg*carre*1.5);
        if (plop.x > 0.99 || plop.y > 0.99) rgb=vec3(.5,0.,.5);
        else rgb=vec3(.7,.7,.7);
    }


    // Output to screen
    fragColor = vec4(rgb,1.0);
}

//Pour changer la vitesse de rotation 
#define SPEED1 iTime
#define SPEED2 iTime*1.0
#define SPEED3 iTime*2.0
#define SPEED4 iTime*3.0
#define SPEED5 iTime*4.0
#define SPEED8 iTime*8.0


vec3 getSun(vec2 uv){
	float sun = 1.0 - distance(uv-0.9,iMouse.xy / iResolution.y);
    sun = clamp(sun,0.0,1.0);

    float glow = sun;
    glow = clamp(glow,0.0,1.0);

    sun = pow(sun,100.0);
    sun *= 100.0;
    sun = clamp(sun,0.0,1.0);

    glow = pow(glow,6.0) * 1.0;
    glow = pow(glow,(uv.y));
    glow = clamp(glow,0.0,1.0);

    sun *= pow(dot(uv.y, uv.y), 1.0 / 1.65);

    glow *= pow(dot(uv.y, uv.y), 1.0 / 2.0);

    sun += glow;

    vec3 sunColor = vec3(1.0,0.6,0.5) * sun;

    return vec3(sunColor);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

	vec2 q1 = p - vec2(0.2,0.5);
    vec2 q2 = p - vec2(0.5,0.5);
    vec2 q3 = p - vec2(0.8,0.5);

    vec3 sun = getSun(p);
    // Pour la couleur du ciel
    vec3 col = mix( vec3(1.0,1.0,1.0), vec3(0.0,0.0,1.0), sqrt(p.y) );




	float b = 0.015;

    //construction et ajout de deux batons 
    col *= 1.0 - (1.0-smoothstep(b,b+0.002, abs(q1.x-0.25*sin(q1.y))))*(1.0-smoothstep(0.0,0.0,q1.y));
    col *= 1.0 - (1.0-smoothstep(b,b+0.002, abs(q2.x-0.25*sin(q2.y))))*(1.0-smoothstep(0.0,0.0,q2.y));
    col *= 1.0 - (1.0-smoothstep(b,b+0.002, abs(q3.x-0.25*sin(q3.y))))*(1.0-smoothstep(0.0,0.0,q3.y));

	//construction de la tete des moulin Ã  vent
    float r1 = 0.05 + 0.2*cos( atan(q1.y,q1.x)*4.0 + SPEED8);
	col *= smoothstep( r1, r1+0.01, length( q1 ) );
    float r2 = 0.05 + 0.2*cos( atan(q2.y,q2.x)*4.0 - SPEED5);
    col *= smoothstep( r2, r2+0.01, length( q2 ) );
    float r3 = 0.05 + 0.2*cos( atan(q3.y,q3.x)*4.0 + SPEED8);
    col *= smoothstep( r3, r3+0.01, length( q3 ) );


	fragColor = vec4(col + sun,1.0);
}

// RENT-A-GHOST (C) ME

vec2 rot(vec2 p, float a)
{
float sa = sin(a), ca = cos(a);
return p * mat2(ca, -sa, sa, ca);
}   
float seg( in vec2 p, in vec2 a, in vec2 b )
{
vec2 ba = b-a;
vec2 pa = p-a;
float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
return length(pa-h*ba);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
vec2 p = (fragCoord.xy - iResolution.xy/2.) / iResolution.y;
p*=1.7+sin(p.x+time*0.9)*0.3;
p = rot(p,0.5+sin(time+p.y*1.2+p.x*0.2)*0.5);
p += sin(time*.87)*.7;
vec2 pp = rot(p,degrees(-45.0*0.5));
float d = length(pp-vec2(0.25,0.2))-0.04;
d = min(d,length(pp-vec2(0.0,0.3))-0.04);
pp = rot(pp,degrees(((pp.x-0.1)*-0.05))*0.75);
float d2 = seg(pp, vec2(-0.1,0.0), vec2(0.2,-0.1));
d2 += 0.02;
float d3 = length(pp-vec2(0.05,0.0))-0.02;
float v = 0.5+sin(time*1.1)*0.5;
d2 = mix(d3,d2,v);
float angle = atan(p.y, p.x);
float radius = length(p*p) * (1. + sin(p.x*6.0+angle+p.y*9.0 + time)*.1);
float ed = 0.055 + 0.02*sin(p.y*10.0+time*1. + angle);    
ed = -(ed /  (radius - 0.3+sin(20.0*p.x*p.y)*0.1));
ed -= abs(sin(p.y*0.1+time*1.3)*0.3);
vec3 col = vec3(0.5,0.7,0.1) * ed;
d = abs(d)+0.01;
d = 1.0-smoothstep(0.02,0.025,d);
col += d*vec3(1.5,1.5,1.5);
d2 = 1.0-smoothstep(0.03,0.04,d2);
col.rgb+=d2*vec3(0.9,0.9,0.9);
fragColor = vec4(1.-col,1.0);
}

//fireworks in heart shape
//np = no. of particles
//snp = no. of spawned particles

#define rad(x) radians(x)
#define np 50.
#define snp 40.
#define spawn 1
#define trail 1

//random value
vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
	return fract(vec2(a.x*a.y, a.y*a.z));
}
float hash(vec2 uv){
 	return fract(sin(dot(uv,vec2(154.45,64.548))) * 124.54)  ; 
}

//particle
vec3 particle(vec2 st, vec2 p, float r, vec3 col){
 	float d = length(st-p);
    d = smoothstep(r, r-2.0/iResolution.y, d);//d<r?1.0:0.0;
    return d*col;
}
//particle with light
vec3 burst(vec2 st, vec2 pos, float r, vec3 col, int heart) {
	st -= pos;
    if (heart==1) st.y -= sqrt(abs(st.x))*0.1;
    r *=0.6*r;
    return (r/dot(st, st))*col*0.6;
}

//displacement with s = p0 + ut + 0.5at^2
vec2 get_pos(vec2 u, vec2 a, vec2 p0, float t, float ang){
    ang = rad(ang);
    vec2 d = p0 + vec2(u.x*cos(ang), u.y*sin(ang))*t + 0.5*a*t*t;
    return d;
}
//velocity at time t
vec2 get_velocity(vec2 u, vec2 a, float t, float ang){
    ang = rad(ang);
    return vec2(u.x*cos(ang), u.y*sin(ang)) + a*t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float aspect = iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);
    float t = mod(iTime, 10.);

    //particle
    float r = 0.04;
    vec2 u = vec2(5.); //init velocity
    vec2 a = vec2(0.0, -9.8); //acc due to gravity
    float ang = 75.0; //angle of projection

    vec3 p1 = vec3(0.0); //particle

    for (float i=0.; i<np; i++)
    {
        vec2 rand = N22(vec2(i));
        vec2 ip = vec2(sin(15.*rand.x)*aspect, -1.+r); //initial pos
        u = vec2(sin(5.*rand.x), 5.+sin(4.*rand.y)); //initial velocity
        float t1 = t-i/5.;
    	vec2 s = get_pos(u, a, ip, t1, ang); // displacement
    	vec2 v = get_velocity(u, a, t1, ang);
        float Tf = 2.0*u.y*sin(rad(ang))/abs(a.y); //time of flight
		vec2 H = get_pos(u, a, ip, Tf/2.0, ang); //max height

        vec3 pcol = vec3(sin(22.*rand.x), sin(5.*rand.y), sin(1.*rand.x)); //color per particle

        if (v.y<-0.5){
            //p1+= burst(uv, H, max(0.0, 0.1-(t1*t1-Tf*0.5)), pcol*0.5, 1 );
        	r=0.0; //die
        }
        p1 += burst(uv, s, r, pcol, 0); //main particle

        //trail particle
        if (trail==1){
        	for (float k=4.0; k>0.0; k--){
                vec2 strail = get_pos(u, a, ip, t1-(k*0.02), ang); //delay for trail particles
        		p1 += burst(uv, strail, v.y<-0.5?0.0:r-(k*0.006), pcol, 0);
        	}
        }

        //spawn
        if (v.y<=0.0 && t1>=Tf/2.0 && spawn==1)
        {
            for (float j=0.0; j<snp; j++)
            {
                vec2 rand2 = N22(vec2(j));
                float ang2 = (j*(360./snp));
                r = 0.035; // radius of spawned particles
                r -= (t1-Tf*0.5)*0.04;
                //r/=(t-(Tf/2.)+0.2);

                float x = cos(rad(ang2)); //coords of unit circle 
                float y = sin(rad(ang2));

                y = y + abs(x) * sqrt( (8.- abs(x))/50.0 ); // heart shape as a function of j

                vec2 heart = vec2(x*x + y*y)*(0.4/(t1*sqrt(t1))); //velocity vector
                vec2 S = get_pos(heart, a*0.03, H, t1-(Tf/2.), ang2);
                pcol = vec3(sin(8.*rand2.x), sin(6.*rand2.y), sin(2.*rand2.x)); // color per spawn particle
                p1 += burst(uv, S, max(0.0,r), pcol, 0);

            }
        } 

    }

    //bg
    float stars = pow(hash(uv),200.) * 0.5;
    col = p1;
    vec3 night = vec3(0.06, 0.02, 0.18)*vec3(uv.y*0.5+0.5)+vec3(stars)*vec3(uv.y*0.5+0.5);;
    col += night*(1.0-p1);
    //col = vec3(light(uv, vec2(0.0), 0.1));
    fragColor = vec4(col,1.0);
}

// Source Material:
// Great source for all kinds of computer graphics stuff: http://iquilezles.org/
// Fractals: http://blog.hvidtfeldts.net/index.php/2011/06/distance-estimated-3d-fractals-part-i/

// Videos:
// Ray marching setup and HDR colors: https://www.youtube.com/watch?v=Cfe5UQ-1L9Q
// SDF maths: https://www.youtube.com/watch?v=sl9x19EnKng

const float PI = 3.141592;

vec2 fold(in vec2 p, in float angle)
{
    vec2 n = vec2(cos(angle), sin(angle));
    p -= 2.0 * min(0.0, dot(p, n)) * n;
    return p;
}

vec3 fold(vec3 p)
{
    float t = iTime;
    p.xy = fold(p.xy, -PI / 3.0 - cos(t * 0.5) * 0.1);
    p.xy = fold(p.xy, PI / 3.0);
    p.yz = fold(p.yz, -PI / 6.0 + sin(t * 0.4) * 0.022);
    p.yz = fold(p.yz, +PI / 6.0);

    return p;
}

vec3 curve(vec3 p)
{
    for (int i = 0; i < 11; ++i)
    {
        p *= 2.0;
        p.x -= 2.6;
        p = fold(p);
    }

    return p;
}

// Signed distance function that describes the scene
float sceneSDF(vec3 pos)
{
    pos.x += 1.7;
    pos = curve(pos);

    return length(pos * 0.0004) - 0.0006;
}

// Approximates the normal of the surface at the given position
// by calculating the gradient of the scene SDF
vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(0.0001, 0.0);

    return normalize(vec3(
        sceneSDF(pos + e.xyy) - sceneSDF(pos - e.xyy),
        sceneSDF(pos + e.yxy) - sceneSDF(pos - e.yxy),
        sceneSDF(pos + e.yyx) - sceneSDF(pos - e.yyx)));
}

// Returns the distance to scene surface.
// If the ray hit nothing this returns -1.0
float rayMarchScene(in vec3 rayOrigin, in vec3 rayDir)
{
    float t = 0.0;
    for (int i = 0; i < 100; ++i)
    {
        vec3 pos = rayOrigin + rayDir * t;

        float dist = sceneSDF(pos);
        t += dist;
        if (dist < 0.001)
            break;
        if (t > 100.0)
            return -1.0;
    }

    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;
    //vec2 mouse = vec2(0.5, 1.0);
    float time = mouse.x * 6.28;//iTime * 0.2;
    float cameraDist = mix(1.0, -0.1, mouse.y);

    vec3 rayOrigin = vec3(cos(time), 0.1, sin(time)) * cameraDist;
    vec3 lookAtPos = vec3(0.2, -0.3, 0.0);

    vec3 forward = normalize(lookAtPos - rayOrigin);
    vec3 right = cross(forward, vec3(0.0, 1.0, 0.0));
    vec3 up = cross(right, forward);

    vec3 rayDir = normalize(forward + right * uv.x + up * uv.y);

    float t = rayMarchScene(rayOrigin, rayDir);

    // Skybox color
    vec3 skyCol = vec3(0.3, 0.5, 0.4) - rayDir.y * 0.3;
    vec3 col = skyCol;
    if (t > 0.0)
    {
        vec3 surfacePos = rayOrigin + rayDir * t;
        vec3 normal = calcNormal(surfacePos);

        float fogStart = 0.3;
        float fogEnd = 1.0;
        float fog = smoothstep(fogStart, fogEnd, t);

        vec3 sunDir = normalize(vec3(-0.8, 0.4, 0.3));
        float sunDiffuse = clamp(dot(normal, sunDir), 0.0, 1.0);
        float sunShadow = step(rayMarchScene(surfacePos + normal * 0.0001, sunDir), 0.0);
        float skyDiffuse = clamp(0.5+0.5*dot(normal, vec3(0.0, 1.0, 0.0)), 0.0, 1.0);

        col = vec3(1.8, 1.0, 0.7) * sunDiffuse;
        col *= mix(0.5, 1.0, sunShadow);

        col = mix(col, skyCol, fog);
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));    
    fragColor = vec4(col, 1.0);
}

const float PI = 3.1415926;
const float E = 0.005;

struct Ray
{
    vec3 pos;
    vec3 dir;
};

// https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3 = fract(p3 * 0.1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(0.1031, 0.1030, 0.0973));
    p3 += dot(p3, p3.yxz + 33.33);
    return fract((p3.xxy + p3.yxx) * p3.zyx);
}

mat2 rotate2D(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat2(c, s, -s, c);
}

vec3 rotate(vec3 p)
{
    p.xz *= rotate2D(iTime * 0.2);
    p.yz *= rotate2D(0.5);
    return p;
}

float deBlock(vec3 p)
{
    vec3 pb = p;

    p.xz += 0.5;
	vec2 q = vec2(length(p.xz) - 0.5, p.y);
	float d = length(q) - 0.12;

	p = pb;
	p.yz -= 0.5;
	q = vec2(length(p.yz) - 0.5, p.x);
	d = min(d, length(q) - 0.12);

	p = pb;
	p.xy -= vec2(0.5, -0.5);
	q = vec2(length(p.xy) - 0.5, p.z);
	d = min(d, length(q) - 0.12);

	d = abs(d) - 0.0125;

	return d;
}

vec2 de(vec3 p)
{
    p = rotate(p);

    vec3 pb = p;

    vec3 id = floor(p);
    p = fract(p) - 0.5;

    vec3 r = hash33(id);
    if (r.x < 0.5)  p.x *= -1.0;
	if (r.y < 0.5)	p.y *= -1.0;
	if (r.z < 0.5)	p.z *= -1.0;

    float d = deBlock(p);
	float offset = smoothstep(0.45, 0.5, length(p));
	d -= offset * 0.03;

    p = pb;
    d = max(max(d, abs(p.x) - 5.0), abs(p.z) - 5.0);

    return vec2(d * 0.75, offset);
}

// iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 normal(vec3 p)
{
    float h = E;
    vec2 k = vec2(1.0, -1.0);
    return normalize(
            k.xyy * de(p + k.xyy * h).x + 
            k.yyx * de(p + k.yyx * h).x + 
            k.yxy * de(p + k.yxy * h).x + 
            k.xxx * de(p + k.xxx * h).x
        );
}

float ao(Ray ray)
{
    float d = 0.0;
    d += de(ray.pos + ray.dir * 1.0).x;
    d += de(ray.pos + ray.dir * 2.0).x;
    d += de(ray.pos + ray.dir * 3.0).x;
    d += de(ray.pos + ray.dir * 4.0).x;
    d += de(ray.pos + ray.dir * 5.0).x;
    return pow(d / 15.0, 2.0);
}

float noiseValue3D(vec3 p, float div)
{
    p *= div;
    vec3 i = floor(p);
    vec3 f = fract(p);
    float r1 = hash13((i + vec3(0.0, 0.0, 0.0)));
    float r2 = hash13((i + vec3(1.0, 0.0, 0.0)));
    float r3 = hash13((i + vec3(0.0, 1.0, 0.0)));
    float r4 = hash13((i + vec3(1.0, 1.0, 0.0)));
    float r5 = hash13((i + vec3(0.0, 0.0, 1.0)));
    float r6 = hash13((i + vec3(1.0, 0.0, 1.0)));
    float r7 = hash13((i + vec3(0.0, 1.0, 1.0)));
    float r8 = hash13((i + vec3(1.0, 1.0, 1.0)));
    return mix(
            mix(mix(r1, r2, smoothstep(0.0, 1.0, f.x)), mix(r3, r4, smoothstep(0.0, 1.0, f.x)), smoothstep(0.0, 1.0, f.y)),
            mix(mix(r5, r6, smoothstep(0.0, 1.0, f.x)), mix(r7, r8, smoothstep(0.0, 1.0, f.x)), smoothstep(0.0, 1.0, f.y)),
            smoothstep(0.0, 1.0, f.z)
        );
}
float noiseValueFbm3D(vec3 p, float div, int octaves, float amplitude)
{
    float o = 0.0;
    float fbm_max = 0.0;
    for(int i = 0; i >= 0; i++)
    {
        if(i >= octaves)    break;
        float a = pow(amplitude, float(i));
        o += a * noiseValue3D(p, div * pow(2.0, float(i)));
        fbm_max += a;
    }
    return o / fbm_max;
}

void trace(Ray ray, inout vec3 color, float md)
{
    vec3 or = ray.pos;

    float ad = 0.0;
    for (float i = 1.0; i > 0.0; i -= 1.0 / 120.0)
    {
        vec2 o = de(ray.pos);
        if (o.x < E)
        {
            vec3 n = normal(ray.pos);

            // diffuse
            vec3 ld = normalize(vec3(1.0, 0.75, 0.5));
            float l = pow(dot(n, ld) * 0.5 + 0.5, 3.0) * 3.0;

            // specular
            vec3 h = normalize(ld + normalize(or - ray.pos));
            float hn = max(dot(h, n), 0.0);
            float s1 = pow(hn, 15.0) * 0.75;
            float s2 = pow(hn, 50.0) * 15.0;

            // ao
            Ray rayAo;
            rayAo.pos = ray.pos;
            rayAo.dir = n;
            float a = ao(rayAo);

            // noise
            vec3 p = rotate(ray.pos);
            float ns = noiseValueFbm3D(p, 10.0, 4, 0.5);
            ns = noiseValueFbm3D(p + vec3(ns), 10.0, 4, 0.5);

            // color
            color += mix(
                    vec3(0.01, 0.1, 1.0) + s2 * ns * ns * ns, 
                    (mix(vec3(0.2, 0.2, 0.2), vec3(0.15, 0.15, 0.3), ns) + s1) * max(ns, 0.35), 
                    smoothstep(0.8, 1.0, 1.0 - o.y)
                ) * l * a;

//            color = n * 0.5 + 0.5;
//            color = vec3(a);

            return;
        }

        ray.pos += ray.dir * o.x;
        ad = ad + o.x;
        if (ad > md)
        {
            break;
        }
    }

    // background
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    // view
    vec3 view = vec3(0.0, 0.0, 10.0);
    vec3 at = normalize(vec3(0.0, 0.0, 0.0) - view);
    vec3 right = normalize(cross(at, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, at);
    float focallength = 1.75;

    // ray
    Ray ray;
    ray.pos = view;
    ray.dir = normalize(right * p.x + up * p.y + at * focallength);

    // ray marching
    trace(ray, color, 20.0);

    // cheap tonemapping
    // https://www.desmos.com/calculator/adupt0spl8
    float k = 0.75;
    color = mix(color, 1.0 - exp(-(color - k) / (1.0 - k)) * (1.0 - k), step(k, color));

    // gamma correction
    color = pow(color, vec3(0.454545));

    fragColor = vec4(color, 1.0);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u); //large odd digits, 0 and 1 are fully mixed

float hash11(uint n){
    n ^= (n << 9);
    n ^= (n >> 1);
    n ^= (n << 1);
    n *= k.x;
    return float(n) / float(0xffffffffu);
}
vec2 hash22(uvec2 n){
    n ^= (n.yx << 9);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    n *= k.xy;
    return vec2(n) / float(0xffffffffu);
}
vec3 hash33(uvec3 n) {
    n ^= (n.yzx << 9);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    n *= k;
    return vec3(n) / float(0xffffffffu);
}

