float pMod1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float ssub( float d1, float d2, float k )
{
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

// simplified :)
float _cindex = 0.0;			// colour index
float map( in vec3 p )
{
    float c = 3.3+pMod1(p.y,2.5);
    p.x += sin(iTime+c*0.5)*4.0;
    c *= 7.7+pMod1(p.x,2.5);
    float d1 = length(p)-0.9;
    float d2 = length(vec2(p.z-1.6,p.y))-(1.0-pow((sin( mod(iTime+c*0.35, 200.))*0.5+0.5), 40.));
    d2 = ssub(d2, d1-0.1, 0.05);
    _cindex = 2.0+step(d1,d2);
    return  min(d2, d1);    
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

