void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 bg = background(uv)*0.65;
     // camera movement	
    float an = sin(iTime*0.7)*0.35;
    float dist = 8.0+sin(iTime*0.8)*4.0;
    an+=PI*0.5;
    float y = 0.0;

    if (iMouse.z>0.5)
    {
        an=iMouse.x/iResolution.x*4.0;
        y = (iMouse.y/iResolution.y)*8.0;
        y-=4.0;
    }

	vec3 ro = vec3( dist*cos(an), -y, dist*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 40.0;
        float t = 0.0;
        for( int i=0; i<MARCHSTEPS; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting	
        vec3 col = bg;	//vec3(0.1);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );

            // nasty :)
            vec3 ambcol =  vec3(0.65, 0.4, 0.35);
            if(_cindex > 2.5)
				ambcol = eyetex(vec2((0.25-( atan(nor.z, nor.x) / TAU)) * 12.0, -( asin(nor.y) / PI)*6.0))*0.9;
            col = (ambcol*ambcol)*(dif+0.25);
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif


	fragColor = vec4( tot, 1.0 );
}

