float sceneMap3D(vec3 pos)
{
    float minT = 10000.0;
    float t = minT;
    for(int i = 0; i < primitives.length(); i++)
    {
        if(primitives[i].primitiveId == SQUARE_PLANE)
        {
        	vec4 curNor = normalize(rotMat(primitives[i].transform) * vec4(0.0, 0.0, 1.0, 0.0));
            float maxOffset = max(max(abs(primitives[i].transform.trans.x), 
                                      abs(primitives[i].transform.trans.y)), 
                                      abs(primitives[i].transform.trans.z));
            curNor.w = maxOffset;
        	t = squarePlane(pos, curNor);
        }
        else if(primitives[i].primitiveId == CUBE)
        {
        	t = cube(pos, primitives[i].transform);
        }


        if(t < minT)
        {
        	minT = t;
        }
    }
    t = minT;
    return t;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = -1.0;
        int obj = -1;
        sceneMap3D(origin + t * dir, m, obj);
        if(m < 0.0001) 
           return 0.0;
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

vec3 computeNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj) {
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(primitives[hitObj].primitiveId == CUBE)
        {
        	if(abs(m) < 0.01) 
                return;
        }
        else if(primitives[hitObj].primitiveId == SQUARE_PLANE)
        {
            if(abs(m) < 0.01) 
            {
            	return;
            }

        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

const vec3 a = vec3(0.5, 0.5, 0.5);
const vec3 b = vec3(0.5, 0.5, 0.5);
const vec3 c = vec3(2.0, 1.0, 1.0);
const vec3 d = vec3(0.5, 0.2, 0.25);

vec3 colorPalette(float t) {
    return a + b * cos(6.28 * (t * c + d));
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    #if ATTENUATION
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    #endif
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view, float thick) {
    vec3 lightVec = normalize(light - p);
    float lambert = max(0.0, dot(lightVec, n));
    if(hitObj == -1)
        return vec3(0.0, 0.0, 0.0);
    if(hitObj == 1)
    {
    	vec3 color = primitives[hitObj].matColor * lambert * vec3(1.0, 1.0, 1.0);
        float sub = subsurface(lightVec, n, view, thick);
        color = color + vec3(1.0, 0.0, 0.0) * sub * vec3(1.0, 1.0, 1.0);
        //color = vec3(sub);
        color *= softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
    	return color;
    }
    vec3 color = primitives[hitObj].matColor * lambert * vec3(1.0, 1.0, 1.0);
	color *= softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
    return color;

}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

Intersection sdf3D(vec3 dir, vec3 eye) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    isect = isect + 0.01 * nor;
    //vec3 lightDir = normalize(lightPos - isect);

    vec3 light = rotateY(pointLight.transform.trans, iTime);
    float thickness = fiveTapAO(isect, -nor, FIVETAP_K);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, light, normalize(eye - isect), thickness);


    vec3 normalAsColor = 0.5 * (nor + vec3(1.0, 1.0, 1.0));

    return Intersection(t, surfaceColor, isect, hitObj);
}




// Returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F)); 

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

