#define p(x) (max(0.0, 4.0 * (x) * (1.0 - (x))))

vec3 color(float t) {
    float r = p(t * 2.0);
    float g = p(t * 2.0 - 0.5);
    float b = p(t * 2.0 - 1.0);

    return vec3(r, g, b);
}

vec2 complex_exponent(vec2 c1, float f) {
    float r = length(c1);
    float phi = atan(c1.y, c1.x);

    float r_exponent = pow(r, f);

    return vec2(r_exponent * cos(f * phi), r_exponent * sin(f * phi));
}

vec2 mult_complex(vec2 c1, vec2 c2) {
    return vec2(c1.x * c2.x - c1.y * c2.y, c1.x * c2.y + c1.y * c2.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord -.5* iResolution.xy) / iResolution.y;

    // our uv coordinates, transformed to make the shape fit the window more nicely
    vec2 c = 3. * uv;

    // our exponent for composed function. 2.0 is a mandelbrot set, but many exponents look good!
    float min_exponent = 2.0;
    float max_exponent = 10.0;
    float exponent = (min_exponent + max_exponent) * 0.5 + (max_exponent - min_exponent) * 0.5 * sin(iTime * 1.0);

    vec2 z = vec2(0., 0.);
    vec2 f = z;
    for (int i = 0; i < N; i++) {
        f = complex_exponent(f, exponent) + c;
    }

    float d = distance(f, z);

  	float intensity = (1.0 - d) * (1.0 - d);

    fragColor = vec4(color(intensity), 1.0);
}

