#define M_PI 3.1415926535897932384626433832795

const int MAX_MARCHING_STEPS = 100; // How many marching iterations should we do before giving up
const float MAX_MARCHING_DIST = 100.0; // How far should we march for before giving up and calling it "sky"
const float MARCHING_SURFACE_DIST = 0.01; // How close should we march towards some geometry before we consider it a hit
const float MAX_LIGHT_BOUNCES = 6.0; // How many times we should attempt to bounce light in shadowed areas before giving up

const vec3 WORLD_UP = vec3(0.0, 1.0, 0.0);
const vec3 VIEW_TARGET = vec3(0.0, 2.5, 0.0);
const float ZOOM_FAC = 1.0;

const vec3 COL_PINK = vec3(0.969, 0.659, 0.722);
const vec3 COL_BLUE = vec3(0.333, 0.804, 0.988);

/** Given a point in 3D space, figure out how far away we are from the nearest piece of geometry */
float getDistToGeom(vec3 point) {
    float minDist = MAX_MARCHING_DIST;

    // Sphere object
    vec3 sphere1Pos = vec3(-2.0, 1.0, 0.0);
    float sphere1Radius = 1.0;
    float sphere1Dist = length(point - sphere1Pos) - sphere1Radius;
    minDist = min(minDist, sphere1Dist);

    // Sphere object
    vec3 sphere2Pos = vec3(2.0, 0.5, 0.0);
    float sphere2Radius = 0.5;
    float sphere2Dist = length(point - sphere2Pos) - sphere2Radius;
    minDist = min(minDist, sphere2Dist);

    // Many sphere objects
    float rot = sin(iTime * 2.0) * 0.5 + 0.5;
    for (float i = 0.0; i < 2.0 * M_PI; i += M_PI / 6.0) {
        // vec3 spherePos = vec3(cos(i * 4) + sin(iTime * 2 + i), 4 + sin(i * 4) + cos(iTime * 2 + i), sin(i));
        vec3 spherePos = vec3(sin(iTime * 4.0 + i) * (1.0 - rot) + cos(iTime * 4.0 + i) * rot, 3.0 + cos(iTime + i), sin(iTime * 4.0 + i) * rot + cos(iTime * 4.0 + i) * (1.0 - rot));
        float sphereRadius = 0.15;
        float sphereDist = length(point - spherePos) - sphereRadius;
        minDist = min(minDist, sphereDist);
    }

    // Ground plane distance can be calculated as just the point offset from the ground
    float planeDist = point.y;
    minDist = min(minDist, planeDist);

    // Return whatever distance is closest to the point
    return minDist;
}

/** Given a ray origin and ray direction, figure out how far the ray goes before hitting some geometry */
float rayMarch(vec3 rayOrigin, vec3 rayDir) {
    float distFromOrigin = 0.0;

    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        vec3 currentPos = rayOrigin + rayDir * distFromOrigin;

        float distToGeom = getDistToGeom(currentPos);
        distFromOrigin += distToGeom; // March forward

        // Check if we should exit the loop
        if (distFromOrigin > MAX_MARCHING_DIST || distToGeom < MARCHING_SURFACE_DIST) break;
    }

    return distFromOrigin;
}

/** Given a point on a surface in 3D space, figure out its normal direction */
vec3 getNormal(vec3 point) {
    float dist = getDistToGeom(point);

    return normalize(dist - vec3(
                getDistToGeom(point - vec3(0.01, 0.0, 0.0)),
                getDistToGeom(point - vec3(0.0, 0.01, 0.0)),
                getDistToGeom(point - vec3(0.0, 0.0, 0.01))
                ));
}

/** Gets the pixel color of a point in 3D space */
float getLight(vec3 point, vec3 lightPos) {
    vec3 lightDir = normalize(lightPos - point);
    vec3 surfaceNorm = getNormal(point);

    float diffuse = max(0.0, dot(lightDir, surfaceNorm));

    // Now, let's raymarch towards our light source
    // if it turns out we can't raymarch all the way to the light, that means we hit an object along the way
    // meaning we should be in shadow

    // Adjust the point so we don't start inside some geometry
    bool rayEscaped = false;
    for (float i = 0.0; i < MAX_LIGHT_BOUNCES; i++) {
        vec3 pointAdj = point + surfaceNorm * MARCHING_SURFACE_DIST * 3.0;

        float distTowardsLight = rayMarch(pointAdj, lightDir);
        if (distTowardsLight < length(lightPos - point)) {
            // We hit something - let's do a light bounce
            vec3 hitPoint = pointAdj + lightDir * distTowardsLight;
            vec3 hitNormal = getNormal(hitPoint);

            float bounceMarchDist = rayMarch(hitPoint, hitNormal);
            vec3 bouncePoint = hitPoint + hitNormal * bounceMarchDist;
            vec3 bounceNorm = getNormal(bouncePoint);

            diffuse *= pow(1.0 - (1.0 / MAX_LIGHT_BOUNCES), i);

            point = bouncePoint;
            surfaceNorm = bounceNorm;
        } else {
            rayEscaped = true;
        }
    }

    // If a ray never made it to the light source, make it pitch black
    if (!rayEscaped) {
        diffuse = 0.0;
    }

    return diffuse;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ratio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    uv -= 0.5; // Move the origin to the middle of the screen
    uv.x *= ratio; // Account for the aspect ratio

    vec3 col = vec3(0.0, 0.0, 0.0);

    vec3 rayOrigin = vec3(cos(iTime * 0.2) * 12.0, 1.5 + sin(iTime * 0.5), sin(iTime * 0.2) * 12.0); // Camera position

    vec3 camForward = normalize(VIEW_TARGET - rayOrigin);
    vec3 camRight = normalize(cross(WORLD_UP, camForward));
    vec3 camUp = cross(camForward, camRight);

    vec3 screenCenter = rayOrigin + camForward * ZOOM_FAC; // Figure out where the center of the screen is in 3D space
    vec3 intersectPoint = screenCenter + (uv.x * camRight) + (uv.y * camUp);

    vec3 rayDir = intersectPoint - rayOrigin;

    float rayDist = rayMarch(rayOrigin, rayDir);
    vec3 point = rayOrigin + rayDir * rayDist; // Get the point in 3D space

    vec3 lightPos;
    lightPos = vec3(0.0 + sin(iTime * 1.5) * 8.0, 5.0, 6.0 + cos(iTime * 1.5) * 8.0);
    col += getLight(point, lightPos) * COL_BLUE * (sin(iTime * 0.3) + 1.0) * 0.5;

    lightPos = vec3(0.0 + sin(iTime) * 2.0, 2.0, 6.0 + cos(iTime) * 2.0);
    col += getLight(point, lightPos) * COL_PINK * (cos(iTime * 0.3) + 1.0) * 0.5;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

#define N_ITER 80.
#define N_REF 3.
#define PI 3.1415926535

#define BOX vec3(5, 5, 1000)


// some chaotic function
float hash(){
    float time = iTime;
	return clamp(floor(fract(sin(time)*cos(time))*2.*sin(time*1.45)+0.5), 0., 1.);
    //float time = mod(iTime*0.01, 50.);
    //return abs(floor(cos(3.*fract(exp(.2*(time*time+30.))))));
}

// 2D rotation matrix
mat2 rot(float a){ // rotation matrix
	return mat2(cos(a), -sin(a),
               sin(a), cos(a));
}

// box sdf
float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// sin pulse function based on time : control the cube shape
float pulse(){
    float x = mod(iTime*10., 15.);
    if(x > PI*2.) return 0.;
    return 0.5 + 0.5*sin(x-PI/2.);
}

float map(in vec3 p, out int id){	
    float d;

    // cube
    vec3 c = vec3(0., -3.5, 7); // center of the cube
    // animate the cube
    vec3 q = p - c;
    float t = iTime*0.4;
    q.xz *= rot(iTime*0.5);
    q.yz *= rot(iTime*0.6);
    q.xy *= rot(iTime*0.8);
    // sdf with displacement
    float d1 = box(q, vec3(0.7)) + 0.1/distance(c, p) * pulse();

    // outer box (walls)
   	float d2 = -box(p, BOX);

    if(d1 < d2){
    	id = 0; d = d1;
    } else {
    	id = 1; d = d2;
    }
    return d;
}

float map(in vec3 p){ // overloaded simplified method
   int _; return map(p, _);
}

vec3 getNormal(vec3 p) {
    const float eps = 0.0001;
    return normalize(vec3(
        map(vec3(p.x + eps, p.yz)) - map(vec3(p.x - eps, p.yz)),
        map(vec3(p.x, p.y + eps, p.z)) - map(vec3(p.x, p.y - eps, p.z)),
        map(vec3(p.xy, p.z + eps)) - map(vec3(p.xy, p.z - eps))
    ));
}


float raymarch(vec3 ro, vec3 rd, out float shad, out int id){
    bool hit = false;
    float h, t = 0.;

    for(float i = 0.; i < N_ITER; ++i){
    	h = map(ro + rd * t, id);

        if(h < 0.001){
            shad = i/N_ITER;
            hit = true;
            break;
        }

        t += h;
    }

	return hit ? t : 0.;
}

// get texture intensity : create the stripe pattern
float wall(float z, vec3 n){

    float t = iTime*4.;
    float w = 1.5; // width of a stripe
    float r = 1.15; // ratio light surface / drak reflective surface per stripe 


    float u = abs(z - BOX.z) - t; // position along depth axis
    float a = mod(u, w*r);
    float k = step(w, a); // black or bright surface

    float h = hash();
    if(h == 0.)
        if(n.x != 0.){k = 1. - k;} // change animation when hash changes

    float c = h == 0. ? 7. : 12.; // number of stripes per wave of light
    float s = h == 0. ? 2. : 15.; // waves speed
    float d = h == 0. ? 1. : -1.; // direction of light waves
    float st = floor(iTime*s);
    // create the stripe pattern, with uniform color
    // and make light stripes move forward at st speed with steps
    float b = floor(mod(d*u + st*w*r , c*w*r)/w/r);

    k /= clamp(exp(b*0.4)*1.7, 1., 100.); // decay color intensity

    return k;
}


vec3 matcol(){ // color of the cube
    return vec3(1., 1., 5.); 
}

vec3 matcol(float k){ // color of the walls, depends on k
    return k <= 0. ?
        vec3(0.4) :
    	vec3(1., 0.5, 0.7) * k * 4.;
}


vec3 noise(vec3 p){
    vec3 tex =	texture(iChannel0, p.xz).rgb;
    tex += 		texture(iChannel0, p.yx).rgb;
    tex += 		texture(iChannel0, p.yz).rgb;
    return tex;
}

vec3 disp1(vec3 p){ // normal displacement for the cube's surface
	return noise(p)/3.*0.015;
}

vec3 disp2(vec3 p){ // normal displacement for the reflective surfaces of the walls
	return 0.1/noise(p*0.25)/3.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // ray
    vec3 ro = vec3(0., -3., 0.);
    vec3 rd = normalize(vec3(uv, 1.));

    // animate the camera
    rd.xy *= rot(sin(iTime*0.5)*0.5);
    rd.yz *= rot(sin(iTime)*0.1);

    // pixel color
    vec3 col = vec3(0.);

    // RAYMARCHING

    int id = -1; // id of the object we hit
    float shad; // ration number of steps / number of iterations

    float t, k;

    float i;
    for(i = 0.; i <= N_REF; ++i){
    	t = raymarch(ro, rd, shad, id);

        if(t == 0.){ // cancel if error detection
            col = vec3(0.);
            break;
        }

        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);

        vec3 scol;

        float decay = clamp((1.-shad-exp(p.z*0.05)*0.01), 0., 1.); // fade along depth axis

        if(id == 0){ // if we hit the cube
            scol = matcol() * decay; // get the material color, with fading along depth axis
            n += disp1(p); // normal displacement
        } else { // if we hit the walls
            k = wall(p.z, n);
            scol = matcol(k) * decay;
            n += disp2(p);
        }

        if(i == 0.) col = scol; // initial color
        else col *= scol * (1.-t*0.05); // reflection with fading proportional to length of the reflected ray

        if(k > 0.) break; // no reflection if we hit a light stripe

        // reflect ray
        ro = p + n * 0.01;
        rd = reflect(rd, n);
    }

    // some post processing
    col *= 1.1;
    col += vec3(0., 0.1, 0.);

    // Output to screen
    fragColor = vec4(col,1.);
}

// Comment this if topping is way too laggy for you.
#define NICE_TOPPINGS

float smin(float a, float b, float r) {
    float h = clamp(0.5 + 0.5 * (b - a) / r, 0.0, 1.0);
    return mix(b, a, h) - r * h * (1.0 - h);
}

vec2 rand2d(vec2 u) {
    return fract(sin(
        vec2(dot(u, vec2(12.345, 67.89)),
            dot(u, vec2(512.215, 91.51)))
    ) * 42123.45678) * 2.0 - 1.0;
}

vec3 rand3d(vec3 u) {
    return fract(sin(
        vec3(dot(u, vec3(12.345, 67.89, 1.56)),
            dot(u, vec3(512.215, 91.51, 918.12)),
            dot(u, vec3(12.567, 84.23, 92.5)))
    ) * 42123.45678) * 2.0 - 1.0;
}

float ball(vec3 p, vec3 off, float r) {
    return length(p - off) - r;
}

float sol(vec3 p) {
    return p.y;
}

float cylinder(vec3 p, vec3 off, float ra, float rb, float h) {
    p = p - off;
    vec2 d = vec2(length(p.xz) - 2.0 * ra + rb, abs(p.y) - h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - rb;
}

float topping(vec3 p, vec3 off) {
    float v = cylinder(p, off, 0.161, 0.1, -0.05);
    vec3 currentOff = vec3(0.0, 0.0, 0.0);

#ifdef NICE_TOPPINGS
    for (int i = 0; i < 10; i++) {
        vec2 deltaOff = vec2(sin(float(i)) * 0.29, cos(float(i)) * 0.3);
        currentOff = vec3(deltaOff.x, -0.05, deltaOff.y);
        v = smin(v, ball(p, off + currentOff, 0.015 + cos(float(i * 3)) * 0.01), 
            0.1 + (sin(float(i * 2)) * 0.005 + 0.001));
    }
#endif
    return v;
}

float grapes(vec3 p, vec3 off) {
    float v = ball(p, off + vec3(0.1, 0.0, 0.0), 0.03);
    v = min(v, ball(p, off + vec3(0.03, 0.0, 0.0), 0.03));
    v = min(v, ball(p, off + vec3(0.05, 0.0, -0.06), 0.03));
    v = min(v, ball(p, off + vec3(0.06, 0.04, -0.025), 0.03));

    for (int i = 0; i < 5; i++) {
        vec2 rnd = rand2d(vec2(float(i + 2), float(i))) * 0.2;
        v = min(v, ball(p, off + vec3(rnd.x, 0.0, rnd.y), 0.03));
    }
    return v;
}

float line(vec3 p, vec3 a, vec3 b, float r) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float candle(vec3 p, vec3 off) {
    p = p - off;
    return line(p, vec3(0.0, -0.02, 0.1), vec3(0.0, 0.08, 0.11), 0.02);
}



