vec2 map(vec3 p) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = sol(p);
    if (dist < closest) { closest = dist; id = 0.5; }

    dist = cylinder(p, vec3(0.0, 0.1, 0.0), 0.2, 0.1, 0.1);
    if (dist < closest) { closest = dist; id = 1.5; }

    dist = cylinder(p, vec3(0.0, 0.36, 0.0), 0.16, 0.1, 0.09);
    if (dist < closest) { closest = dist; id = 2.5; }

    dist = topping(p, vec3(0.0, 0.51, 0.0));
    if (dist < closest) { closest = dist; id = 3.5; }

    dist = grapes(p, vec3(0.0, 0.585, 0.0));
    if (dist < closest) { closest = dist; id = 4.5; }

    dist = candle(p, vec3(0.0, 0.585, 0.0));
    if (dist < closest) { closest = dist; id = 5.5; }

//    dist = age(p, vec3(0.0, 0.6, 0.0));
//    if (dist < closest) { closest = dist; id = 5.5; }

    return vec2(closest, id);
}

vec2 intersect(vec3 ro, vec3 rd) {
    float depth = 0.0;
    float id = -1.0;

    for (int i = 0; i < 200; i++) {
        vec2 result = map(ro + depth * rd);
        if (result.x <= 0.001) {
            id = result.y;
            break;
        }
        depth += result.x;
    }
    return vec2(depth, id);
}

#define PI acos(-1.0)
vec3 bottomCakeColor(vec3 p) {
    vec3 baseColor = vec3(1.2, 1.2, 0.7);
    vec3 toppingColor = vec3(1.0, 0.5, 0.01);
    float dist = pow(max(p.y - 0.25, 0.0), 0.2);
    vec3 color = mix(baseColor, toppingColor, clamp(dist, 0.0, 1.0));

    p = normalize(p);
    float rad = atan(p.x / p.z) / (2.0 * PI);
    float v = sin(rad) * 10.0;
    float yDist = 1.0 - pow(max(abs(v - p.y), 0.0), 2.0);
    yDist = 1.0 - clamp(pow(yDist, 0.2), 0.0, 1.0);
    vec3 chocolat = vec3(0.82, 0.411, 0.117);
    color = mix(color, chocolat, yDist);
    return vec3(color);
}

