vec3 getColor(float id, vec3 p) {
    if (id < -0.5) { return vec3(0.0); }
    if (id < 1.0) { return vec3(1.05, 1.0, 1.0); }
    if (id < 2.0) { return bottomCakeColor(p); }
    if (id < 3.0) { return topCakeColor(p); }
    if (id < 4.0) { return vec3(0.82, 0.411, 0.117) * 0.4; }
    if (id < 5.0) { return vec3(0.435, 0.176, 0.658); }
