vec3 getNormal(vec3 p, vec2 info) {
    const float epsilon = 0.001;
    vec3 n = normalize(vec3(
        map(p).x - map(vec3(p.x - epsilon, p.yz)).x,
        map(p).x - map(vec3(p.x, p.y - epsilon, p.z)).x,
        map(p).x - map(vec3(p.xy, p.z - epsilon)).x
    ));
    return n;
}

float softShadow(vec3 ro, vec3 rd, float k) {
    float depth = 0.01;
    float res = 1.0;
    vec2 dv = vec2(1.0, -1.0);
    for (int i = 0; i < 30; i++) {
        dv = map(ro + rd * depth);
        res = min(res, smoothstep(0.0, 1.0, k * dv.x / depth));
        if (res <= 0.0001) {
            break;
        }
        depth += clamp(dv.x, 0.02, 2.0);
    }
    return res;
}

float directLighting(vec3 pos, vec3 n, vec3 lightPos, vec3 lightDir) {
    float shadowIntensity = softShadow(pos + n * 0.001, lightDir, 5.0);
    return shadowIntensity;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    float r = 1.0;
    vec3 ro = vec3(sin(iTime * 0.1) * r, 1.0 + (sin(iTime * 0.05) * 0.5 + 0.5), cos(iTime * 0.1) * r);
    vec3 center = vec3(sin(iTime * 0.5) * 0.1, 0.5 + (cos(iTime * 0.1) * 0.05), cos(iTime * 0.5) * 0.1);

    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 info = intersect(ro, rd);
    vec3 pos = ro + info.x * rd;
    vec3 n = getNormal(pos, info);

    vec3 lightPos = vec3(1.5, 2.0, 0.0);
    vec3 lightDir = normalize(lightPos);

    float attenuation = 1.0 * smoothstep(0.9, 1.0, dot(normalize(lightPos - pos), lightDir));
    float ambient = 1.0;
    float diffuse = max(dot(n, pos), 0.0);
    float back = max(dot(n, vec3(-lightDir.x, 0.0, -lightDir.z)), 0.0);
    float top = clamp(n.y * 0.8 + 0.2, 0.0, 1.0);
    float fr = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 3.0);
    float sol = clamp(-n.y * 0.8 + 0.2, 0.0, 1.0) * pow(clamp(1.0 - pos.y, 0.0, 1.0), 2.0);
    float shadow = directLighting(pos, n, lightPos, lightDir);
    float specular = 0.0;
    if (info.y > 3.1 && info.y < 5.0) {
        vec3 refl = reflect(-lightDir, n);
        specular = pow(max(dot(-rd, refl), 0.0), 16.0);
    }

    vec3 light = vec3(0.0);
    light += ambient * vec3(0.2, 0.2, 0.2) * attenuation * shadow;
    light += diffuse * vec3(0.73, 0.7, 0.7) * attenuation;
    light += back * vec3(0.2, 0.1, 0.1) * attenuation;
    light += top * vec3(0.3, 0.3, 0.3) * attenuation;
    light += fr * vec3(0.98, 1.0, 0.86) * attenuation;
    light += sol * vec3(0.1, 0.1, 0.1) * attenuation;
    light += specular * vec3(5.0, 5.0, 5.0) * attenuation * shadow;

    vec3 color = light * getColor(info.y, pos);

    color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

#define MAX_STEPS 80
#define MAX_DIST 80.
#define SURF_DIST .0001

float sinPatt(vec3 p){
	return 0.1 + abs(sin(p.x) - cos(p.y) + sin(p.z))*1.2 ;
}
float getDist(vec3 p) {
	vec4 s = vec4(0, 0, 0, 2);
    //s.xz += vec2(2.*cos(iTime),2.*sin(iTime));
    float angle = iTime*0.5;
    float si = sin(angle);
    float co = cos(angle);

    float scale = 5.+4.*sin(angle);
    mat2 rotationMatrix = mat2( co, si,
                               	-si,  co);
    //s.w = s.w*(1.2+0.2*sin(angle));
    vec2 p1 = rotationMatrix*p.xz ;
    p = vec3(p1.x,p.y,p1.y);
    float sphereDist =  length(p-s.xyz)-s.w;
    float d = max(sphereDist,(0.85-sinPatt(p*(scale)))/scale);
    return  d;
}
float rayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = getDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}
vec3 getNormal(vec3 p) {
	float d = getDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy),
        getDist(p-e.yxy),
        getDist(p-e.yyx));

    return normalize(n);
}

vec3 getColor(vec3 p) {
