    vec3 col = vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) * cos(6.28319 * (vec3(0.2, 0.0, 0.0) + amount * vec3(1.0, .9, 0.8)));
    return col*amount ;
}
