uint hash( uint x ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
float random( float f ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    const uint mantissaMask = 0x007FFFFFu;
    const uint one          = 0x3F800000u;

    uint h = hash( floatBitsToUint( f ) );
    h &= mantissaMask;
    h |= one;

    float  r2 = uintBitsToFloat( h );
    return r2 - 1.0;
}

