float zoom = 0.1;
vec2 center = vec2(-0.58, 0.5);
float PI = 3.14159265359;
int max_i = 200;
float POWER = 2.0;

vec2 toPolar(vec2 c)
{
    float r = length(c);
    float arg = 0.0;

    arg = atan(c.y/c.x);
    return vec2(r, arg);
}
vec2 toCartesian(vec2 pol)
{
    vec2 c;
    c.x = pol.x*cos(pol.y);
    c.y = pol.x*sin(pol.y);
    return c;
}

int iterate(vec2 c)
{
    vec2 pol;
    vec2 c_temp = c;
    for(int i = 0; i < max_i; i++)
    {
        if(length(c_temp) > 2.0)
            return i;
        pol = toPolar(c_temp);
        pol.x = pow(pol.x, POWER);
        pol.y *= POWER;
        c_temp = toCartesian(pol);
        c_temp += c;
    }
    return max_i;
}

