float Scan2DDA2(vec2 pa, vec2 pb)
{ 
    vec2 ro = pa, pd = pb - pa
    , rd = normalize(pd), nrd = normalize(rd)
    , ad = abs(pd)
    , dd = 1. / abs(nrd)
    , pos = ro
    , sd = fract(pos);
    ivec2 mp = ivec2(floor(pa)) // tile iterator
    , aid = abs(ivec2(floor(pb))-mp)
    , st = ivec2(sign(rd)); // step direction
    int side = 0;
    if (st.x > 0) sd.x = 1. - sd.x;
    if (st.y > 0) sd.y = 1. - sd.y;
    sd *= dd;
    int nsteps = 1+aid.x+aid.y;
    for (int i = nsteps; --i >= 0; ) {
        if (Condition(mp))
            if (!Traverse(mp))
            	break;
        side = int(sd.x >= sd.y);
        switch (side) {
            case 0: sd.x += dd.x; mp.x += st.x; break;
        	case 1: sd.y += dd.y; mp.y += st.y; break;
        }
    }
    float dist = float(mp[side]) - pos[side];
    dist += float((1 - st[side]) / 2);
    dist /= 1.*rd[side]; // unproject distance from axial to along ray
    return dist;
