float Scan2stb(vec2 m0, vec2 m1)
{
	vec2 d = m1 - m0, s = sign(d), V = m0;
	int niter = int(floor(max(abs(d.x), abs(d.y)))) + 1;
	for (int i = niter; i-- > 0; ) {
        vec2 adj = mix(floor(V+s), ceil(V+s), .5-.5*s) - V
        , xV = adj.x * vec2(1., d.y/d.x)
        , yV = adj.y * vec2(d.x/d.y, 1.)
        , dV = dot(xV,xV) < dot(yV,yV) ? xV : yV;
        V += dV;
        ivec2 q = ivec2(floor(V));
        if (Condition(q))
            if (!Traverse(q))
            	break;
    }
	float dist;
		dist = length(V-m0); // seems wasteful to compute when not needed
    return dist;
}
#endif

