float Scan2jt(vec2 m0, vec2 m1)
{
    vec2 rayPos = m0;
    vec2 rayDir = m1;
    vec2 s = sign(rayDir);
    ivec2 rayStep = ivec2(s);
    rayDir = normalize(rayDir);
    ivec2 mapPos = ivec2(floor(rayPos));
    vec2 sideDist;
    vec2 mask;
    // core of https://shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 (somewhat reduced)
    int MAX_RAY_STEPS = 32;
    vec2 deltaDist = 1.0 / abs(rayDir);
    sideDist = (s * (vec2(mapPos) - rayPos + .5) + .5) * deltaDist; 

    for (int i = 0; i < MAX_RAY_STEPS; ++i) {
        //if (getVoxel(mapPos)) continue;
