            if (!Traverse(mapPos))
            	break;

        // Thanks kzy for the suggestion! for 3d
        //mask = lessThanEqual(sideDist.xyz, min(sideDist.yzx, sideDist.zxy));
        mask = vec2(lessThanEqual(sideDist.xy, sideDist.yx)); // for 2d
        sideDist += mask * deltaDist;
        mapPos += ivec2(mask) * rayStep;
    }
    // jt's texcoord addon recovering distance & subvoxel intersection-position of ray 
    // as described in https://lodev.org/cgtutor/raycasting.html (see "perpWallDist" there)
    //float d = (mask.x ? sideDist.x - deltaDist.x : mask.y ? sideDist.y - deltaDist.y : mask.z ? sideDist.z - deltaDist.z : 0.0) / length(rayDir);
    vec2 l = mask * (sideDist - deltaDist);
    return l.x + l.y; // one will be zero
}
