const int grid = 5;
const float scale = 1.1 * float(grid-1);

vec2 StoQ(vec2 s, vec2 r)
{
    return (s+s-r) * scale / r.y + .5;
}

void mainImage(out vec4 c, vec2 p)
{
    float a = .2 * iTime;
    vec2 R = iResolution.xy
		, q = StoQ(p, R);
	vec2 m0 = StoQ(iMouse.zw, R)
       , m1 = StoQ(iMouse.xy, R);
    if (iMouse.z <= 0.) {
        m0 = vec2(.5); m1 = m0 + float(grid-1)*vec2(vec2(sin(a), cos(a)));
    }
	qi = ivec2(floor(q)); // gridcell
	celltouchesline = false;
    float l =
    Scan2DDAnext //Scan2jt //Scan2DDA2 //Scan2branchless //Scan2stb //Scan2DDA3 //Scan2F //Scan2B //Scan2IF //
        (m0, m1);
    vec2 hp = m0 + normalize(m1 - m0) * l;
    bool h = celltouchesline;
    float g = h ? .1 : .85;
    if (((qi.x^qi.y)&1)!=0) g += .05; // checks
    g += max(0., 1.-.25*R.y/scale*seg(q, m0, m1));
    g += max(0., .5 - 12.*(distance(hp, q) - .1));
	c = vec4(vec3(sqrt(g)), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(.3, .3, 1.);

    vec2 center = vec2(0., 0.);
    float r = .1;
    float exc = .2;
    for (float i = 1.; i < 10.; ++i) {
        float theta = i * iTime+i;
        vec2 p = vec2( sin(theta) * exc, cos(theta) * exc) + center;
    	col = mix(col, i*vec3(.05, .08, .1), smoothstep(.051, .03, length(uv - p) - r));
        r -= r * 0.5;
        exc -= r;
        center = p;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
