void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a=.6+.1*mod(.03*iTime,1.);
    float b=sqrt(1.-a*a);
    float c=a/b;
    mat2 m=mat2(a,-b,b,a);

    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = vec2(-.6,-.2)+.7*uv;
	uv.x *= iResolution.x/iResolution.y;
    for(int i=8;i>0;i--){
     while(uv.y>0.||uv.x>c*uv.y)uv*=m;
     uv*=.85;
     uv+=vec2(.07,-.035);
    }
	float d = .4+.2*atan(uv.x,uv.y);
	fragColor = vec4(vec3(d,d*d,1.-d),1.);
    if(uv.x-uv.y<.05)fragColor=vec4(0.,.5,0.,1.);
}

const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;

const float AO_DIST = 0.085;
const float AO_KVAL = 2.0;

const float SHADOW_HARDNESS = 6.0;

// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 2.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 4.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.25;


struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;	// Assign every object in scene a unique ID int
};



void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec3 rotatePtY(vec3 pt, float a)
{    
    pR(pt.yz, 0.);
    pR(pt.xz, a);
    pR(pt.xy, 0.);

    return pt;
}

vec3 rotatePtX(vec3 pt, float a)
{    
    pR(pt.yz, a);
    pR(pt.xz, 0.);
    pR(pt.xy, 0.);

    return pt;
}

// 2D rotation about the y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}


void rayCast(vec3 eye, vec3 ref, vec2 ndc, out vec3 dir) {    
    // Compute camera axis
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V; 

    dir = normalize(p - eye);
}


// SHAPE SDFs
float sphere(vec3 p, float r, vec3 c) {
	return distance(p, c) - r;
}

float cube(vec3 p, vec3 c) {
    return length(max(abs(p) - c, 0.0));
}

float squarePlane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}


#define SPHERE_SDF	 	sphere(pos + vec3(-5.0, 0.0, 0.0), 4.0, vec3(0.0, 0.0, 0.0))
#define SPHERE_ID		1
#define CUBE_SDF		cube(rotatePtY(pos + vec3(0.0, 1.5, 6.0), 0.5), vec3(1.5, 3., 1.5))
#define CUBE_ID			2
#define TORUS_SDF		torus(rotatePtX(rotatePtY(pos + vec3(0.0, 0.2, -6.0), -0.4), -0.7), vec2(2.0, 0.75))
#define TORUS_ID		3
#define FLOOR_SDF		squarePlane(pos + vec3(0.0, 2.0, 0.0), vec4(0.0, 1.0, 0.0, 1.0))
#define FLOOR_ID		4
#define LIGHT_SDF		sphere(pos + vec3(-0.5, 0.0, 0.0), 2.5, lightPos)
#define LIGHT_ID		5

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = SPHERE_SDF;
    obj = SPHERE_ID;

    float t2;
    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
      	obj = CUBE_ID;
    }

    if ((t2 = TORUS_SDF) < t) {
    	t = t2;
      	obj = TORUS_ID;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
      	obj = FLOOR_ID;
    }

    if ((t2 = LIGHT_SDF) < t) {
    	t = t2;
    	obj = LIGHT_ID;
    }
}


float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t = SPHERE_SDF;

    float t2;
    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS_SDF) < t) {
    	t = t2;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
    }

    if ((t2 = LIGHT_SDF) < t) {
    	t = t2;
    }

    return t;
}


float shadowMap3D(vec3 pos) {
    float t = SPHERE_SDF;

    float t2;
    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS_SDF) < t) {
    	t = t2;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
    }

    return t;
}


vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;

    for (int i = 0; i < RAY_STEPS; ++i) {
    	vec3 pos = origin + t * dir;
        float m; // Minimum distance from this point to an object in the scene
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
        	return;
        }
        t += m;
    }
	// We hit nothing
    t = -1.0;
    hitObj = -1;
}


float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - shadowMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}


float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}


float subsurfaceColor(vec3 lightDir, vec3 n, vec3 view, float thin) {
	vec3 scatterDir = lightDir + n * DISTORTION;
	float lightReachingEye = pow(clamp(dot(view, -scatterDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
	float attenuation = max(0.0, dot(n, lightDir) + dot(view, -lightDir));
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thin;
    return totalLight;
}

void contrast( inout vec3 color, in float c) {
    float t = 0.5 - c * 0.5; 
    color.rgb = color.rgb * c + t;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thin) {
    float lambert = max(0.0, dot(n, lightVec));

    switch(hitObj) {
        // Sphere
        case SPHERE_ID:
        vec3 color1 = vec3(1.0, 0.5, 1.2) * vec3(1.0, 0.7, 0.4) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        color1 = color1 + vec3(0.6, 1.0, 0.75) * subsurfaceColor(lightVec, n, view, thin) * vec3(1.0, 0.88, 0.7);
        //contrast(color1, 1.5);
        return color1;
        break;
        // Cube
        case CUBE_ID:
        vec3 color2 = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.7, 0.5) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return color2 + vec3(1.0, 0.5, 0.6) * subsurfaceColor(lightVec, n, view, thin) * vec3(1.0, 0.88, 0.7);
        break;
        // Torus
        case TORUS_ID:
        vec3 color3 = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.65, 0.45) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return color3 + vec3(0.4, 0.4, 0.8) * subsurfaceColor(lightVec, n, view, thin) * vec3(1.0, 0.88, 0.7);
        break;
        // Box
        case FLOOR_ID:
        vec4 color4 = texture(iChannel0, vec2(p.xz / 10.0));
        return vec3(1.0, 1.0, 1.0) * vec3(color4) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Light
        case LIGHT_ID:
        return vec3(1.0, 0.88, 0.7);
        break;
        // Background
        case -1:
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
	float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, AO_KVAL);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(t, surfaceColor, isect, hitObj);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec3 lightPos = rotateY(vec3(12.0, 4.0, -6.0), iTime * 0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = rotateY(vec3(0.0, 0.0, 15.0), sin(30.0) * 3.14159 * 0.5);
    vec3 ref = vec3(0, 0, 0);
    vec3 rayDir;

    rayCast(eye, ref, uv, rayDir);

    Intersection isect = sdf3D(rayDir, eye, lightPos);

    // Output to screen
    fragColor = vec4(isect.color, 1.);
}

float circle(vec2 uv, float radius, float blur,  vec2 pos)
{

    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

float distLine(vec2 p, vec2 a, vec2 b){
	vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/dot(ba, ba),0., 1.);
    return length(pa-ba*t);
}

float line(vec2 p, vec2 a, vec2 b){
	float d = distLine(p,a,b);
    float m = 0.9*smoothstep(.01,.001, d/30.);

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 pos1 = vec2(.7 * ratio, .7);
    vec2 pos2 = vec2(.4 * ratio, .7);
    vec2 pos3 = vec2(.2 * ratio, .7);

    vec2 b1Pos = vec2(.7 *ratio, .4);
    vec2 b2Pos = vec2(.4 *ratio, .4);
    vec2 b3Pos = vec2(.2 *ratio, .4);

    vec2 sunPos = vec2(.1, 3.4);

    float dist = distance(pos1, uv) * 2.0;
    float dist2 = distance(pos2, uv) * 2.0;
    float dist3 = distance(pos3, uv) * 2.0;

    vec2 uv1 = uv;
    uv1 -= pos1;

    vec2 uv2 = uv;
    uv2 -= pos2;

    vec2 uv3 = uv;
    uv3 -= pos3;

    float angle = atan(uv1.y, uv1.x) + iTime;
    float angle2 = atan(uv2.y, uv2.x) + iTime;
    float angle3 = atan(uv3.y, uv3.x) + iTime;

    float radius = sin((angle+iTime*3.)*3.);
    radius *= .6;

    float radius2 = sin((angle2+iTime*3.)*3.);
    radius2 *= .45;

    float radius3 = sin((angle3+iTime*3.)*3.);
    radius3 *= .3;

    float value = 1.-smoothstep(radius, radius +.0, dist);
    float value2 = 1.-smoothstep(radius2, radius2 +.0, dist2);
    float value3 = 1.-smoothstep(radius3, radius3 +.0, dist3);

    float cap1 = circle(uv, .04, 0., pos1);
    float cap2 = circle(uv, .03, 0., pos2);
    float cap3 = circle(uv, .02, 0., pos3);

    float back1 = circle(uv, .07, 0., pos1);
    float back2 = circle(uv, .05, 0., pos2);
    float back3 = circle(uv, .03, 0., pos3);

    float bar1 = rect(uv, b1Pos, vec2(.08, .6));
    float bar2 = rect(uv, b2Pos, vec2(.06, .6));
    float bar3 = rect(uv, b3Pos, vec2(.04, .6));

    float m = line(uv,vec2(-.1, .2), vec2(2., -.15));

    float sun = circle(uv, .5, 3., sunPos);

    vec3 color = vec3(.7294, .8980, .9686);
    color = mix(vec3(1., 1., 0.), color, sun);

    color = mix(color, vec3(.9), bar1);
    color = mix(vec3(.9), color, back1);
    color = mix(color, vec3(.7), value);
    color = mix(vec3(.9), color, cap1);

    color = mix(color, vec3(.9), bar2);
    color = mix(vec3(.9), color, back2);
    color = mix(color, vec3(.7), value2);
    color = mix(vec3(.9), color, cap2);

    color = mix(color, vec3(.9), bar3);
    color = mix(vec3(.9), color, back3);
    color = mix(color, vec3(.7), value3);
    color = mix(vec3(.9), color, cap3);

    color = mix(color, vec3(0., 1., 0.), m);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

