#define SCALE 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = SCALE * (2. * fragCoord - iResolution.xy) / iResolution.y 
              * mat2(1, -1, 1, 1) / sqrt(2.),
     	 gv = fract(uv) - .5,
         id = floor(uv);

    float t = iTime;
    float m = 0.;

    for(int k = 0; k < 9; ++k) {
        vec2 off = vec2(k % 3, k / 3) - 1.;
        float l = length(gv - off) - 1.5 * (sin(t - length(id + off) * .3) * .5 + .5);
        m += (1. - 2. * m) * smoothstep(.7, -.7, l / min(.05, fwidth(l)));
    }

    fragColor = vec4(m);
}

/*
 Formula by IQ, with added translation (qd) and elongation/compression (qc)
*/
vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d, in float qc, in float qd){
    return a + b * sin(2.0 * 3.14159265359 * (c * t * qc + d  + qd));
}

/*
   wrong z^2+c
*/
float julia(in vec2 ri, in vec2 c, in float time){
    float zr = (ri.x-0.5)*2.25;
    float zi = (ri.y-0.5)*2.5;
    float r = c.x;
    float i = c.y;
    float max = time;
    for(float k=0.0;k<max;k++){
        float t = 1.35*zr*zr - zi*zi + r;
        zi = 2.35*zr*zi + i;
        zr = t;
        if(zr*zr+zi*zi > 1000.0){
            return k/max;
        }
    }
    return 1.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime;
//    time = 0.0;

    // Coloring
    float t2 = pow(uv.x, 0.5)*0.5 *-0.5*pow(uv.y,0.5);
    vec3 col = pal(t2, vec3(0.5),vec3(0.25),vec3(0.5),vec3(0.8,0.5,0.2),3.0,time);
    float u = 0.35+mod(time*0.25,0.75);
    u = u*u*200.0;
    float r= 0.4 + cos(time*0.37)*0.3;
    vec2 c = vec2(sin(time)*r, cos(time)*r);
    col = col*0.7 + col*vec3(julia(uv,c,u));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265;
const float PI_2 = PI / 2.;

float fold(float x) {
    return abs(mod(x, 2.0) - 1.0);
}

float foldPlus(float x) {
    return log(1.0 + fold(x));
}

float smoothThres(float x, float strength) {
    return smoothstep(0.5 - strength, 0.5 + strength, x);
}

float smoothFold(float x, float strength) {
    return smoothThres(fold(x), strength);
}

vec3 colMap(float x, vec3 a, vec3 b) {
    return a * (1.0 - x) + b * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime * 64. / 60.);

    // Normalized pixel coordinates
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= iResolution.xy / scale / 2.;
    uv *= 2.;
    float dist = log(uv.x*uv.x+uv.y*uv.y); // not real distance, but useful for log spirals
    float angle = atan(uv.y, uv.x) / PI;

    //angle -= t * 1. / 5.;

    float twistiness = 0.5 * (fold(t * 2.) - 0.5);
    twistiness += cos(t * 2. * PI) * 0.2;

    float value = foldPlus(angle * 5. + (dist - 1.) * twistiness);

    value = smoothFold(value * 1.5 + dist * 1. + t * 02.0, 0.05);

    vec3 colA0 = vec3(1.0, 0.3, 0.8);
    vec3 colA1 = vec3(0.2, 0.4, 0.7);
    vec3 colB0 = vec3(0.6, 0.2, 0.4);
    vec3 colB1 = vec3(0.4, 0.8, 1.0);
    vec3 colA = colMap(value, colA0, colA1);
    vec3 colB = colMap(value, colB0, colB1);

    vec3 col = colMap(smoothFold((angle * 5. + 0.5) + (dist - 1.) * twistiness, 0.02),
                     colA, colB);

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float FOVY = 0.25 * 3.141569;
const int RAY_STEPS = 256;

#define SPHERE_SDF sdfSphere(pos, vec3(0.5, .8, 5), .2)
#define SPHERE_ID 1
#define SPHERE2_SDF sdfSphere(pos, vec3(-0.5, .8, 5), .2)
#define SPHERE2_ID 2
#define SPHERE3_SDF sdfSphere(pos, vec3(0, .5, 5), .3)
#define SPHERE3_ID 3
#define SPHERE4_SDF sdfSphere(pos, vec3(0, -.3, 5), .1)
#define SPHERE4_ID 4
#define SPHERE5_SDF sdfSphere(pos, vec3(0, .5, 7), 2.)
#define SPHERE5_ID 5

