mat4 inverseTransform(vec3 translate, vec3 rotate, vec3 scale) {
    mat4 s;
    // matrices indexed to columns!
    s[0] = vec4(scale.x, 0., 0., 0.);
    s[1] = vec4(0., scale.y, 0., 0.);
    s[2] = vec4(0., 0., scale.z, 0.);
	s[3] = vec4(0., 0., 0., 1.);                                     

    rotate.x = radians(rotate.x);
    rotate.y = radians(rotate.y);
    rotate.z = radians(rotate.z);

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 r = r_x * r_y * r_z;

    mat4 t;
    t[0] = vec4(1., 0., 0., 0.);
    t[1] = vec4(0., 1., 0., 0.);
    t[2] = vec4(0., 0., 1., 0.);
    t[3] = vec4(translate.x, translate.y, translate.z, 1.); 

    return inverse(t * r * s);      
}

float sdfPlane(vec3 p, mat4 inverseT) {
    p = vec3(inverseT * vec4(p, 1.));
    float dot = dot(p, vec3(0., 0., 1.));
    if (abs(dot) < 0.0001) {
        // n must be normalized
        if (p.x >= -.5 && p.x <= 0.5 && p.y >= -0.5 && p.y <= 0.5) {
            return 0.;  
        }
        return 324234.;
    }
    float dist = 0.f;
    if (abs(p.x) > 0.5) {
        dist += ((p.x - 0.5) * (p.x - 0.5));
    }
    if (abs(p.y) > 0.5) {
        dist += ((p.y - 0.5) * (p.y - 0.5));
    }
    dist += abs(p.z) * abs(p.z);
    return sqrt(dist);
} 

// Box with side lengths b
float sdfBox(vec3 p, vec3 scale, mat4 inverseT)
{
    p = vec3(inverseT * vec4(p, 1.));
    vec3 q = vec3(abs(p.x), abs(p.y), abs(p.z)) - vec3(.5);
    float d = q.x;
    d = max(d,q.y);
    d = max(d,q.z);
    return d * min(min(scale.x, scale.y), scale.z);
}


float sdfSphere(vec3 p, vec3 c, float r) {
    return distance(p, c) - r;
}

void sceneMap3D(vec3 pos, out float t, out int objectHit) {
	t = SPHERE_SDF;
    objectHit = SPHERE_ID;
    float t2;

    if ((t2 = SPHERE2_SDF) < t) {
        t = t2;
        objectHit = SPHERE2_ID;
    }
    if ((t2 = SPHERE3_SDF) < t) {
        t = t2;
        objectHit = SPHERE3_ID;
    }
    if ((t2 = SPHERE4_SDF) < t) {
        t = t2;
        objectHit = SPHERE4_ID;
    }
    if ((t2 = SPHERE5_SDF) < t) {
        t = t2;
        objectHit = SPHERE5_ID;
   }
}

// called by computeNormal
float sceneMap3D(vec3 pos) {
    float t;
    int obj;
    sceneMap3D(pos, t, obj);
    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int objectHit) {
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; i++) {
        vec3 pos = origin + t * dir;
        float min;
        sceneMap3D(pos, min, objectHit);
        if (min < 0.01) {
            return;
        }
        t += min;
    }
    t = -1.;
    objectHit = -1;
}

vec3 computeNormal(vec3 p, vec3 lightPos) {
    vec3 epsilon = vec3(0., .001, 0.);
    return normalize(vec3(sceneMap3D(p + epsilon.yxx) - sceneMap3D(p - epsilon.yxx),
                          sceneMap3D(p + epsilon.xyx) - sceneMap3D(p - epsilon.xyx),
                     	  sceneMap3D(p + epsilon.xxy) - sceneMap3D(p - epsilon.xxy)));
}


// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 3.;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.;


float subsurfaceColor(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * SCALE;
    float attenuation = 1.0;
    //attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

#define DIST 0.085

float fiveTapAO(vec3 p, vec3 n, float k) {
    float sum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        sum += coeff * (i * DIST - sceneMap3D(p + n * i * DIST));
    }
    return 1.0 - k * sum;
}

vec3 computeMaterial(int objectHit, vec3 p, vec3 n, vec3 lightPos, vec3 view) {
    vec3 lightDir = normalize(lightPos - p);
    float lambert = max(0., dot(n, lightDir));
    vec3 color;
    float subsurface = subsurfaceColor(lightDir, n, view, fiveTapAO(p, -n, 2.0));

    switch (objectHit) {
        case SPHERE_ID:
        color = vec3(153., 204., 255.) / vec3(255.);
        break;
        case SPHERE2_ID:
        color = vec3(153., 204., 255.) / vec3(255.);
        break;
        case SPHERE3_ID:
        color =  vec3(255, 255, 0) / vec3(255.);
        break;
        case SPHERE4_ID:
        color =  vec3(255, 102, 153) / vec3(255.);
        break;
        case SPHERE5_ID:
        color = vec3(255, 204, 204) / vec3(255.);
        break;
    }
    return color * lambert + color * subsurface;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

Intersection sdf3D(vec3 eye, vec3 dir) {
    float t;
    int objectHit;
    march(eye, dir, t, objectHit);
    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect, vec3(0, 0, 0));

    vec3 lightPos =  rotateY(vec3(0, 2., 20), sin(iTime * 0.5));

    vec3 color = computeMaterial(objectHit, isect, nor, lightPos, normalize(eye - isect));

    if (objectHit == -1) {
        color = vec3(0, 0, 0);
    }

    return Intersection(t, color, isect, objectHit);
}

vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0., 1., 0.)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / float(iResolution.y);

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - vec2(1, 1);

    vec3 eye = vec3(0, 0, 0);
    vec3 ref = vec3(0, 1, 11);
    vec3 rayDir = rayCast(eye, ref, uv); 

    Intersection isect = sdf3D(eye, rayDir);

    fragColor = vec4(isect.color, 1.0);
}

//This is based on https://www.shadertoy.com/view/3d2BWD

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (100.*fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    //uv *= 30.*abs(1.1 + sin(iTime*0.1));
    float len;
    uv += sin(uv/20.0)/10.0;
    for (int i = 0; i < 10; i++) {
    	len = length(uv);
        uv.x += sin(iTime+sin(uv.y))-len/uv.y;
        uv.y += cos(iTime+cos(uv.x))-len/uv.x;
    }
    float col = cos(len);
    fragColor = vec4(col,col,col,1.0);
}

#define time iTime

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(0.890,-0.900)))*757.153);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) + (c - a)* u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float fbm ( in vec2 _st) {
    float v = sin(time*0.3)*0.1;
    float a = 0.1;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(1.0), -sin(0.5), acos(0.5));
    for (int i = 0; i < 3; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 2.6;
    }
    return v;
}

// 2D rotate
mat2 rotate(in float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Wide-angle lens is baced on Menger Sponge Variation
    // https://www.shadertoy.com/view/ldyGWm
    vec3 st = (vec3(2.0 * fragCoord.xy - iResolution.xy, iResolution.y));
    st = normalize(vec3(st.xy, sqrt(max(st.z * st.z - dot(st.xy, st.xy) *4.,0.)))) * 0.3;

    st.xy *= rotate(time*0.1);
    st.xz *= rotate(time*0.2);

    vec2 coord = st.xy;
    coord.x += 0.1*time;
    coord.y += 0.2*time;    
    float len; 
    for (int i = 0; i < 3; i++) {
        len = length(vec2(coord.x, coord.y));  
        coord.x +=  sin(coord.y + time * 0.620)*0.1;
        coord.y +=  cos(coord.x + time * 0.164)*0.1;
    }
    len -= 3.;

    vec3 color = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st.xy + 1.0);
    q.y = fbm( st.xy + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st.xy + 1.0*q + vec2(0.570,0.520)+ 0.02*time );
    r.y = fbm( st.xy + 1.0*q + vec2(0.340,-0.570)+ 0.05*time);

    for (float i = 0.0; i < 3.0; i++) {
        r += 1.0 / abs(mod(st.y + st.x,((1.0 + sin(time*0.3))*0.48+0.04) * .5 * i) * 1000.);
        r += 1.0 / abs(mod(st.y - st.x,((1.0 + sin(time*0.3))*0.48+0.04) * .5 * i) * 1000.);
    }

    color = mix(color, cos(len + vec3(0.2, 0.0, -0.5)), 1.0);
    color = mix(vec3(0.730,0.386,0.372), vec3(0.397,0.576,0.667), color);

    float f = mix(fbm(st.xy+r),fbm(st.xy+r*0.1),step(0.,abs(st.y)-abs(st.x)));   
    color = 2.0*(f*f*f+.6*f*f+.5*f)*color;

    fragColor = vec4(color,1.);
}

precision lowp float;


#define PI 3.14159265358979
#define R 0.07
#define D 0.05


const vec3 base_colors[] = vec3[](
    vec3(1.0, 5.0, 0.0),
    vec3(2.0, 2.0, 9.0),
    vec3(5.0, 4.0, 2.0),
    vec3(4.0, 4.0, 5.0)
);


// from https://www.shadertoy.com/view/lsf3WH
float hash(vec2 p) {
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return fract( p.x*p.y*(p.x+p.y) );
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0, 0.0) ), 
                     hash( i + vec2(1.0, 0.0) ), u.x),
                mix( hash( i + vec2(0.0, 1.0) ), 
                     hash( i + vec2(1.0, 1.0) ), u.x), u.y);
}


// from https://www.shadertoy.com/view/MsKGWz
float hash12(vec2 p) {
    float h = dot(p,vec2(127.1, 311.7));	
    return fract(sin(h)*43758.5453123);
}


// from https://www.shadertoy.com/view/MsKGWz
float hash5(float x) {
    return fract(sin(x)*43758.5453+0.4);
}


float disc(vec2 c, vec2 pos) {
    pos -= c;
    float d = length(pos / R);
    if (d > 1.0+D) {
        return 0.0;
    }
    else {
        float a = atan(pos.y, pos.x);
        float dm = d + 0.03*(cos(a*5.0+2.5)+1.0);
        return (0.02+0.01*(
            ((dm < 1.0+D) ? smoothstep(0.0, 1.0, dm) : 0.0) -
            smoothstep(1.0, 1.0+D, dm)))*
            (0.75+0.25*noise(75.0*pos*mat2( 1.6,  1.2, -1.2,  1.6 )));
    }
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y/iResolution.x;
    uv.y *= aspect;
	vec3 col = mix(vec3(0.02, 0.05, 0.00),
                   vec3(0.10, 0.20, 0.05),
                   dot(vec2(0.6, 0.6/aspect), uv)+
                   //0.4*noise(20.0*uv)-0.2+
                   0.2*hash12(uv)-0.1);

    for (float i=100.0; i>0.0; i--) {
        int m = int(i) & 3;
        float x = 1.1*hash5(2.0*i+3.0)-0.05;
        float y = 1.1*hash5(5.0*i)-0.05;
        float dx = 0.05*sin(0.3*iTime+0.01*i) + 0.05*sin(0.4*iTime+0.01*i);
        float dy = 0.03*sin(0.2*iTime+0.100*i) + 0.03*sin(0.3*iTime+0.100*i);
        float b = pow((1.0+cos(0.75*iTime+0.1*i)), 0.5) * (0.7+0.3*cos(0.51*iTime+0.03*i));
        col += (base_colors[m] + 2.0*vec3(hash5(x), hash5(y), hash5(x+y))) *
            disc(vec2(x+dx, (y+dy)*aspect), uv) * b;
    }

    // tone mapping and gamma
    fragColor = vec4(pow(col/(col+1.0), vec3(1.0)/2.2), 1.0);
}

// "Big Rubik Pseudosolver" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define SIZE4  1  // =1 for 4^3 cube; =0 for 3^3

#define AA     0  // optional antialiasing

mat3 VToRMat (vec3 v, float a);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float Hashff (float p);

#define VAR_ZERO min (iFrame, 0)

mat3 vuMat;
vec2 qBlk;
float tCur, dstFar, bSize, rSeed;
const float pi = 3.1415927;

vec4 BlkHit (vec3 ro, vec3 rd, vec3 p, mat3 m)
{
  vec3 rdm, v, tm, tp, u, fcBlk;
  float dMin, dn, df;
  dMin = dstFar;
  rdm = m * rd;
  v = (m * ro - p) / rdm;
  tp = bSize / abs (rdm) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < min (df, dMin)) {
    dMin = dn;
    fcBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rdm;
  }
  if (dMin < dstFar) {
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk));
  }
  return vec4 (dMin, fcBlk);
}

vec4 SphHit (vec3 ro, vec3 rd, float rad)
{
  vec3 vn;
  float b, d, w;
  b = dot (rd, ro);
  w = b * b + rad * rad - dot (ro, ro);
  d = dstFar;
  if (w > 0.) {
    d = - b - sqrt (w);
    vn = (ro + d * rd) / rad;
  }
  return vec4 (d, vn);
}

#if SIZE4
const float nbE = 4.;
const float nTwist = 28.;
#else
const float nbE = 3.;
const float nTwist = 22.;
#endif

mat3 RotSeq (vec3 bId, float nt, float aRot)
{                     // Derived rotation matrix sequence from Kali's "Rubik"
  mat3 rMat, m;
  vec3 ax[3], vSlice;
  vec2 e;
  float s, sa;
  e = vec2 (1., 0.);
  ax[0] = e.xyy;
  ax[1] = e.yxy;
  ax[2] = e.yyx;
  rMat = mat3 (e.xyy, e.yxy, e.yyx);
  for (float n = float (VAR_ZERO); n < nTwist; n ++) {
    if (n > nt) break;
    s = mod (n + floor (6. * Hashff (99. + rSeed)), 6.);
    sa = sign (s - 2.5);
    s = mod (s, 3.);
    vSlice = (s == 0.) ? e.xyy : ((s == 1.) ? e.yxy : e.yyx);
    if (length ((bId + 0.5 * (nbE + 1.)) * vSlice) - 1. == floor (nbE * Hashff (n + rSeed))) {
      m = VToRMat (sa * ((s == 0.) ? ax[0] : ((s == 1.) ? ax[1] : ax[2])),
         0.5 * pi * ((n < nt) ? 1. : aRot));
      for (int k = 0; k < 3; k ++) ax[k] = m * ax[k];
      rMat = m * rMat;
      if (n < nt) bId = VToRMat (sa * vSlice, -0.5 * pi) * bId;
    }
  }
  return rMat;
}

float LabSym (vec2 p)
{
  vec2 q;
  float d, r;
  r = length (p);
  d = max (min (0.06 - abs (0.1 - abs (r - 0.8)), p.y), min (0.06 - abs (p.y), 1.1 - abs (p.x)));
  q = Rot2D (p, 2. * pi * floor (16. * ((r > 0.) ? atan (p.y, - p.x) / (2. * pi) : 0.) + 0.5) / 16.);
  d = max (d, min (min (0.06 - abs (q.y), 0.2 - abs (q.x + 1.1)), p.y + 0.1));
  q.x += 1.5;
  d = max (d, min (0.1 - length (q), p.y + 0.1));
  return d;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  mat3 rMat, rMatH;
  vec4 col4, bs4;
  vec3 col, vn, ltDir, fcBlk, bId, bidH, cc[6], de3, ld, bLab;
  vec2 qBlkH, de;
  float dstBlk, dstSphr, d, nt, tCyc, shSpd, tWait, aRot, dSum, sSum, bMin;
  cc[0] = vec3 (1., 0.2, 0.2);
  cc[1] = vec3 (1., 0.4, 0.2);
  cc[2] = vec3 (0.2, 0.2, 1.);
  cc[3] = vec3 (0.2, 1., 0.2);
  cc[4] = vec3 (1., 1., 0.2);
  cc[5] = vec3 (1., 1., 1.);
  bSize = 0.48;
  shSpd = 10.;
  tWait = 3.;
  tCyc = (nTwist + tWait) * (1. + 1. / shSpd) + tWait;
  nt = nTwist - max (0., mod (tCur, tCyc) - tWait);
  if (nt + tWait < 0.) nt = - shSpd * (nt + tWait);
  aRot = smoothstep (0.1, 0.9, max (0., fract (nt) * sign (nt)));
  nt = max (0., floor (nt));
  rSeed = 17.77 * floor ((tCur + (nTwist + tWait) / shSpd + tWait) / tCyc + 1.);
  bMin = 0.5 * (nbE - 1.) - 0.5;
  bLab = vec3 (0., 0., -1.);
  if (nbE == 4.) bLab -= 0.5;
  dstBlk = dstFar;
  for (float j = float (VAR_ZERO); j < nbE * nbE * nbE; j ++) {
    bId = vec3 (mod (j, nbE), mod (floor (j / nbE), nbE), floor (j / (nbE * nbE))) - 0.5 * (nbE - 1.);
    if (Maxv3 (abs (bId)) > bMin) {
      rMat = RotSeq (bId, nt, aRot);
      bs4 = BlkHit (ro, rd, bId, rMat);
      d = bs4.x;
      if (d < dstBlk) {
        dstBlk = d;
        fcBlk = bs4.yzw;
        rMatH = rMat;
        qBlkH = qBlk;
        bidH = bId;
      }
    }
  }
  bs4 = SphHit (ro, rd, (nbE - 0.2) * bSize);
  dstSphr = bs4.x;
  if (min (dstBlk, dstSphr) < dstFar) {
    if (dstBlk < dstSphr) {
      ro += dstBlk * rd;
      de = smoothstep (-0.08, 0., abs (qBlkH.xy) - bSize);
      de3 = vec3 (0.25 * pi * de * sign (qBlkH.xy), 0.);
      vn = normalize (fcBlk + ((fcBlk.x != 0.) ? de3.zxy : ((fcBlk.y != 0.) ? de3.xzy : de3))) * rMatH;
      if (length (max (abs (qBlkH) - bSize + 0.12, 0.)) < 0.07) {
        if (abs (bidH.x) > bMin && sign (bidH.x) == fcBlk.x ||
            abs (bidH.y) > bMin && sign (bidH.y) == fcBlk.y ||
            abs (bidH.z) > bMin && sign (bidH.z) == fcBlk.z) {
          if      (abs (fcBlk.x) == 1.) col4.rgb = (fcBlk.x > 0.) ? cc[0] : cc[1];
          else if (abs (fcBlk.y) == 1.) col4.rgb = (fcBlk.y > 0.) ? cc[2] : cc[3];
          else if (abs (fcBlk.z) == 1.) col4.rgb = (fcBlk.z > 0.) ? cc[4] : cc[5];
          col4 = vec4 (mix (col4.rgb, vec3 (0.2, 0.2, 0.2), max (de.x, de.y)), 0.2);
        } else {
          col4 = vec4 (0.1, 0.1, 0.1, 0.);
        }
        if (bidH == bLab && sign (bidH.z) == fcBlk.z)
           col4 *= 1. - 0.6 * smoothstep (-0.05, 0., LabSym (-16. * (vec2 (0.5, 0.5) * bSize + qBlkH.yx)));
      } else {
        col4 = vec4 (0.2, 0.2, 0.2, 0.2);
      }
    } else if (dstSphr < dstFar) {
      ro += dstSphr * rd;
      vn = bs4.yzw;
      col4 = vec4 (0.1, 0.1, 0.1, 0.);
    }
    dSum = 0.;
    sSum = 0.;
    ltDir = normalize (vec3 (1., 1., -0.6));
    for (int j = 0; j < 4; j ++) {
      ltDir.xy = Rot2D (ltDir.xy, 0.5 * pi);
      ld = vuMat * ltDir;
      dSum += pow (max (dot (vn, ld), 0.), 1.5);
      sSum += pow (max (dot (normalize (ld - rd), vn), 0.), 32.);
    }
    col = col4.rgb * (0.2 + 0.5 * dSum) + col4.a * sSum;
    col = pow (clamp (col, 0., 1.), vec3 (0.8));
  } else {
    col = vec3 (0.6);
  }
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.15 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.04 * pi * tCur;
    el -= 0.07 * pi * (1. + sin (0.05 * pi * tCur));
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -10. * nbE / 3.);
  zmFac = 3.7;
  dstFar = 100.;
  if (length (uv) < 0.99) {
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
      col += (1. / naa) * ShowScene (ro, rd);
    }
  } else col = mix (vec3 (0.6), vec3 (0.82), smoothstep (0., 0.1, length (uv) - 0.99));
  fragColor = vec4 (col, 1.);
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec3 w, b1, b2;
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  w = (1. - cs.x) * v * v + cs.x;
  b1 = (1. - cs.x) * v.zyx * v.xzy;
  b2 = - cs.y * v;
  m[0][0] = w.x;  m[1][1] = w.y;  m[2][2] = w.z;
  m[1][2] = b1.x + b2.x;  m[2][1] = b1.x - b2.x;
  m[2][0] = b1.y + b2.y;  m[0][2] = b1.y - b2.y;
  m[0][1] = b1.z + b2.z;  m[1][0] = b1.z - b2.z;
  return m;
}

