float fbm ( in vec2 _st) {
    float v = sin(iTime*0.5)*0.2;
    float a = 0.3;
    vec2 shift = vec2(100.);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(1.0), -sin(0.5), acos(0.5));
    for (int i = 0; i <3; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2. + shift;
        a *= 1.5;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y) * 0.5;

    vec2 coord = st * 0.2;
    float len;
    for (int i = 0; i < 3; i++) {
        len = length(coord);
        coord.x +=  sin(coord.y + iTime * 0.1)*2.1;
        coord.y +=  cos(coord.x + iTime * 0.1 + cos(len * 1.0))*1.;
    }
    len -= 3.;

    vec3 color = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st );
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(0.570,0.520)+ 0.1*iTime );
    r.y = fbm( st + 1.0*q + vec2(0.340,-0.570)+ 0.05*iTime);
    float f = fbm(st+r);

    color = mix(color, cos(len + vec3(0.5, 0.0, -0.1)), 1.0);
    color = mix(vec3(0.478,0.738,0.760),vec3(0.563,0.580,0.667),color);

    fragColor = vec4((f*f*f+.6*f*f+.5*f)*color,1.);
}

vec3 rotate(vec3 p, float angleX, float angleY)
{
    float cosA = cos(angleY);
	float sinA = sin(angleY);
	vec3 r = vec3(p.x, sinA * p.z + cosA * p.y, cosA * p.z - sinA * p.y);
    cosA = cos(angleX);
	sinA = sin(angleX);
	return (-vec3(cosA * r.x - sinA * r.z, r.y, sinA * r.x + cosA * r.z));
}

float flower(vec3 p)
{
    float x2 = p.x * p.x;
    float y2 = p.y * p.y;
    float z2 = p.z * p.z;
	return (1.44 * x2 + y2 + z2 +
            1.0 / sqrt(abs(cos((5.0 + cos(0.5 * iTime)) * atan(p.z / p.y)))) +
    		1.0 / (y2 + z2) + 3.0 / (x2 + y2 + z2) - 7.0);
}

