vec2 hash( vec2 p ) {
    p=vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))); 
    return fract(p);}

// return distance as x, and xy as y
vec3 voronoi( in vec2 x )
{
    vec2 n = floor( x );
    vec2 f = fract( x );

    vec2 mg, mr;

	vec3 m = vec3( 8.0 );
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2  g = vec2( float(i), float(j) );
        vec2  o = hash( n + g );

        vec2  r = g - f + o;
        //o = 0.5 + 0.5*sin( iTime + 6.2831*o );

	    //vec2  r = g - f; // square
		float d = dot( r, r );

        if( d<m.x ){
			mr = r;
			mg = g;
            m = vec3( d, o.x,o.y);
        } 

    }


    //distance to
    float md = 8.0 ;
  for( int j=-2; j<=2; j++ ){
	for( int i=-2; i<=2; i++ ){
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash( n + g );
        //o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        //o = 0.2*sin( iTime + 6.2831*o );
        vec2 r =  g - f + o;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }
  }


    return vec3( sqrt(m.x), m.y*m.z, md );
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = fragCoord.xy/max(iResolution.x,iResolution.y);
    p.x-=iTime*.02;
    p.y-=iTime*.01;

    // voronoi patterm
	vec3 c = voronoi(10.*p);
    c.y += floor(iTime/5.);

    //randomize order
    vec2 hashing = hash(vec2(0.,c.y))*30.;
	c.y *= hashing.x*hashing.y;
    c.y = fract(c.y);

    // colorize
    vec3 col;
    col = vec3(c.y);
    col = vec3(color(c.y));

    //edge
    vec3 edge = vec3(smoothstep( 0.04, 0.07,c.z));

    col = min(col,edge);

    // Output to screen
    fragColor = vec4(col,1.0);
}

