// Uniform wood grain:
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float scale = 100.0;
    float displacement = float(iFrame)*0.001;

    // Perlin noise-generated seed w/ displacement factors.
    int seed = int(cnoise(vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y)+displacement)*scale); 

    float i1 = float(i4_uniform_0i(seed));
    seed++;
    float i2 = float(i4_uniform_0i(seed));
    seed++;
    float i3 = float(i4_uniform_0i(seed));

    // convert int -> float
    i1 = float(i1)/2147483647.0;
    i2 = float(i2)/2147483647.0;
    i3 = float(i3)/2147483647.0;

    // clamp color to range for wood
    i1 = clamp(i3,0.25,0.32);//R
    i2 = clamp(i2,0.20,0.22);//G
    i3 = clamp(i1,0.10,0.11);//B

    fragColor = vec4(normalize(vec3(i1,i2,i3)) * vec3(1.40,1.50,1.15), 1.0);
}

