float DE(vec3 p) {


    float scale = 0.55;




    float dist =sdBox(p- vec3(0.0), vec3(1.0) );
    float dist2 =sdBox(p- vec3(0.0), vec3(0.35,3.0,0.35) );
	dist =dist*2.0-dist2;


	for (int i = 0; i < MENGER_ITERATIONS; i++) {
		dist = max(dist, -sdCrossRepScale(p, scale));
		scale *= 3.0*sin(scale);
	}




	return dist;
}

