//faster than general equation complex power
vec2 square(vec2 n){
	return vec2(n.x*n.x-n.y*n.y,2.0*n.x*n.y);
}
//we can also ask what happens when we raise z to a different power
//making the recursive definition z=z^p+c
vec2 power(vec2 n,float p){
    //this is eulers identity 
	//(a+bi)^p=len(a,b)e^(atan2(b,a)i)=len(a,b)(cos(atan2(b,a))+isin(atan2(b,a)))
    float theta=atan2(n);
    float len=length(n);
    return pow(len,p)*vec2(cos(p*theta),sin(p*theta));
}


