#define AA  0    // optional antialiasing (longer compile time)

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

struct {
  vec3 pCont;
  float rCont, hCont, hSurf, hCap, hLab;
} cnf;

vec3 sunDir, ltDir;
float dstFar, tCur, rTbl, hTbl;
int idObj;
bool inCont, chkLbl, doBott;
const int idCap = 1, idBal = 2, idLbl1 = 3, idLbl2 = 4, idLbl3 = 5, idStir = 6, idStirB = 7,
   idFom = 8, idTbl = 9, idFlr = 10, idRail = 11, idWal = 12;

const float pi = 3.14159;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float TObjDf (vec3 p)
{
  vec3 pp, q;
  float dMin, d, r, bf, s, rb, hb;
  dMin = dstFar;
  pp = p - cnf.pCont;
  r = length (pp.xz);
  if (doBott) {
    bf = 0.95;
    s = bf * cnf.rCont;
    q = pp;
    rb = cnf.rCont * (bf + (1. - bf) * smoothstep (0., 0.2 * cnf.rCont, q.y + cnf.hCont) -
       0.65 * smoothstep (0., 1.5 * cnf.rCont, q.y - cnf.hCont) +
       0.05 * smoothstep (0.,  0.1 * cnf.rCont, q.y - 2. * cnf.hCont - 0.7 * cnf.rCont));
    d = inCont ? rb - r : r - rb;
    d = max (d, max (q.y - 2. * cnf.hCont - cnf.rCont, - q.y - cnf.hCont));
    hb = cnf.hCont + 0.3 * (r * r / (s * s) - 1.);
    d = min (d, max (r - bf * cnf.rCont, abs (q.y + hb - 0.02) - 0.02));
    dMin = d;
  } else {
    bf = 0.95;
    q = pp;
    rb = cnf.rCont * (bf + (1. - bf) * smoothstep (0., 0.2 * cnf.rCont, q.y + cnf.hCont) +
       0.4 * smoothstep (0., 2.8 * cnf.rCont, q.y + 0.75 * cnf.hCont));
    d = inCont ? rb - r : r - rb;
    d = max (d, abs (q.y) - cnf.hCont);
    q.y -= - cnf.hCont + 0.05;
    d = min (d, PrCylDf (q.xzy, bf * cnf.rCont, 0.05));
    dMin = d;
  }
  return 0.8 * dMin;
}

float ObjDf (vec3 p)
{
  vec3 pp, q;
  float dMin, d, r, rm, a, s, db;
  dMin = dstFar;
  pp = p - cnf.pCont;
  r = length (pp.xz);
  rm = 1.4 * cnf.rCont;
  if (doBott) {
    q = pp;
    q.y -= cnf.hCap;
    d = PrCylDf (q.xzy, 0.4 * cnf.rCont + 0.4 - min (0.4 * (q.y + 0.4) * (q.y + 0.4), 1.), 0.4);
    DMIN (idCap);
    if (inCont) {
      q = pp;
      q.x = abs (q.x) - 0.7;
      q.y -= - cnf.hCont + 0.9;
      d = PrSphDf (q, 0.6);
      DMIN (idBal);
    }
    if (! inCont && chkLbl) {
      q = pp;
      q.y -= cnf.hLab;
      db = abs (r - cnf.rCont - 0.02) - 0.01;
      a = atan (q.z, - q.x);
      d = max (db, length (vec2 (r * a, q.y)) - 2.);
      DMIN (idLbl1);
      d = max (db, max (abs (2. * (mod (a, 2. * pi) - pi)), abs (q.y)) - 2.);
      DMIN (idLbl2);
    }
  } else {
    if (! inCont && chkLbl) {
      q = pp;
      q.y -= cnf.hLab;
      d = max (abs (r - rm - 0.02) - 0.01, length (vec2 (r * atan (- q.x, - q.z), q.y)) - 1.5);
      DMIN (idLbl3);
    }
    q = pp;
    q.xz = Rot2D (q.xz, 0.3 * pi);
    q.xy = Rot2D (q.xy - vec2 (1.2, 1.6), 0.05 * pi);
    d = PrCylDf (q.xzy, 0.2, cnf.hCont + 1.5);
    DMIN (idStir);
    q.y -= cnf.hCont + 1.5;
    d = PrSphDf (q, 0.3);
    DMIN (idStirB);
    q = pp;
    s = r / rm;
    s = 0.2 * (1. - s * s);
    q.y -= cnf.hCont - 0.1 + s;
    d = PrCylDf (q.xzy, rm - 0.03, 0.2 + s);
    DMIN (idFom);
  }
  if (! inCont) {
    q = p;
    q.y -= hTbl - 0.1;
    d = PrCylDf (q.xzy, rTbl, 0.1);
    DMIN (idTbl);
    q = p;
    q.y -= 0.5 * hTbl;
    d = PrCylDf (q.xzy, 1., 0.5 * hTbl);
    q = p;
    q.z -= 17.;
    d = min (d, max (q.y, q.z));
    DMIN (idFlr);
    q.y = abs (abs (q.y - 8.) - 4.) - 2.;
    q.z -= -0.5;
    d = length (q.yz) - 0.3;
    q = p;
    q.x = mod (q.x + 5., 10.);
    q -= vec3 (5., 6.5, 16.5);
    d = min (d, PrCylDf (q.xzy, 0.3, 7.5));
    DMIN (idRail);
    d = max (max (61. - length (p.xz), abs (p.y - 20.) -20.1), p.z - 15.);
    DMIN (idWal);
  }
  return 0.8 * dMin;
}

float TObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = TObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 CObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float LabSym (vec2 p)
{
  vec2 q;
  float d, r;
  r = length (p);
  d = max (min (0.06 - abs (0.1 - abs (r - 0.8)), p.y), min (0.06 - abs (p.y), 1.1 - abs (p.x)));
  q = Rot2D (p, 2. * pi * floor (16. * ((r > 0.) ? atan (p.y, - p.x) / (2. * pi) : 0.) + 0.5) / 16.);
  d = max (d, min (min (0.06 - abs (q.y), 0.2 - abs (q.x + 1.1)), p.y + 0.1));
  q.x += 1.5;
  d = max (d, min (0.1 - length (q), p.y + 0.1));
  return d;
}

vec4 ObjCol (vec3 p)
{
  vec4 c, colLb1, colLb2, colLb3, colLb4;
  vec3 q;
  vec2 w;
  float r, a, s;
  colLb1 = vec4 (0., 0.7, 0.4, 0.1);
  colLb2 = vec4 (0., 0., 0.5, 0.1);
  colLb3 = vec4 (1., 1., 0.2, 0.3);
  colLb4 = vec4 (0.7, 0.7, 0.7, 0.);
  q = p - cnf.pCont;
  r = length (q.xz);
  if (doBott) {
    a = atan (q.z, - q.x);
    if (idObj == idCap) {
      c = colLb1 + 0.1;
      if (r < 1.4) {
        if (q.y > cnf.hCap - 0.1) {
          s = LabSym (1.3 * q.xz);
          c = mix (mix (c, colLb2, smoothstep (-0.05, -0.01, s)), colLb3, smoothstep (0.01, 0.05, s));
        } else c *= 0.5;
      } else c *= 1. - 0.3 * sin (16. * a);
    } else if (idObj == idLbl1) {
      w = vec2 ((cnf.rCont + 0.1) * a, q.y - cnf.hLab);
      c = mix (colLb1, colLb2, smoothstep (0., 0.05, length (w) - 1.9));
      s = LabSym (0.9 * w);
      c = mix (mix (c, colLb2,
         smoothstep (-0.05, -0.01, s)), colLb3, smoothstep (0.01, 0.05, s));
      if (r < cnf.rCont + 0.01) c = mix (c, colLb4, 0.8);
    } else if (idObj == idLbl2) {
      w = vec2 ((cnf.rCont + 0.1) * (mod (a, 2. * pi) - pi), q.y - cnf.hLab);
      c = mix (colLb1, colLb2, smoothstep (0., 0.05, max (abs (w.x) - 2.95, abs (w.y) - 1.9)));
      w = mod (w + vec2 (0., 0.5), vec2 (3., 2.2)) - 0.5 * vec2 (3., 2.2);
      s = LabSym (1.5 * w);
      c = mix (mix (c, colLb2,
         smoothstep (-0.05, -0.01, s)), colLb3, smoothstep (0.01, 0.05, s));
      if (r < cnf.rCont + 0.01) c = mix (c, colLb4, 0.8);
    } else if (idObj == idBal) {
      c = vec4 (0.9, 0.9, 1., 0.2); 
    }
  } else {
    if (idObj == idLbl3) {
      w = vec2 ((1.4 * cnf.rCont + 0.1) * atan (- q.x, - q.z), q.y - cnf.hLab);
      c = mix (colLb1, colLb2, smoothstep (0., 0.05, length (w) - 1.4));
      s = LabSym (1.4 * w);
      c = mix (mix (c, colLb2, smoothstep (-0.05, -0.01, s)), colLb3, smoothstep (0.01, 0.05, s));
      if (r < 1.4 * cnf.rCont + 0.01) c = mix (c, colLb4, 0.8);
    } else if (idObj == idFom) {
      c = vec4 (1., 1., 1., 0.3) * (1. - 0.1 * Fbm2 (8. * q.xz));
    } else if (idObj == idStir) {
      q.xz = Rot2D (q.xz, 0.3 * pi);
      q.xy = Rot2D (q.xy - vec2 (1.2, 1.6), 0.05 * pi);
      c = mix (vec4 (0.8, 0.9, 0.8, 0.1), vec4 (0.9, 0.7, 0., 0.1), step (0., sin (4. * pi * q.y)));
    } else if (idObj == idStirB) {
      c = vec4 (0.9, 0.3, 0.3, 0.1);
    }
  }
  if (idObj == idTbl) {
    c = vec4 (0.85, 0.95, 0.9, 0.2);
    if (p.y > hTbl - 0.1) c = (mix (vec4 (0.5, 0.7, 0.4, 0.1), c, 
       smoothstep (0.02, 0.03, abs (Fbm2 (vec2 (0.5, 0.2) * p.xz) - 0.4)))) *
       (0.7 + 0.3 * smoothstep (0.2, 0.5, r - cnf.rCont));
  } else if (idObj == idFlr) {
    c = vec4 (0.5, 0.3, 0.1, 0.1) * (0.7 + 0.3 * SmoothBump (0.1, 3.9, 0.05, mod (p.z + 2., 4.))) *
       (0.8 + 0.2 * smoothstep (0.1, 0.2, length (p.xz) - rTbl));
  } else if (idObj == idRail) {
    c = vec4 (0.8, 0.8, 0.9, 0.1);
  } else if (idObj == idWal) {
    c = vec4 (0.9, 0.9, 0.85, 0.1);
  }
  return c;
}

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = 0.5 * p + vec2 (1.5, 0.) * tCur;
  h = 0.5 * sin (dot (q, vec2 (-0.05, 1.))) + 0.3 * sin (dot (q, vec2 (0.1, 1.2)));
  wFreq = 1.;
  wAmp = 1.;
  tw = 0.1 * tCur * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 3; j ++) {
    q *= qRot;
    t4 = q.xyxy * wFreq + tw.xxyy;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    t = 1. - sqrt (v4.xz * v4.yw);
    t *= t;
    t *= t;
    h += wAmp * dot (t, t);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return h;
}

vec3 WaveNf (vec3 p, float d)
{
  vec2 e;
  e = vec2 (max (0.01, 0.005 * d * d), 0.);
  p *= 0.5;
  return normalize (vec3 (0.3 * (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy),  WaveHt (p.xz + e.yx))), e.x)).xzy;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float sd, a, f, ff, fd, h;
  a = atan (rd.z, rd.x) - 0.005 * tCur;
  h = Fbm1 (32. * a);
  if (rd.y < 0.02 * h * h + 0.005) {
    col = mix (vec3 (0.3, 0.32, 0.3), vec3 (0.4, 0.43, 0.43), 0.5 +
       0.5 * dot (normalize (rd.xz), - normalize (sunDir.xz))) *
       (1. - 0.3 * Fbm2 (128. * vec2 (a, rd.y)));
  } else {
    q = 0.01 * (ro.xz + vec2 (0., 8.) * tCur + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = 0.7 * (0.7 + 0.5 * ff) * (vec3 (1.) - vec3 (0.2, 0.2, 0.1) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    fd = smoothstep (0.01, 0.1, rd.y);
    col = mix (mix ((vec3 (0.7, 0.7, 0.75)), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol, 0.1 + 0.9 * f * fd);
    sd = pow (max (dot (rd, sunDir), 0.), 256.);
    col += (0.1 * sd + 0.3 * pow (sd, 4.)) * vec3 (1., 1., 0.3);
  }
  return col;
}

void SetParm (int cnfId)
{
  hTbl = 6.;
  rTbl = 12.;
  if (cnfId == 0) {
    cnf.rCont = 3.;
    cnf.hCont = 2. * cnf.rCont;
    cnf.pCont = vec3 (2.5 * cnf.rCont, cnf.hCont + hTbl + 0.2, 0.);
    cnf.hSurf = 2.8 * cnf.hCont + hTbl;
    cnf.hCap = 2. * cnf.hCont + cnf.rCont + 0.1;
    cnf.hLab = 0.3 * cnf. hCont;
  } else {
    cnf.rCont = 2.5;
    cnf.hCont = 2.5 * cnf.rCont;
    cnf.pCont = vec3 (-2.5 * cnf.rCont, cnf.hCont + hTbl + 0.2, 0.);
    cnf.hSurf = 2. * cnf.hCont + hTbl - 0.1;
    cnf.hLab = 0.65 * cnf.hCont;
  }
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 roo, rdo, rdd, vn, vnW, colD, colR, col, row, rdw;
  vec2 vf;
  float dstObj, dstCont, dstContW, dstSurf, rdDotN, eta, hMenisc, dw, s, rFac;
  bool contHit, inLiq, inLiqW;
  SetParm (doBott ? 0 : 1);
  eta = 1.33;
  roo = ro;
  rdo = rd;
  inCont = false;
  chkLbl = true;
  dstCont = TObjRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  dstContW = dstCont;
  hMenisc = 0.;
  rdDotN = 0.;
  inLiq = false;
  contHit = (dstCont < min (dstObj, dstFar));
  if (contHit) {
    ro += dstCont * rd;
    vn = CObjNf (ro);
    vnW = vn;
    rdDotN = - dot (rd, vn);
    if (ro.y < cnf.hSurf) {
      rd = refract (rd, vn, 1. / eta);
      inLiq = true;
      inLiqW = true;
    }
    hMenisc = max (0., ro.y - cnf.hSurf);
    ro += 0.01 * rd;
    inCont = true;
    dstSurf = dstFar;
    if (inLiq && rd.y > 0. || ! inLiq && rd.y < 0.) dstSurf = - (ro.y - cnf.hSurf) / rd.y;
    dstCont = TObjRay (ro, rd);
    dstObj = ObjRay (ro, rd);
    if (dstSurf < min (min (dstCont, dstObj), dstFar)) {
      ro += dstSurf * rd;
      if (inLiq) {
        vn = vec3 (0., -1., 0.);
        rdd = refract (rd, vn, eta);
        if (length (rdd) > 0.) {
          rd = rdd;
          inLiq = false;
        } else {
          rd = reflect (rd, vn);
        }
      } else {
        vn = vec3 (0., 1., 0.);
        rd = refract (rd, vn, 1. / eta);
        inLiq = true;
        inLiqW = true;
      }
      ro += 0.01 * rd;
      dstCont = TObjRay (ro, rd);
      dstObj = ObjRay (ro, rd);
    }
    if (dstCont < min (dstObj, dstFar)) {
      ro += dstCont * rd;
      vn = CObjNf (ro);
      if (inLiq) {
        rdd = refract (rd, vn, eta);
        if (length (rdd) > 0.) {
          rd = rdd;
          inCont = false;
          inLiq = false;
        } else {
          rd = reflect (rd, vn);
        }
      } else inCont = false;
      hMenisc = min (hMenisc, max (ro.y - cnf.hSurf, 0.));
      ro += 0.01 * rd;
      dstObj = ObjRay (ro, rd);
    }
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    col4 = ObjCol (ro);
    vn = ObjNf (ro);
    vf = vec2 (0.);
    if (idObj == idFom) vf = vec2 (16., 1.);
    else if (idObj == idWal) vf = vec2 (128., 0.2);
    else if (idObj == idFlr) {
      s = mod (ro.z + 2., 4.);
      vn.yz = Rot2D (vn.yz, 0.2 * pi * SmoothBump (0.2, 0.3, 0.05, abs (s)) * sign (s - 2.));
    }
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    colD = col4.rgb * (0.2 + 0.2 * max (- dot (ltDir, vn), 0.) + 0.8 * max (dot (ltDir, vn), 0.) +
       col4.a * pow (max (dot (rd, reflect (sunDir, vn)), 0.), 32.));
  } else {
    row = ro;
    rdw = rd;
    rFac = 1.;
    if (rd.y < 0.) {
      dw = - row.y / rdw.y;
      row += dw * rdw;
      rdw = reflect (rdw, WaveNf (row, dw));
      rFac = 0.9;
    }
    colD = rFac * BgCol (row, rdw);
  }
  colR = vec3 (0.);
  if (contHit) {
    ro = roo;
    rd = rdo;
    ro += dstContW * rd;
    rd = reflect (rd, vnW);
    ro += 0.01 * rd;
    inCont = false;
    chkLbl = false;
    dstObj = ObjRay (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      col4 = ObjCol (ro);
      vn = ObjNf (ro);
      colR = col4.rgb * (0.2 + 0.2 * max (- dot (ltDir, vn), 0.) + 0.8 * max (dot (ltDir, vn), 0.) +
         col4.a * pow (max (dot (rd, reflect (sunDir, vn)), 0.), 32.));
    } else {
      row = ro;
      rdw = rd;
      rFac = 1.;
      if (rd.y < 0.) {
        dw = - row.y / rdw.y;
        row += dw * rdw;
        rdw = reflect (rdw, WaveNf (row, dw));
        rFac = 0.9;
      }
      colR = rFac * BgCol (row, rdw);
    }
  }
  if (contHit) {
    if (inLiqW) hMenisc = 0.;
    col = mix (vec3 (0.8, 0.7, 0.2), vec3 (1.), smoothstep (0., 0.2, hMenisc)) * colD;
    if (eta != 1.) col = mix (colR, col, 0.2 + 0.8 * smoothstep (0., 0.9, rdDotN));
  } else col = colD;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  } else {
    //float t = mod (0.01 * tCur, 2.);
    //az += 0.2 * pi * SmoothBump (0.3, 0.7, 0.1, mod (t, 1.)) * sign (t - 1.);
    //el -= 0.03 * pi * sin (0.007 * pi * tCur);
  }
  el = clamp (el, -0.25 * pi, 0.03 * pi);
  az = clamp (az, -0.3 * pi, 0.3 * pi);
  if (abs (abs (az) - 0.5 * pi) < 0.19 * pi) az = (0.5 + 0.19 * sign (abs (az) - 0.5 * pi)) * pi * sign (az);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 15., -55.);
  zmFac = 3.5;
  dstFar = 150.;
  ltDir = vuMat * normalize (vec3 (0.5, 1., -1.));
  sunDir = normalize (vec3 (1., 1., -1.));
  doBott = (sign (abs (mod (az + 2. * pi, 2. * pi) - pi) - 0.5 * pi) == sign (uv.x));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

/*
General comments:

Everything is vectors. Whenever a point is mentioned below, I actually mean a vector drawn 
from some location to that point. Distance between points is really just the difference
between the two vectors representing those points, i.e. everything is vector addition or
subtraction, and dot and cross products can be used to obtain useful information.

*/

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST 0.01

float GetDist(vec3 p) {
// Returns the shortest distance from point p to any of the shapes in the field.
// This function also contains definitions of the shapes themselves. This is probably
// not the best place for these...

	vec4 s = vec4(0, 1, 6, 1); // Sphere parameters. First three are coords of center. Last
                               // is radius. 

    // Distance to sphere's surface is the distance between point p and the sphere's center,
    // minus the radius of the sphere.
    float sphereDist = length(p-s.xyz)-s.w; 

    // We assume a horizontal plane in the x-z plane, so the distance from p to the plane
    // is just the y-coord of p.
    float planeDist = p.y;

    return min(sphereDist,planeDist);
}


float RayMarch(vec3 ro, vec3 rd) {
// Marches along a ray with origin at ro and direction rd. Returns the
// distance along the ray from ro to the first intersection with any shape in the view.

	float dO = 0.; // Distance to origin from current point in the marching process.

    for (int i=0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd*dO; // Point that's a distance dO along the ray.
        float dS = GetDist(p); // Shortest distance from p to the object.
        dO += dS; // Add the shortest distance to the ray location.
        if (dO>MAX_DIST || dS<SURF_DIST) break; // Check for stopping conditions
    }
    return dO;
}


vec3 GetNormal(vec3 p) {
// Finds the normal vector to the surface of the object at point p.

	float d = GetDist(p); // Find the shortest distance to the object surface from point p.
                          // This will not be exactly zero because of the error cushion built
                          // into the ray marching algorithm.
    vec2 e = vec2(0.01, 0); // Set up a perturbation vector.
    vec3 n = d - vec3(    // This line of code hides quite a bit of clever geometry. Draw yourself
        GetDist(p - e.xyy), // some diagrams in 2D, and do the necessary trig to convince yourself 
        GetDist(p - e.yxy), // that it is correct.
        GetDist(p - e.yyx));

    return normalize(n);
}


float GetLight(vec3 p) {
// Diffuse lighting and shadow function. 

    vec3 lightPos = vec3(0, 5, 6); // Defines the position of the light source.
    lightPos.xz += vec2(sin(iTime),cos(iTime))*2.; // Moves the light source in a horizontal circle.
    vec3 l = normalize(lightPos-p); // Vector pointing from p to the light source.
    vec3 n = GetNormal(p); // Unit vector normal to the object's surface.

    float dif = clamp(dot(n,l),0.,1.); // Brightness is set to the dot product between the surface
                                       // normal and the ray pointing from the object to the light.
                                       // clamp function ensures no brightnesses less than zero.

    float d = RayMarch(p+n*SURF_DIST*2., l); // Use ray marching to determine the distance from the
                                             // the current point on the object to the light source.
                                             // Use a location slightly offset from the current point
                                             // else the ray marching algorithm will exit right away,
                                             // because we're too close to the object.

    if (d < length(lightPos - p)) dif *= .1; // If the distance traveled by the ray marching algorithm
                                             // from the the current point toward the light source is 
                                             // less than the full distance to the light, then we've
                                             // encountered an object between us and the light, and we're
                                             // therefore in shadow, so lower the brightness.    

    return dif; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Black screen:
    vec3 col = vec3(0);

    // Camera:
    vec3 ro = vec3(0, 1, 0);

    // Rays drawn from camera through each pixel of screen:
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    // Find the distance along each ray at which we bump into the first shape.
    // Since rd is a vector of rays passing through the screen pixels, d is going
    // to be a vector of distances.
    float d = RayMarch(ro, rd);

    vec3 p = ro + rd*d;
    float dif = GetLight(p);
    col = vec3(dif);

    // The distances are all going to be greater than 1, given the location of ro and
    // the shapes we've defined. This means that the color used to depict d will be
    // white for all pixels, which is not very interesting. Therefore, divide d by 6
    // to make d values smaller. We use 6 because that's how far away the sphere is.
    //d /= 6.;
    //col = vec3(d);

    // Output to screen
    fragColor = vec4(col,1.0);
}

