    col = vec3 (0.8, 0.7, 0.2) * (0.5 + 0.5 * smoothstep (0., 0.03, max (uv.x, uv.y)));
  } else col = vec3 (0.75);
  if (false && mPtr.z > 0. && length (uv) < 0.01) col *= 0.5;
  fragColor = vec4 (col, 1);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  return max (length (p.xy - vec2 (clamp (p.x, - rhi, rhi), 0.)) - rlo, abs (p.z) - h);
}

float PrFlatCyl2Df (vec2 p, float rhi, float rlo)
{
  return length (p - vec2 (clamp (p.x, - rhi, rhi), 0.)) - rlo;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define STATIC_ON true
#define STATIC_FADE_PERIOD 0.7

#define BAR_NOISE_ON true

#define STATIC_NOISE_THRESHOLD 0.8
#define BAR_NOISE_THRESHOLD 0.98
#define MAX_BAND_WIDTH 50.

float randomNoise(float inputValue)
{
	return ((sin(inputValue*344.3423+434.21222*inputValue)/8.)+.125)
			+ ((cos(inputValue*456.15+5543.53428*inputValue)/8.)+.125)
        	+ ((sin(inputValue*54.21111+455.24534*inputValue)/8.)+.125)
        	+ ((cos(inputValue*54.21111+951.23984*inputValue)/8.)+.125);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 XYStaticCol = vec3(1.);
    vec3 YBarStaticCol = vec3(0.7);
    vec3 vhsBlueCol = vec3(0.,0.,.8);
	vec3 finalCol = vec3(0.);

    // Useful Signals
    float YBarNoise = randomNoise(fragCoord.y*iTime);
    float XYStaticNoise = randomNoise(fragCoord.x*fragCoord.y*iTimeDelta);
    float sinBackAndForth = (sin(iTime)/2.)+.5;
    vec4 videoSignal = texture(
        iChannel0,
        vec2(
            fract(uv.x+(fract(iTime)/(100.+iTime))),
        	fract(uv.y+(0.01*sin(iTime/YBarNoise)*fract(iTime/3.)))
        )
    );



    // Start with Blue
   	finalCol = videoSignal.rgb;

    // Add basic Static
    if(STATIC_ON)
    {
        if (XYStaticNoise > STATIC_NOISE_THRESHOLD)
        {
            finalCol = mix(XYStaticNoise*XYStaticCol*(.5*cos(iTime/STATIC_FADE_PERIOD)+1.), finalCol, 0.7);
        } 
    }

    if (BAR_NOISE_ON)
    {

        if (fract(iTime)<.2)
        {
            for (float row = fragCoord.y-MAX_BAND_WIDTH; row<fragCoord.y+MAX_BAND_WIDTH; row++)
            {
                if (randomNoise(row*iTime)>BAR_NOISE_THRESHOLD)
                {
                    finalCol = YBarStaticCol*YBarNoise;
                }
            }
        }
    }

	vec3 screenDoor = vec3(
        .2*(5.*sin((fract(iTime/6.)+10.)*fragCoord.y)+1.),
        .2*(5.*cos((fract(iTime/6.)+10.)*fragCoord.x)+1.), 
        1.); 

