                  );  

    // Output Final color to screen
    fragColor = vec4(finalCol,1.0);
}

float smin(float a, float b, float k) {
	float h = max(k - abs(a - b), 0.0);

    return min(a, b) - h * h /(k * 4.); 
}

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;

    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float sdEllipsoid(vec3 p, vec3 rad) {
	float k1 = length(p/rad);
    float k2 = length(p/(rad * rad));

    return k1 * (k1 - 1.) / k2;
}

float sdMain(vec3 p) {
	float t = fract(iTime * .75);
    float y  = 4. * t * (1. - t);

    vec3 v = vec3(0., y, 0.);

    float sy = 0.5 + 0.5 * y;
	float sz = 1./ sy;

    //box shape
    vec3 rad = vec3(.1, .2 * sy, .1 * sz);
    vec3 pos = p - v;
    float d = sdBox(pos, rad);

    //sphere shape
	vec3 pos1 = pos - vec3(0., -.2, 0.);
	vec3 rad1 = vec3(0.2, 0.2 * sy, 0.2 * sz);
    float d1 = sdEllipsoid(pos1, rad1);

    d = smin(d, d1, .05);
    return d;
}

float map (vec3 p) {
	float d = sdMain(p);

	float d1 = (p.y) - (-0.25);    

    return min(d, d1);
}

vec3 getNormal(in vec3 p) {
	vec2 e = vec2(0.001, 0.);   
    return normalize( vec3( map(p + e.xyy) - map(p - e.xyy),
                           	map(p + e.yxy) - map(p - e.yxy),
                           	map(p + e.yyx) - map(p - e.yyx) ) );
}

float rayMarch(vec3 ro, vec3 rd) {
	float t = 0.;
    for(int i = 0; i < 100; ++i) {
        vec3 p = ro + t * rd;
        float h = map(p);
        if(h < .001)
            break;
        t += h;
        if(t > 20.)
            break;
    }
    if(t > 20.) 
        t = -1.0;
    return t;
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax) {
	float res = 1.;
    float t = tmin;
    float ph = 1e20;

    for(int i = 0; i < 25; ++i) {
		float h = map(ro + t * rd);

        float y = h * h / (2. * ph);
        float d = sqrt(h * h - y * y);
        res = min(res, 10. * d / max(0., t - y));

        ph = h;
        t += h;
    	if(res < .0001 || t > tmax) 
            break;
    }
    return clamp(res, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);

    vec2 uv = (2. * (fragCoord) - iResolution.xy)/iResolution.y;

    float a = 10.0 * iMouse.x / iResolution.x;

    float howFar = 1.25;
    vec3 ro = vec3(howFar * sin(a), 0.45, howFar * cos(a));

    vec3 lookAt = vec3(0., 0.45, 0.);
    vec3 f = normalize(lookAt - ro);
    vec3 u = normalize(cross(f, vec3(0., 1., 0.)));
    vec3 r = normalize(cross(u, f));
    float zoom = 1.5;

    vec3 rd = normalize(uv.x * u + uv.y * r + zoom * f);

    col = vec3(0.4, 0.75, 1.) - rd.y * .7;
    col = mix(col, vec3(0.7, 0.75, 0.8), exp(-10. * rd.y));


    float t = rayMarch(ro, rd);

    if( t > 0.) {
        vec3 p = ro + t * rd;
        vec3 norm = getNormal(p);

        vec3 mat = vec3(0.18);
        vec3 sunDir = normalize(vec3(0.8, 0.5, 0.2));        

        float sunDif 	= clamp(dot(norm, sunDir), 0., 1.) 
            * softShadow(p, sunDir, .001, 4.);
        float sunShadow = step(rayMarch(p + norm*0.001, sunDir), 0.0); 
        float skyDif 	= clamp(0.5 + 0.5 * dot(norm, vec3(0., 1., 0.)), 0., 1.);   
        float bounceDif = clamp(0.5 + 0.5 * dot(norm, vec3(0., -1., 0.)), 0., 1.);   

        col  = mat * vec3(7.0, 5.0, 4.0) * sunDif * sunShadow;
        col += mat * vec3(0.5, 0.8, 0.9) * skyDif;
        col += mat * vec3(0.7, 0.3, 0.2) * bounceDif;

    }
    col = pow(col, vec3(0.4545));
    //col /= float(AA * AA);


    fragColor = vec4(col,1.0);
}

