void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 center = vec2(.5*ratio, .5);

    float border = circle(uv,.45, 0., center);
    border -= circle(uv, .5, 0., center);

    float blank = circle(uv,.38, 0., center);
    blank -= circle(uv, .45, 0., center);

    float middle = circle(uv,.02, 0., center);

    vec2 uvMin = uv;
    vec2 uvHour = uv;

    uvMin -= center;
    uvMin *= rotate2d((iTime)*1.2);
    uvMin += center;

    uvHour -= center;
    uvHour *= rotate2d((iTime)*.1);
    uvHour += center;

    float minHand = rect(uvMin, center,vec2(.02,.4));
    float hourHand = rect(uvHour, center,vec2(.02,.25));

    //lines
    float l = line(uv, vec2(.5*ratio, 0.), vec2(.5*ratio, 1.));
    l += line(uv, vec2(0.*ratio, .5), vec2(1.*ratio, .5));
    l += line(uv, vec2(.35*ratio, 0.), vec2(.65*ratio, 1.));
    l += line(uv, vec2(.05*ratio, 0.), vec2(.95*ratio, 1.));
    l += line(uv, vec2(.05*ratio, 1.), vec2(.95*ratio, 0.));
    l += line(uv, vec2(.35*ratio, 1.), vec2(.65*ratio, 0.));

    vec3 col = mix(vec3(1.), vec3(0.), l);
    col = mix(vec3(1.), col, blank);
    col = mix(col, vec3(0.), minHand);
    col = mix(col, vec3(0.), hourHand);
    col = mix(vec3(0.), col, middle);
    col = mix(col, vec3(0.), border);

    // Output to screen
    fragColor = vec4(col,1.0);
}

