const float PI = 3.14159265;
mat2 r2d(float a){float sa = sin(a);float ca=cos(a);return mat2(ca,sa,-sa,ca);}

float lenny(vec2 v)
{
  return abs(v.x)+abs(v.y);
}
float sat(float a)
{
  return clamp(a,0.,1.);
}
vec3 sat(vec3 v)
{
  return vec3(sat(v.x),sat(v.y), sat(v.z));
}
float _sub(float a, float b)
{
  return max(a,-b);
}
float _cir(vec2 uv, float sz)
{
  return length(uv)-sz;
}

float _loz(vec2 uv,float sz)
{
  return lenny(uv)-sz;
}

vec3 tunnel(vec2 uv,float sz)
{
  //uv += vec2(sin(length(uv)),cos(length(uv)))*.1;
  float a = sz*atan(uv.y,uv.x)/PI;
  float d = .021*iTime+.01/length(uv);
  vec3 col = texture(iChannel0,vec2(d,a)).xxx;
  return sat(1.-length(uv*.2))*col*sat(length(uv*20.))*mix(vec3(.28+abs(uv.x),.34,sat(.17+length(uv))),vec3(.8,.5,.7),length(uv)-.9);
}

vec3 rdrLand(vec2 uv)
{
  vec3 back;
  vec3 col;
  col += 2.*tunnel(uv,1.);
 // col += 1.5*tunnel(uv*.8*r2d(iTime*.2),.2);
  col += tunnel(uv*5.2*r2d(iTime*.2),.2);

  for (int i = 0;i<8;++i)
  {
    float fi= float(i);
    float sz = 5./(fi+1.);
    float szc = 7.*mod(sz+iTime*.1,1./9.);
    float cut = _cir(uv, szc-szc/10.);
    float ci = _cir(uv, szc);
    float cir = 1.-sat(_sub(ci,cut)*20.);
    col += vec3(.53,.94,.58)*(cir)*(sz-.1)*.1;
  }

  return back+col*.5;
}

vec3 rdrSqr(vec2 uv)
{
  uv = uv *r2d(PI/4.);
  vec3 acc;
  for (int i = 0; i < 16; i++)
  {
     vec2 suv = uv;
     suv = suv*r2d(.8*smoothstep(0.,.9,mod(iTime+float(i)*.5,.9)));
     vec3 col;

     float an = iTime*.1+.15*float(i)+atan(uv.y, uv.x)/PI;
     float sz = mod(iTime/(float(i+1)*.7),1.);
     float sqr = _sub(_loz(suv, sz),_loz(suv,sz*.999));
     col = vec3(1.,float(i)/10.,uv.y*.5+.2)*((1.-sat(sqr*200.))+.7*(1.-sat(sqr*15.)))*sat(float((mod(an,.05)-.015))*20.);
     acc += col;
  }
    return acc;
}

vec3 rdrScn(vec2 uv)
{
  float maskLand = 1.-sat(_cir(uv,.5)*50.);

  vec3 land = rdrLand(uv).zyx;
  land += rdrSqr(uv)*sat(lenny(uv*2.))*.7;
  return land;
}

vec3 rdr2(vec2 uv)
{
    vec2 ouv = uv;
    ouv.y *= 1.+cos((ouv.x*PI));
    vec3 col = rdrScn(ouv);
  	col *= vec3(.7, .34, .89)*sat(1.-lenny(uv))*2.;
    vec3 col2;
    col2 += 2.*vec3(.7, .54, .89).zyx*sat(1.-lenny(uv*vec2(1., 8.)));
    col2 += vec3(.7, .54, .89).zyx*sat(1.-lenny(uv*vec2(.5, 90.)));
    col2 += .9*vec3(.7, .54, .89).zyx*sat(1.-lenny((uv*r2d(PI/4.)*vec2(.5, 10.))));
    col2 += .5*vec3(.7, .54, .89).zyx*sat(1.-lenny((uv*r2d(PI/4.)*vec2(.5, 2.))));
    col2 += .5*vec3(.7, .54, .89).zyx*sat(1.-lenny(((uv+vec2(-.2, -.5))*r2d(PI/4.)*vec2(.5, 90.))));
    col2 += .5*vec3(.7, .54, .89).zyx*sat(1.-lenny(((uv+vec2(-.2, .3))*r2d(PI/4.)*vec2(.5, 5.))));

    vec3 halo = vec3(.6,.7,.9)*(1.-sat(_sub(_cir(uv, .65), _cir(uv, .6))*15.));
    return col+col2*.5+halo*.4;
}

vec3 rdrPost(vec2 uv)
{
    vec2 dir = uv*.05;
    float r = rdr2(uv+dir).x;
    float g = rdr2(uv).y;
    float b = rdr2(uv-dir).z;
    return vec3(r,g,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  uv -= vec2(.5)*iResolution.xy/iResolution.xx;
  uv*= 1.5;
  vec3 col = rdrPost(uv);
  fragColor = vec4(col, 1.0);
}

#define MAX_RAY_STEPS 100
#define EPS 0.001
#define t iTime

struct Light {
    vec3 p;
    float r;
    vec3 c;
} light;

struct RM {
    float sdf;
    float atm;
};

float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float cylinder(vec3 p, float r) {
    return length(p.xz) - r;
}

float plane(vec3 p, float h) {
    return p.y - h;
}

float SceneSdf(vec3 p) {
    float plane = plane(p, -5.0);
    float dcBox1 = max(box(p - vec3(0., 1., 0.), vec3(1., 0.1, 1)), -cylinder(p, 0.5));
    float dcBox2 = max(box(p - vec3(0., 0., 0.), vec3(1., 0.1, 1)), -cylinder(p, 0.5));
    float d = min(dcBox1, dcBox2);
    d = min(d, plane);
    float dLight = length(light.p - p) - light.r;
    d = min(d, dLight);
    return d;
}

vec3 GetNormal(vec3 p) {
    float d = SceneSdf(p);
    vec2 e = vec2(0.001, 0.);
    vec3 n = d - vec3(
        SceneSdf(p - e.xyy),
        SceneSdf(p - e.yxy),
        SceneSdf(p - e.yyx)
    );

    return normalize(n);
}

float WeightedLightBlob(Light light, float decay, vec3 pos) {
    return light.r * decay / pow(length(pos - light.p), 2.);
}

RM RayMarching(vec3 ro, vec3 rd) {
    float sdf = 0.;
    float atm = 0.;
    float decay = 1.;
    vec3 pos = ro;
    for(int i = 0; i < MAX_RAY_STEPS; i++) {        
        float d = SceneSdf(pos);
        atm += WeightedLightBlob(light, decay, pos);
        if (abs(d) < EPS) {
            break;
        }
        sdf += d;
        pos += d * rd;
        decay *= 0.98;
    }
    return RM(sdf, atm);
}

vec3 RenderSceneWithLight(Light light, vec3 ro, vec3 rd) {
    RM rmMap = RayMarching(ro, rd);
    vec3 p = ro + rmMap.sdf * rd;
    vec3 n = GetNormal(p); 
    vec3 pToL = light.p - p;
    vec3 pToLN = normalize(pToL);
    float diffuse = max(dot(n, pToLN), 0.);

    vec3 col = diffuse * vec3(1.) + rmMap.atm * light.c;

    RM rmShadow = RayMarching(p + n, pToLN);
    if (rmShadow.sdf < length(pToL) - light.r) {
        col *= 0.3;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float dor = 2.5;
    vec2 cs = vec2(0.5 + 1.5 * cos(0.5 * t), 0.5 + 1.5 * sin(0.5 * t));
    vec3 ro = vec3(dor * cs.x - 2., 3. * cs.x, dor * cs.y - 2.);
    vec3 lookAt = vec3(0.);
    vec3 col = vec3(0.);

    vec3 oz = normalize(lookAt - ro);
    vec3 ox = normalize(cross(vec3(0., 1., 0.), oz));
    vec3 oy = normalize(cross(oz, ox));

    vec3 rd = uv.x * ox + uv.y * oy + oz;

    light = Light(vec3(0., cs.x, 0.), .01, vec3(1, 0.871, 0.231));
    col = RenderSceneWithLight(light, ro, rd);

    fragColor = vec4(col, 1.);
}

vec3 waves(vec2 uv, vec3 color)
{
    // To create the waves
	float wave_width;
	uv  = -.2 + 5.0 * uv;
	uv.y += 0.0;
	for(float i = .0; i < 15.; i++) {

		uv.y += (.07 * sin(uv.x + i/2.0 + iTime ));
		wave_width = abs(1. / (150.0 * uv.y));
		color += vec3(wave_width * 1.9, wave_width, wave_width * 1.5);
	}

    return color;
}

float circle(vec2 uv,float radius, float blur,  vec2 pos)
{

    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle), sin(angle), cos(angle));
}

