vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b * cos(6.28318 * (c * t + d) );
}



// RANDOM NOISE FUNCTIONS
float random1(vec3 p) {
  return fract(sin(dot(p, vec3(127.1, 311.7, 191.999))) * 43758.5453);
}

vec2 random2(vec2 p) {
  return fract(sin(vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)))) * 43758.5453);
}

float smootherStep(float a, float b, float t) {
  t = t*t*t*(t*(t*6.0 - 15.0) + 10.0);
  return mix(a, b, t);
}

vec3 random3(vec3 p) {
    return fract(sin(vec3(dot(p,vec3(127.1, 311.7, 191.999)),
                          dot(p,vec3(269.5, 183.3, 765.54)),
                          dot(p,vec3(420.6, 631.2, 109.23))
                    )) * 43758.5453);
}



// PERLIN NOISE FUNCTION
float perlin(vec3 p) {
  vec3 pFract = fract(p);
  float llb = random1(floor(p));
  float lrb = random1(floor(p) + vec3(1.0,0.0,0.0));
  float ulb = random1(floor(p) + vec3(0.0,1.0,0.0));
  float urb = random1(floor(p) + vec3(1.0,1.0,0.0));

  float llf = random1(floor(p) + vec3(0.0,0.0,1.0));
  float lrf = random1(floor(p) + vec3(1.0,0.0,1.0));
  float ulf = random1(floor(p) + vec3(0.0,1.0,1.0));
  float urf = random1(floor(p) + vec3(1.0,1.0,1.0));

  float lerpXLB = smootherStep(llb, lrb, pFract.x);
  float lerpXHB = smootherStep(ulb, urb, pFract.x);
  float lerpXLF = smootherStep(llf, lrf, pFract.x);
  float lerpXHF = smootherStep(ulf, urf, pFract.x);

  float lerpYB = smootherStep(lerpXLB, lerpXHB, pFract.y);
  float lerpYF = smootherStep(lerpXLF, lerpXHF, pFract.y);

  return smootherStep(lerpYB, lerpYF, pFract.z);
}



// FBM NOISE FUNCTION
float fbm(vec3 p, float octaves) {
  float amp = 0.5;
  float freq = 8.0;
  float sum = 0.0;
  float maxSum = 0.0;
  for(float i = 0.0; i < 10.0; ++i) {
    if(i == octaves)
    break;
    maxSum += amp;
    sum += perlin(p * freq) * amp;
    amp *= 0.5;
    freq *= 2.0;
  }
  return sum / maxSum;
}



// WORLEY NOISE FUNCTION
float worley2D(vec2 p) {
    vec2 pointInt = floor(p);
    vec2 pointFract = fract(p);
    float minDist = 1.0;

    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 neighbor = vec2(float(x), float(y));
            vec2 point = random2(pointInt + neighbor);
            point = 0.5 + 0.5 * sin(iTime * 0.01 + 6.2831 * point);  
            vec2 diff = neighbor + point - pointFract;
            float dist = length(diff);
            minDist = min(minDist, dist);
        }
    }
    return minDist;
}

float worley3D(vec3 p) {
    p *= 0.3;
    vec3 pointInt = floor(p);
    vec3 pointFract = fract(p);

    float minDist = 1.0;

    for (int z = -1; z <= 1; z++) {
        for (int y = -1; y <= 1; y++) {
            for (int x = -1; x <= 1; x++) {
                vec3 neighbor = vec3(float(x), float(y), float(z));
                vec3 point = random3(pointInt + neighbor);
                point = 0.5 + 0.5 * sin(iTime * 1.5 * 0.01 + 6.2831 * point);  
                vec3 diff = neighbor + point - pointFract;
                float dist = length(diff);
                minDist = min(minDist, dist);
            }
        }
    }
    return minDist;
}



// COORDINATE & ROTATION FUNCTIONS
void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec3 rotatePtY(vec3 pt, float a)
{    
    pR(pt.yz, 0.);
    pR(pt.xz, a);
    pR(pt.xy, 0.);

    return pt;
}

vec3 rotatePtX(vec3 pt, float a)
{    
    pR(pt.yz, a);
    pR(pt.xz, 0.);
    pR(pt.xy, 0.);

    return pt;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

void CoordinateSystem(in vec3 v1, out vec3 v2, out vec3 v3) {
    if (abs(v1.x) > abs(v1.y)) {
    	v2 = vec3(-v1.z, 0.0, v1.x) / sqrt(v1.x * v1.x + v1.z * v1.z);
    } else {
    	v2 = vec3(0.0, v1.z, -v1.y) / sqrt(v1.y * v1.y + v1.z * v1.z);
    }
    v3 = cross(v1, v2);
}

mat3 TangentToWorld(vec3 nor) {
    vec3 tangent, bitangent;
    CoordinateSystem(nor, tangent, bitangent);
    return mat3(tangent, bitangent, nor);
}



void rayCast(vec3 eye, vec3 ref, vec2 ndc, out vec3 dir) {    
    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + ndc.x * H + ndc.y * V;
    dir = normalize(p - eye);
}



// SHAPE SDFs
float sphere(vec3 p, float r, vec3 c) {
	return distance(p, c) - r;
}

float cube(vec3 p, vec3 c) {
    return length(max(abs(p) - c, 0.0));
}

float squarePlane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}


