void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = VENUS_SDF;
    obj = VENUS_ID;

    float t2;
    if ((t2 = EARTH_SDF) < t) {
    	t = t2;
      	obj = EARTH_ID;
    }

    if ((t2 = MARS_SDF) < t) {
    	t = t2;
      	obj = MARS_ID;
    }

    if ((t2 = SATURN_SDF) < t) {
    	t = t2;
      	obj = SATURN_ID;
    }

    if ((t2 = TORUS1_SDF) < t) {
    	t = t2;
      	obj = TORUS1_ID;
    }

    if ((t2 = TORUS2_SDF) < t) {
    	t = t2;
      	obj = TORUS2_ID;
    }

    if ((t2 = NEPTUNE_SDF) < t) {
    	t = t2;
      	obj = NEPTUNE_ID;
    }

    if ((t2 = BACK_SDF) < t) {
    	t = t2;
      	obj = BACK_ID;
    }

    if ((t2 = LIGHT_SDF) < t) {
    	t = t2;
    	obj = LIGHT_ID;
    }
}


