vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


vec3 WorlyTangentSpaceNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(worley3D(pos + epsilon.yxx) - worley3D(pos - epsilon.yxx),
                          worley3D(pos + epsilon.xyx) - worley3D(pos - epsilon.xyx),
                          worley3D(pos + epsilon.xxy) - worley3D(pos - epsilon.xxy)));
}


vec3 FbmTangentSpaceNormal(vec3 pos, float a) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(fbm(pos + epsilon.yxx, a) - fbm(pos - epsilon.yxx, a),
                          fbm(pos + epsilon.xyx, a) - fbm(pos - epsilon.xyx, a),
                          fbm(pos + epsilon.xxy, a) - fbm(pos - epsilon.xxy, a)));
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;

    for (int i = 0; i < RAY_STEPS; ++i) {
    	vec3 pos = origin + t * dir;
        float m; // Minimum distance from this point to an object in the scene
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
        	return;
        }
        t += m;
    }
	// We hit nothing
    t = -1.0;
    hitObj = -1;
}


float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - shadowMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}


float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}


float subsurfaceColor(vec3 lightDir, vec3 n, vec3 view, float thin) {
	vec3 scatterDir = lightDir + n * DISTORTION;
	float lightReachingEye = pow(clamp(dot(view, -scatterDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
	float attenuation = max(0.0, dot(n, lightDir) + dot(view, -lightDir));
    float totalLight = attenuation * (lightReachingEye + AMBIENT) * thin;
    return totalLight;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view, float thin) {
    vec3 worley_normal = WorlyTangentSpaceNormal(p);
    worley_normal = TangentToWorld(n) * worley_normal;
    float worleyLambert = dot(light, worley_normal);

    vec3 fbm_normal = FbmTangentSpaceNormal(p, 4.0);
    fbm_normal = TangentToWorld(n) * fbm_normal;
    float fbmLambert = dot(light, fbm_normal);

    float lambert = max(0.0, dot(n,light));

    switch(hitObj) {
        // Sphere 1
        case VENUS_ID:
        vec3 color1 = vec3(1.0,0.88,0.7) * vec3(1.0,0.7,0.4) * palette(fbm(p * sin(iTime * 0.05),4.0),av,bv,cv,dv) * softShadow(light,p,0.1,6.0);
        return color1 + vec3(0.0,0.2,1.0) * subsurfaceColor(light,n,view,thin) * vec3(1.0,0.88,0.7);
        break;
        // Sphere 2
        case EARTH_ID:
        return palette(perlin(p),ae,be,ce,de) * softShadow(light,p,0.1,6.0);
        break;
        // Sphere 3
        case MARS_ID:
        vec3 color2 = vec3(0.6,0.5,0.5) * palette(worley3D(p * sin(iTime * 0.2) * 2.3),am,bm,cm,dm) * softShadow(light,p,0.1,6.0);
        return color2 + vec3(0.2,0.8,0.5) * subsurfaceColor(light,n,view,thin);
        break;
        // Sphere 4
        case SATURN_ID:
        return vec3(0.9,0.9,0.9) * palette(fbm(p * sin(iTime * 0.05),4.) * 1.65,as,bs,cs,ds) * softShadow(light,p,0.1,6.0);
        break;
        // Torus 1
        case TORUS1_ID:
        vec3 color3 = vec3(0.3,0.1,0.8) * vec3(0.7,0.2,0.8) * lambert * softShadow(light,p,0.1,6.0);
        return color3 + vec3(1.0,0.5,0.3) * subsurfaceColor(light,n,view,thin) * vec3(1.0,0.88,0.7);
        break;
        // Torus 2
        case TORUS2_ID:
        vec3 color4 = vec3(1.0,0.88,0.8) * vec3(0.9,0.2,0.8) * lambert * softShadow(light,p,0.1,6.0);
        return color4 + vec3(1.0,0.7,0.3) * subsurfaceColor(light,n,view,thin) * vec3(1.0,0.88,0.7);
        break;
        // Sphere 5
        case NEPTUNE_ID:
        vec3 color5 = vec3(0.4,0.8,1.6) * vec3(1.0,0.4,0.8) * palette(lambert,av,bv,cv,dv) * softShadow(light,p,0.1,6.0) * worley3D(p);
        return color5 + vec3(1.0,0.0,0.8) * subsurfaceColor(light,n,view,thin) * vec3(1.0,0.88,0.7) * worley3D(p);
        break; 
        // BACKGROUND
        case BACK_ID:
        return vec3(0.0, 0.3, 0.4) * palette(worleyLambert,as,bs,cs,ds);
        break;
        // Light
        case LIGHT_ID:
        return vec3(1.0, 0.88, 0.7);
        break;
        // Background
        case -1:
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
	float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, AO_KVAL);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(t, surfaceColor, isect, hitObj);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec3 lightPos = rotateY(vec3(30.0, 4.0, -22.0), iTime * 0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = rotateY(vec3(0.0, 0.0, 15.0), sin(30.0) * 3.14159 * 0.5);
    vec3 ref = vec3(0, 0, 0);
    vec3 rayDir;

    rayCast(eye, ref, uv, rayDir);

    Intersection isect = sdf3D(rayDir, eye, lightPos);

    // Output to screen
    fragColor = vec4(isect.color, 1.);
}

#define RES 0.2


// perlin noise from: https://www.shadertoy.com/view/4sc3z2
#define MOD3 vec3(.1031,.11369,.13787)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float perlin_noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}


float hash21(vec2 p)
{
 	p = fract( p*vec2(123.34, 456.21) );
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}


float star(vec2 p, float time)
{
	float d = length(p);
    float m = (max(0.2, abs(sin(time))) * 0.02) / d;

    m *= smoothstep(1.0, 0.2, d);

    return m;
}


vec3 starField(vec2 uv)
{
    vec3 col = vec3(0);

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    for (int x=-1; x<=1; x++){
        for (int y=-1; y<=1; y++)
        {
            vec2 offset = vec2(x, y);

            float n = hash21(id + offset);
            float star = star(gv - offset - (vec2(n, fract(n*100.0)) - 0.5), iTime*fract(n*135.246));
            float size = min(1.0, fract(n*1234.567) + 0.1);

            col += star * size;
        }
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy/iResolution.y)/RES;
    vec3 coords = vec3(vec2(uv.x, uv.y + iTime*0.03), iTime*0.3);


    vec3 stars = starField( vec2(coords.x, coords.y)*5.0 );


    float warpAmplitude = 10.0;
   	float warpFrequency = 0.04;

    vec3 clouds = vec3( perlin_noise(coords) ) * 0.3;

    vec3 cloudsCol = 0.5 + 0.5*cos((coords*2.0)+vec3(0,3,9)); //color
    clouds *= cloudsCol;
    stars *= cloudsCol*2.0;
    stars *= clouds+0.5;
    vec3 col = clouds + stars;


    // Output to screen
