float DE(vec3 p) {



    float scale = 4.0;

    float dist=sdTriPrism( p-vec3(0.0), vec2(1.0,1.0) );


	for (int i = 0; i < MENGER_ITERATIONS; i++) {
		dist = max(dist, -sdCrossRepScale(p, scale));
		scale *= 3.0;
	}




	return dist;
}

