#define MAX_MARCHING_STEP 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define EPSILON 0.00001


//ç©éµç¸å³
mat4 rotateY_invert(float theta){
	float c = cos(-theta);
    float s = sin(-theta);
    return mat4(
    	vec4(c, 0, s, 0),
        vec4(0, 1, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 0)
    );
}

mat4 transform_invert(vec3 movement){
	return mat4(
    	vec4(1, 0, 0, 0),
        vec4(0, 1, 0, 0),
        vec4(0, 0, 1, 0),
        vec4(-movement, 0)
    );
}

float sphereSDF(vec3 p){
    return length(p) - 1.0;
}

float torusSDF(vec3 p){
    vec2 t = vec2(1.0, 0.4);
	vec2 q = vec2(length(p.xz)-t.x,p.y);
  	return length(q)-t.y;
}

float planeSDF(vec3 p, vec4 n){
	return dot(p, n.xyz) + n.w;
}
float boxSDF(vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}


float sceneSDF(vec3 p){
    vec3 torusP = (rotateY_invert(1.0) * transform_invert(vec3(0.5, 0, 0)) * vec4(p, 1.0)).xyz;
    return unionSDF(
        differenceSDF(sphereSDF(p), torusSDF(torusP)),
        planeSDF(p, vec4(0.0, 1.0, 0.0, 1.0))
    );
}

float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
	float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEP; i++){
       	float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON){
        	return depth;
        }
        depth += dist;
        if(depth >= end){
        	return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up){
	vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
    	vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
}


vec3 estimateNormal(vec3 p){
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye, vec3 lightPos, vec3 lightIntensity)
{
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));
    float dotLN = clamp(dot(L, N), 0.0, 1.0);
    float dotRV = clamp(dot(R, V), 0.0, 1.0);
	return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));    
}

float shadow(vec3 lightPos, vec3 p)
{
    vec3 dir = normalize(p - lightPos);
    float maxDistance = length(p - lightPos);
    for(float t = 0.0; t < maxDistance;)
    {
        float h = sceneSDF(lightPos + dir * t);
        if(h < EPSILON)
            return 0.0;
        t += h;
    }
    return 1.0;
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye){
	const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;
    vec3 lightPos = vec3(4.0 * sin(iTime),
                         2.0,
                         4.0 * cos(iTime));
    vec3 lightIntensity = vec3(0.4, 0.4, 0.4);
    float shadow = shadow(lightPos, p);
    color += phongContribForLight(k_d, k_s, alpha, p, eye, lightPos, lightIntensity) * shadow;

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0, 5.0, 5.0);
    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);
    if(dist > MAX_DIST - EPSILON){
    	fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        return;
    }

    vec3 K_a = vec3(0.3, 0.3, 0.3);
    vec3 K_d = vec3(0.7, 0.2, 0.2);
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 p = eye + dist * worldDir;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);
    fragColor = vec4(color, 1.0);
}

//The argument function takes in parameters of a+bi
//References:
//
//http://paulbourke.net/fractals/tetration/
//3Blue1Brown: https://youtu.be/elQVZLLiod4?t=2558	


#define pi 3.14159
#define CENTER vec2(0.5,.217708)
//#define CENTER vec2(-1.296537,.0021708)
#define LOOP 23.15

//Thank you mla!
int AA = 16;

//Returns angle of p where p = p.x + p.y*i
float arg(vec2 p)
{
    return atan(p.y,p.x);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = exp(10.*pow(sin(pi/2.*iTime/LOOP),2.));


    //resize window and number of iterations
    int N = 40+int(log(t/2.0)*log(t/2.0));


    //background gradient
    vec3 col;
    vec3 fcol;
    for (int k=0;k<AA;k++)
    {
        for (int j=0;j<AA;j++)
        {
            //temporary variables
            vec2 uv = 3.0 * (2.0 * fragCoord.xy + vec2(k,j)/float(AA)-iResolution.xy)/iResolution.y / t+CENTER;
            vec2 p = uv;
            col = vec3((uv.x+3.)/10.,0.4,0.5);
            float c;
            float m;
            for (int i=0;i<N;i++)
            {
                //complex exponentiation
                c = pow(length(uv),p.x)*exp(-p.y*arg(uv));
                //m = p.y*log(length(uv))+p.x*arg(uv);
                m = p.y*0.5*log(dot(uv,uv))+p.x*arg(uv);
                p.x = cos(m);
                p.y = sin(m);
                p *= c;

                //compute color based on magnitude
                if (length(p) > 500.)
                {
                    float f = float(i)/20.;    
                    fcol += abs(vec3((sin(f+1.57)),sin(f+0.4),sin(1.2*(f+0.5))))*0.85;          
                    break;
                }
            }
        }
    }
    fcol /= float(AA*AA);

    fragColor = vec4(mix(col,fcol,0.4),1.0);
}

// License CC0: Abstract Galaxy
//	Been working on scifi shaders, while this is not "realistic" looking I find it nice to look at
#define PI           3.141592654
#define TAU          (2.0*PI)

#define TIME         (iTime*0.2)
#define RESOLUTION   iResolution

#define LESS(a,b,c) mix(a,b,step(0.,c))

#define SABS(x,k)    LESS((.5/k)*x*x+k*.5,abs(x),abs(x)-k)


vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return p.x*vec2(cos(p.y), sin(p.y));
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

float noise1(vec2 p) {
  float s = 1.0;

  p *= tanh(0.1*length(p));

  float a = cos(p.x);
  float b = cos(p.y);

  float c = cos(p.x*sqrt(3.5));
  float d = cos(p.y*sqrt(1.5));

  return a*b*c*d;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float galaxy(vec2 p, float a, float z) {
  vec2 pp = toPolar(p);
  pp.y += pp.x*3.0 + a;
  p = toRect(pp);

  p *= z;

  return noise1(p);
}

float height(vec2 p) {
  p *= 0.75;
  float s = 0.0;
  float a = 1.0;
  float f = 15.0;
  float d = 0.0;
  for (int i = 0; i < 3; ++i) {
    float g = a*galaxy(p, -0.5*TIME + TIME*(0.2*pow(float(i), 1.0)), f);
    s += g;
    a *= sqrt(0.5);
    f *= sqrt(2.0);
    d += a;
  }

  float l = length(p);

  return SABS((-0.25+ s/d), 0.5)*exp(-5.5*l*l);
}

vec3 normal(vec2 p) {
  vec2 eps = vec2(0.000125, 0.0);

  vec3 n;

  n.x = height(p - eps.xy) - height(p + eps.xy);
  n.y = 2.0*eps.x;
  n.z = height(p - eps.yx) - height(p + eps.yx);

  return normalize(n);
}

vec3 postProcess(vec3 col, vec2 q)  {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 lp1 = vec3(-2.0, 0.5, 2.0);
  vec3 ld1 = normalize(lp1 - vec3(p.x, 0.0, p.y));
  vec3 lp2 = vec3(2.0, 0.5, 2.0);
  vec3 ld2 = normalize(lp2 - vec3(p.x, 0.0, p.y));

  float l = length(p);
  float tl = tanh(l);

  float h = height(p);
  float th = tanh(h);
  vec3 n = normal(p);

  float diff1 = max(dot(ld1, n), 0.0);
  float diff2 = max(dot(ld2, n), 0.0);

  vec3 col = vec3(0.0);
  col += vec3(0.5, 0.5, 0.75)*h;
  col += 0.5*pow(diff1, 10.0);
  col += 0.25*pow(diff2, 2.0);
  col += pow(vec3(0.5)*h, n.y*1.75*(mix(vec3(0.5, 1.0, 1.5), vec3(0.5, 1.0, 1.5).zyx, 1.25*tl)));
  col += 0.9*vec3(1.0, 0.9, 0.75)*exp(-10.0*l*l);

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ((fragCoord-.5*iResolution.xy)/iResolution.y);
	uv *= sin(iTime/5.);
    // Time varying pixel color
    float p = 2.;
    vec3 x = iTime/15.+vec3(sin(iTime)*.002+length((uv)/20.));

    vec3 f = cos(pow(x,vec3(p)))+sin(x*2000.)-cos(x*5215.);
    vec3 f2 = cos(pow(x,vec3(p)))+sin(x*200.)-cos(x*5225.);
    vec3 col = 0.5 + 0.5 * f*.2;
    vec3 col2 = 0.5 + 0.5 * f2*.5;
	col = mod(col, 2.);
    col2 = mod(col2, 2.);
    // Output to screen
    fragColor = vec4(col.r-col2.r*.31, col.g*.4-col2.g, col2.z,1.0);
}

float DistLine(vec3 ro, vec3 rd, vec3 p) {
	return length(cross(p-ro, rd))/length(rd);
}

float DrawPoint(vec3 ro, vec3 rd, vec3 p){
    float d = DistLine(ro, rd, p);
    d = smoothstep(.06, .05, d);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime; 
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3 (3.*sin(t), 2.,-3.*cos(t));  // ro - Ray origin, cam up

    vec3 lookat = vec3(.5);

    float zoom = 1.;

    vec3 f = normalize(lookat-ro);   // f - forward vector
    vec3 r = cross(vec3(0., 1., 0.), f);   // r - right vec
    vec3 u = cross(f,r);   // u- up vector

    vec3 c = ro + f*zoom;  // c- center
    vec3 i = c+ uv.x*r + uv.y*u;   // i - intersection
    vec3 rd = i-ro;




    float d = 0.;

    d += DrawPoint(ro, rd, vec3(0., 0., 0.));
    d += DrawPoint(ro, rd, vec3(0., 0., 1.));
    d += DrawPoint(ro, rd, vec3(0., 1., 0.));
    d += DrawPoint(ro, rd, vec3(0., 1., 1.));
    d += DrawPoint(ro, rd, vec3(1., 0., 0.));
    d += DrawPoint(ro, rd, vec3(1., 0., 1.));
    d += DrawPoint(ro, rd, vec3(1., 1., 0.));
    d += DrawPoint(ro, rd, vec3(1., 1., 1.));



    // Output to screen
    fragColor = vec4(d);
}

