  DMINQ (idUBldgX);
  q.z = - abs (q.z);
  d = max (abs (dot (vec2 (q.y - 1.95 - 0.02 * rs, q.z), vec2 (-0.7, 0.7))) - 0.04,
     PrBox2Df (q.xz, vec2 (0.7)));
  DMINQ (idRoof);
  return dMin;
}

float SpltBldgDf (vec3 p, float zHi, float dMin)
{
  vec3 q;
  vec2 s1, s2, b1, b2;
  float d;
  s1 = vec2 (-9., -6.);
  s2 = vec2 (-13.5, -4.);
  b1 = vec2 (5.5, 8.5);
  b2 = vec2 (3., 6.5);
  q = p;
  d = min (PrBoxDf (q - vec3 (s1, -0.5).xzy, vec3 (b1, 4.2).xzy),
     PrBoxDf (q - vec3 (s2, 2.8).xzy, vec3 (b2, 0.9).xzy));
  d = max (d, - PrFlatCylDf ((q - vec3 (-6.7, -1.5, -13.3)).yxz, 2., 1.7, 2.));
  q.x -= -16.5;
  d = max (d, - min (PrBox2Df (q.xy, vec2 (2., 3.3 - 0.8 * q.x)),
     max (PrBox2Df (q.xy, vec2 (2., 3.4)), 0.2 - abs (mod (q.z + 1., 2.) - 1.))));
  d = max (d, zHi);
  DMINQ (idMBldg);
  q = p - vec3 (-19.5, -5., -4.);
  d = min (d, max (PrBoxDf (q, vec3 (5., 4., 5.)),
     - PrBoxDf (q - vec3 (0., 4.2, 0.), vec3 (4.5, 0.8, 4.5))));
  q.y -= 3.1;
  q.xz = abs (q.xz) - 2.1;
  d = min (d, PrRoundBoxDf (q, vec3 (0.8, 0., 0.8), 1.));
  d = max (d, zHi);
  DMIN (idLBldg);
  q = p;
  d = min (PrCylDf (vec3 (mod (q.xz + 0.5, 1.) - 0.5, q.y - 4.5), 0.14, 0.8),
     max (abs (mod (q.y - 0.55, 0.7) - 0.35) - 0.05, abs (q.y - 4.7) - 0.8));
  d = max (d, abs (min (PrBox2Df (q.xz - s1, b1 - 0.5),
     PrBox2Df (q.xz - s2, b2 - 0.5))) - 0.15);
  d = max (d, - PrBox2Df (q.xz - vec2 (-2., -10.), vec2 (4., 1.4)));
  d = max (d, zHi);
  DMIN (idFenc);
  return dMin;
}

float StairDf (vec3 p, float st, float w, float h)
{
  return 0.7 * max ((st + p.y - p.z - abs (mod (p.y + p.z, 2. * st) - st) / sqrt(2.)),
     max (abs (p.x) - w, max (abs (p.y) - h, abs (p.z) - h)));
}

float FBldgDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p - vec3 (3.4, 0.5, -15.);
  d = max (PrBoxDf (q, vec3 (3.4, 3.2, 4.)),
     - PrBoxDf (q - vec3 (0., 4.7, 0.), vec3 (2.9, 2., 3.5)));
  d = min (d, PrRoundBoxDf (q - vec3 (0., 2.4, 0.), vec3 (0.9, 0., 1.5), 1.));
  d = max (d, - PrBoxDf (q - vec3 (-3.4, 1.3, -1.6), vec3 (0.5, 0.7, 0.5)));
  d = max (d, - PrFlatCylDf ((q - vec3 (-2., -0.8, 2.2)).yzx, 1.7, 1., 2.));
  DMIN (idRBldg);
  q = p - vec3 (3., 0.7, -22.);
  d = max (PrBoxDf (q, vec3 (3.5, 6.2, 3.5)),
     - PrBoxDf (q - vec3 (0., 7.2, 0.), vec3 (3, 1.5, 3)));
  d = max (d, - PrBoxDf (q - vec3 (-3.4, 3.8, 0.5), vec3 (0.5, 1., 1.)));
  DMIN (idRBldg);
  q = p - vec3 (-10., -5., -26.);
  d = max (PrBoxDf (q, vec3 (20., 4., 15.)),
     - min (PrBoxDf (q - vec3 (-4., 5.5, 10.5), vec3 (10., 2.5, 4.)),
     PrBoxDf (q - vec3 (1.5, 6., -3.), vec3 (7., 5., 9.))));
  DMIN (idFBldg);
  q = p - vec3 (-2.5, -2.6, -22.2);
  d = min (StairDf (q, 0.35, 0.8, 2.), StairDf (q - vec3 (-2., 1.3, 7.), 0.35, 0.4, 2.));
  DMIN (idStair);
  return dMin;
}

float IcoDodDf (vec3 p, float typ)
{
  vec3 q, dv, vc;
  float cp, sp, scl;
  scl = 1.2;
  p /= scl;
  cp = cos (pi / 5.);
  sp = sqrt (0.75 - cp * cp);
  vc = vec3 (-0.5, - cp, sp);
  for (int j = 0; j < 5; j ++) {
    p.xy = abs (p.xy);
    p -= 2. * min (0., dot (p, vc)) * vc;
  }
  q = p - ((typ < 0.) ? normalize (vec3 (sp, 0., 0.5)) : normalize (vec3 (0., sp, cp)));
  dv = vec3 (length (q - min (0., q.x) * vec3 (1., 0., 0.)), 
     length (q - min (0., q.y) * vec3 (0., 1., 0.)),
     length (q - min (0., dot (q, vc)) * vc));
  return scl * (Minv3 (dv) - 0.15);
}

float WatChanDf (vec3 p, float dMin)
{
  vec3 q, qq;
  vec2 b;
  float d, dw, s;
  b = vec2 (-5.);
  q = p - vec3 (0., 3., -10.);
  qq = q;
  for (float j = 0.; j <= 3.; j ++) {
    if (j > 0.) qq.xz = b + qq.zx ;
    q = vec3 (abs (vec2 (abs (qq.x) - 5., qq.z)) - 1., qq.y - 1.).xzy;
    d = max (PrBoxDf (q, vec3 (0.2, 5., 0.2)), dot (qq, vuPln) - 1.);
    DMINQ (idPilr);
  }
  dw = min (PrBoxDf (p - vec3 (-7.5, 4., -9.9), vec3 (2.9, 0.3, 1.1)),
     PrBoxDf (qq - vec3 (5., 0.23, -2.), vec3 (0.82, 0.3, 1.9)));
  s = - qq.x;
  q = vec3 (abs (vec2 (abs (qq.x) - 5., qq.z)) - 1., qq.y).xzy;
  d = PrBoxDf (q, vec3 (0.2, 5., 0.2));
  DMINQ (idPilr);
  qq.x = - qq.x;
  qq.xz = vec2 (15., 0.) - qq.xz;
  qq.xz = mix (qq.xz, qq.zx, step (qq.x, qq.z));
  qq.xy -= vec2 (10., 1.);
  qq.xz = mix (qq.xz, qq.zx, step (qq.x, qq.z));
  qq.x = - qq.x;
  dw = min (dw, PrBoxDf (qq, vec3 (10.8, max (0., 0.95 - 0.03 * s), 0.65)));
  q = p - vec3 (0., 3., -10.);
  d = max (PrBoxDf (q - vec3 (-7.4, 0.95, 0.1), vec3 (3.5, 0.25, 1.7)), - dw);
  DMINQ (idWChan);
  for (float j = 0.; j <= 3.; j ++) {
    if (j > 0.) q.xz = b + q.zx;
    s = 0.1 * step (1., q.x);
    d = max (PrBoxDf (q - vec3 (0., - s, 0.), vec3 (6., 1.2 - 0.2 * j - s, 1.)), - dw);
    DMINQ (idWChan);
  }
  d = max (PrBoxDf (q - vec3 (5., -0.15, -2.2), vec3 (1., 0.4, 1.2)), - dw);
  DMINQ (idWChan);
  s = sign (q.x);
  q.x = abs (q.x) - 5.;
  d = isPoly ? IcoDodDf (q - vec3 (0., 6.4, 0.), s) : PrSphDf (q - vec3 (0., 6.2, 0.), 1.);
  DMIN (idTop);
  d = PrBoxDf (q - vec3 (0., 5.1, 0.), vec3 (1.5, 0.1, 1.5));
  DMIN (idPlat);
  d = 6. - abs (q.y);
  q.y = mod (q.y - 2.1, 5.6) - 1.7;
  d = max (max (PrBoxDf (q - vec3 (0., 0.5, 0.), vec3 (1.15, 0.7, 1.15)),
     0.85 - min (length (q.xy), length (q.zy))), - d);
  q = p - vec3 (5., 1., 0.);
  d = min (d, max (PrBoxDf (q - vec3 (0., 0.5, 0.), vec3 (1.15, 0.7, 1.15)),
     0.85 - min (length (q.xy), length (q.zy))));
  DMIN (idArch);
  return dMin;
}

float WWheelDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p - vec3 (-7.8, 5.2, -6.2);
  d = PrCylDf (q, 1., 0.2);
  DMIN (idUBldgX);
  q.z -= -2.3;
  d = PrCylDf (q, 0.5, 2.2);
  q.z -= -1.5;
  d = min (d, PrCylAnDf (q, 1.1, 0.15, 0.7));
  DMIN (idWWhl);
  q.xy = Rot2D (q.xy, -0.8 * tCur * flDir);
  q.xy = Rot2D (q.xy, 2. * pi * floor (14. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 14.);
  q.x -= -1.3;
  d = PrBoxDf (q, vec3 (0.7, 0.07, 0.6));
  DMIN (idWWhl);
  return dMin;
}

float WatSurfDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 b;
  float d;
  b = vec2 (-5.);
  q = p - vec3 (0., 3., -10.);
  d = PrBoxDf (q - vec3 (-7.5 , 0.1, 0.1), vec3 (3., 0.8, 1.1));
  DMINQ (idWater);
  for (int j = 0; j <= 3; j ++) {
    if (j > 0) q.xz = b + q.zx;
    d = PrBoxDf (q, vec3 (6., 0.8 - 0.2 * float (j) - 0.02 * q.x, 0.75));
    DMINQ (idWater);
  }
  q.xz = - q.zx;
  q -= vec3 (2.1, -0.05, -5.);
  d = max (PrBoxDf (q, vec3 (1.3, 0.15, 0.75)), q.x + 4. * q.y - 1.5);
  DMINQ (idWater);
  return dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  dMin = UBldgDf (p - vuDir * 35., dMin);
  dMin = SpltBldgDf (p - vuDir * 35., p.z - 99., dMin);
  dMin = SpltBldgDf (p, p.z + 8.7, dMin);  
  dMin = FBldgDf (p, dMin);
  dMin = WWheelDf (p, dMin);
  dMin = WatChanDf (p, dMin);
  dMin = WatSurfDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.002, -0.002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  q = p - vec3 (-8., 7.6, -9.8);
  return max (PrRoundFlatCylAnDf (q.yxz, 5.6, 1.5, 0.1, 0.5, 0.1),  q.x - 0.1);
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.002, -0.002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

float BCylHit (vec3 ro, vec3 rd, float rad)
{
  float a, b;
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  return (- b + sqrt (b * b - a * (dot (ro.xz, ro.xz) - rad * rad))) / a;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colW, roo, vn, q;
  vec2 vf, c;
  float dstObj, dstFWat, sh, a, s, spec;
  dstObj = ObjRay (ro, rd);
  roo = ro;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    spec = 0.1;
    if (idObj <= idRBldg) {
      if (idObj == idWChan) col = mix (vec3 (0.9, 0.4, 0.2), vec3 (0.7, 0.3, 0.4),
         step (qHit.y, -0.4)) * (1. - 0.3 * step (0.95, sin (8. * pi * qHit.y)));
      else if (idObj == idWWhl) col = vec3 (0.8, 0.5, 0.1);
      else if (idObj == idMBldg) col = (qHit.y > 3.69) ? vec3 (0.7, 0.7, 0.8) : 
         ((qHit.y < 3.35 && qHit.x < 1.9) ? vec3 (0.8, 0.7, 0.5) : vec3 (0.6, 1., 1.));
      else /*if (idObj == idRBldg)*/ col = vec3 (0.7, 0.5, 0.3);
    } else if (idObj <= idFenc) {
      if (idObj == idFBldg) col = vec3 (0.75, 0.45, 0.3);
      else if (idObj == idLBldg) col = vec3 (0.7, 0.5, 0.35);
      else if (idObj == idStair) col = (vn.x > -0.01) ? vec3 (0.8, 0.8, 0.5) :
         vec3 (0.75, 0.45, 0.3);
      else /*if (idObj == idFenc)*/ col = vec3 (1., 0.9, 0.4);
    } else if (idObj <= idPilr) {
      if (idObj == idUBldg) col = (max (abs (qHit.x) - 4.7, abs (qHit.z) - 2.7) < 0.) ?
         vec3 (0.2, 0.5, 0.3) : vec3 (0.9, 0.8, 0.5);
      else if (idObj == idUBldgX) col = vec3 (0.9, 0.8, 0.5);
      else if (idObj == idRoof) col = vec3 (0.8, 0.1, 0.2) * (1. - 0.2 * step (0.5,
         sin (16. * pi * qHit.y)));
      else /*if (idObj == idPilr)*/ col = vec3 (0.9, 0.8, 0.7) * (1. -
         0.1 * step (0.7, sin (4. * pi * qHit.y)));
    } else /*if (idObj <= idWater)*/ {
      if (idObj == idPlat) col = vec3 (0.7, 0.6, 0.5);
      else if (idObj == idTop) col = vec3 (0.5, 0.6, 1.);
      else if (idObj == idArch) {
        c = (1. - 0.2 * step (0.8, cos (4. * pi * qHit.xz)));
        col = vec3 (0.9, 0.8, 0.7) * c.x * c.y;
      } else /*if (idObj == idWater)*/ {
        q = qHit;
        s = 1. - smoothstep (0., 2., abs (q.x) - 3.);
        q.x -= tCur * flDir;
        col = vec3 (0.7, 0.8, 1.) * (0.96 - 0.04 * s * sin (2. * pi * q.x));
      }
    }
    if (idObj != idWWhl && idObj != idFenc) {
      if (idObj == idWater) {
        vf = vec2 (0.);
        if (vn.y > 0.5) {
          vf = vec2 (4., 2.);
          spec = 0.2;
        }
      } else {
        vf = vec2 (16., 0.5);
        q = ro;
      }
      if (vf.x > 0.) vn = VaryNf (vf.x * q, vn, vf.y);
    }
    sh = (idObj != idPilr && idObj != idWater) ? ObjSShadow (ro, sunDir) : 1.;
    col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       spec * step (0.95, sh) * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
  } else {
    ro += BCylHit (ro + vec3 (-20., 0., 10.), rd, 100.) * rd;
    a = (atan (ro.z, - ro.x) + 0.3 * floor (0.5 * ro.y)) / (2. * pi);
    col = (1. + 0.2 * ro.y / 100.) * mix (vec3 (0.3, 0.5, 0.2) * (1. -
       0.1 * smoothstep (0.25, 0.3, mod (360. * a + 0.5, 1.) - 0.5)), vec3 (0.4, 0.6, 0.3) *
       (1. - 0.1 * Noisefv2 (256. * vec2 (4. * a, ro.y))), step (0.65, mod (0.5 * ro.y, 1.)));
  }
  ro = roo;
  dstFWat = TrObjRay (ro, rd);
  if (dstFWat < min (dstObj, dstFar)) {
    ro += dstFWat * rd;
    vn = TrObjNf (ro);
    q = ro;
    q.y += 2. * tCur * flDir;
    vn = VaryNf (8. * q, vn, 4.);
    colW = mix (vec3 (0.8, 0.9, 1.), 0.9 * vec3 (0.7, 0.8, 1.),
       smoothstep (0., 1., ro.y - 13.5)) * (0.6 + 0.4 * max (dot (vn, sunDir), 0.)) +
       0.2 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col = mix (col, colW, sqrt (Maxv3 (colW)) * (1. - 0.3 * step (0., sin (16. * pi * ro.z)) *
       (1. - 0.4 * step (0., sin (2. * pi * q.y)))));
  }
  if (isMono) col = vec3 (1., 0.7, 0.3) * Maxv3 (col);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, vx, vy;
  vec2 canvas, uv, ut, uvv;
  float el, az, zmFac, asp, sr;
  bool inCorn, flipVu;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  flDir = 1.;
  isMono = false;
  flipVu = false;
  isPoly = false;
  ut = vec2 (abs (mPtr.x), abs (mPtr.y)) + 0.05 * vec2 (1. / asp, 1.) - 0.5;
  inCorn = (mPtr.z > 0. && min (ut.x, ut.y) > 0.);
  if (inCorn) {
    if (mPtr.x > 0. && mPtr.y > 0.) isPoly = true;
    if (mPtr.x > 0. && mPtr.y < 0.) flDir = -1.;
    if (mPtr.x < 0. && mPtr.y > 0.) flipVu = true;
    if (mPtr.x < 0. && mPtr.y < 0.) isMono = true;
  }
  az = 0.2501 * pi;
  el = -0.143 * pi;
  if (mPtr.z > 0. && ! inCorn) {
    az += 2. * pi * smoothstep (0.05, 0.2, abs (mPtr.x)) * mPtr.x;
    el += 0.5 * pi * smoothstep (0.05, 0.2, abs (mPtr.y)) * mPtr.y;
  }
  ro = vec3 (0., 0., -70.);
  ro.yz = Rot2D (ro.yz, - el);
  ro.xz = Rot2D (ro.xz, - az);
  rd = normalize (vec3 (0., 4., 0.) - ro);
  vuDir = rd;
  vx = normalize (vec3 (vuDir.z, 0., - vuDir.x));
  if (flipVu) vx *= -1.;
  vy = vec3 (0., 1., 0.) - vuDir.y * vuDir;
  vuPln = vy;
  zmFac = 0.065;
  dstFar = 200.;
  sunDir = normalize (vec3 (-2., 3., -1.));
  if (max (abs (uv.x), abs (uv.y)) < 0.97) {
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
      col += (1. / naa) * ShowScene (ro + vec3 (uvv.x * vx + uvv.y * vy) / zmFac, rd);
    }
  } else if (abs (uv.x) < 1.) {
    col = vec3 (0.2, 0.4, 0.6);
  } else {
    col = vec3 (0.75);
  }
  if (mPtr.z > 0. && min (abs (uv.x) - asp, abs (uv.y) - 1.) > -0.1)
     col = mix (col, vec3 (1., 1., 0.), 0.3);
  fragColor = vec4 (col, 1);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  return max (length (p.xy - vec2 (clamp (p.x, - rhi, rhi), 0.)) - rlo, abs (p.z) - h);
}

float PrRoundFlatCylAnDf (vec3 p, float rhi, float rlo, float w, float h, float rc)
{
  vec2 q;
  q = abs (vec2 (length (p.xy - vec2 (clamp (p.x, - rhi, rhi), 0.)) - rlo, p.z)) - vec2 (w, h);
  return length (vec2 (max (q, 0.))) - rc;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.14159265359

vec2 toSmith(vec2 p)  {
  // z = (p + 1)/(-p + 1)
  // (x,y) = ((1+x)*(1-x)-y*y,2y)/((1-x)*(1-x) + y*y)
  float d = (1.0 - p.x)*(1.0 - p.x) + p.y*p.y;
  float x = (1.0 + p.x)*(1.0 - p.x) - p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

vec2 fromSmith(vec2 p)  {
  // z = (p - 1)/(p + 1)
  // (x,y) = ((x+1)*(x-1)+y*y,2y)/((x+1)*(x+1) + y*y)
  float d = (p.x + 1.0)*(p.x + 1.0) + p.y*p.y;
  float x = (p.x + 1.0)*(p.x - 1.0) + p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

vec2 rotate(vec2 v, float a) {
    float s = sin(a);
    float c = cos(a);
    mat2 m = mat2(c, -s, s, c);
    return m * v;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdEquilateralTriangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float sdHorseshoe( in vec2 p, in vec2 c, in float r, in vec2 w )
{
    p.x = abs(p.x);
    float l = length(p);
    p = mat2(-c.x, c.y, 
              c.y, c.x)*p;
    p = vec2((p.y>0.0)?p.x:l*sign(-c.x),
             (p.x>0.0)?p.y:l );
    p = vec2(p.x,abs(p.y-r))-w;
    return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float circle(in vec2 p, in float r ) {
    return length(p) - r;
}

float map(in vec2 p){    
    float d1 = sdEquilateralTriangle(p*0.5);
    float d2 = sdStar5(p*0.1, 1.0, 0.4);
    float d3 = sdStar5(p*0.2, 1.0, 0.4);
    //float d4 = sdHorseshoe(p-vec2(2.0,-0.1), vec2(0.4, -1.5), 1.5, vec2(0.750,0.25));
    float d4 = circle(p, 8.);
    return min(min(d1, max(d2,-d3)), -d4);
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 modMirror2(inout vec2 p, vec2 size) {
  vec2 halfsize = size*0.5;
  vec2 c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  p *= mod(c,vec2(2.0))*2.0 - vec2(1.0);
  return c;
}

vec3 postProcess(in vec3 col, in vec2 q) 
{
  col=pow(clamp(col,0.0,1.0),vec3(0.45)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=vec3(0.15+0.85*pow(29.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7));  // vigneting
  return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord/iResolution.xy;
    vec2 uv = -1. + 2. * q;
    uv.x *= iResolution.x/iResolution.y;

    float sc = 25.0;
    vec2 p = uv*sc;
    p = toSmith(p);
    p = p + 0.7;
    p = rotate(p, iTime*0.05*PI);
    p = fromSmith(p);
    p = toPolar(p);

    modMirror2(p, vec2(150, 2.*PI/16.));
    p = toRect(p);
    modMirror2(p, vec2(15, 15));
    p = rotate(p, iTime*0.05*PI);
    float d = map(p + vec2(0.4, 0)) / sc;

    float r = smoothstep(0.0, 0.001, d);
    float g = smoothstep(0.0, 0.01, d);
    float b = smoothstep(0.0, 1.0, d);

    vec3 color = vec3(r, g ,b);
    color += mix(vec3(1.0,0.0,0.0),vec3(0.0,1.0,0.0),0.5+0.5*sin(d*200.))*(0.5+0.5*sin(d*2000.));
    //color = 1.0 - color;
    color = postProcess(color, q);
    fragColor = vec4(color, 1.0);
}

#define n 10.

#define rand(p) fract(sin(sin(p) * mat2(12.9898, 78.233, 78.233, 12.9898)) * 143758.5453)
#define Circle(w, p, r) smoothstep( w, 0., abs(length(p) - r ) )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord - 0.5 * R) / R.y;
    float t = iTime;
    vec3 col = vec3(0.);
    vec2 p = uv;
    vec2 p1 = vec2(sin(t), cos(t));
    vec2 PtoP0P1 = p - dot(p1, p) * p1;
    float d = length(PtoP0P1);
    if (dot(p, p1) < 0.) {
        d = length(p);
    }
    vec2 cl = vec2(0.5, 0.);
    col = .006 / d * cl.yxy;
    float r = length(p1);
    float i = round(n * length(p) / r);
    float w = 2. * r / R.y;
    float circle = Circle(w, p, r * i / n);

    col += circle * cl.yxy;

    vec2 delay = vec2(1., 20.) * (1. + p1); 
    if (delay.x > 0.99) {
        float echoR = mix(r, 0., delay.y);
        float circle = Circle(w, p, echoR);
        col += circle * cl.yxy;
    } else {
        vec2 enemy = vec2(-0.2 + 0.5 * cos(0.01 * floor(t)));
        col += .005 / length(p - enemy) * cl.xyy;
    }

    float dTargets = length(rand(p - 1e-6 * log(t + 100.)));    
    col += .02 / dTargets * cl.yxy;

    fragColor = vec4(col, 0.0);
}

float sdf(vec3 p){
    return length(p) - 3.0;
}


vec4 sampleDensity(vec3 p){
    float density = clamp(-sdf(p) * 1000.0, 0.0, 1.0);
    vec3 color = clamp(abs(p * 0.3), 0.0, 1.0);
    return vec4(color, density);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);
    vec3 cameraPos = vec3(cos(iTime), sin(iTime), sin(iTime)) * 5.0;
    vec3 rayPos = cameraPos;
    vec3 up = vec3(0,1,0);
    vec3 front = normalize(-cameraPos);
    vec3 left = normalize(cross(up, front));
    up = normalize(cross(front, left));

    vec3 rayDir = normalize(up * uv.y + left * uv.x + front);

    float deltaStep = 0.05;
    vec3 accCol = vec3(0.0);
    float accDensity = 0.0;

    for(int i = 0; i < 128; i++){
        vec4 raw = sampleDensity(rayPos);
        float density = raw.w;
        vec3 volumeCol = raw.xyz;

        accCol += exp(-accDensity) * (1.0 - exp(-density * deltaStep)) * volumeCol;
        accDensity += density * deltaStep;
        rayPos = cameraPos + rayDir * float(i) * deltaStep;
    }

    // Output to screen
    fragColor = vec4(accCol, 1.0);
}

