#define PI 3.141592
#define TAU 6.2831853071
#define time iTime
#define dt mod(iTime,TAU)
#define trir vec2(1.,0.7)

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float smin (float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float caps (vec3 p, vec3 a, vec3 b, float r)
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float fradius;
float ocarina (vec3 p)
{
    vec3 pp = p;
    float bradius = sin((-p.x+1.)*1.4)*0.3+0.08;
    float body = caps(p,vec3(-1.,0.,0.),vec3(1.,0.,0.), bradius);

    p.xy += vec2(0.45,-.6);
    fradius = sin((-p.y+0.4)*2.)*0.16; 
    float flute = caps(p,vec3(0.,-0.2,0.),vec3(0.,0.25,0.), fradius);

    p = pp;
    float c1 = cyl(p+vec3(0.64,-0.15,0.3), 0.08, .3);
    float c2 = cyl(p+vec3(0.8,0., 0.3), 0.08, .3);
    float c3 = cyl(p+vec3(0.64,0.15, 0.3), 0.08, .3);
    float c4 = cyl(p+vec3(0.45,0., 0.3), 0.08, .3);
    float c5 = cyl(p+vec3(-0.1,-0.25, 0.3), 0.08, .3);
    float c6 = cyl(p+vec3(-0.3,-0.1, 0.3), 0.08, .3);
    float c7 = cyl(p+vec3(-0.5,-0.18, 0.3), 0.08, .3);
    float cs = min(c7,min(min(c6,c5),min(min(c1,c3),min(c2,c4))));

    return max(-cs,abs(smin(body,flute,0.1))-0.005);
}

float ring (vec3 p)
{
    p.xy += vec2(0.44,-.48);
    p.xy *= rot(0.1);
    return cyl(p.xzy,0.04+fradius,0.1);
}

int mat_id;
float SDF (vec3 p)
{
    p.yz *= rot(sin(dt)*0.15);
    p.xz *= rot(cos(dt)*0.1);
    p.y -= 0.45+sin(dt)*0.1;
    p.xy *= rot(PI/5.);
    float ri = ring(p);
    float oc = ocarina(p);
    float d = min(ri,oc);
    if (d == ri) mat_id = 1;
    if (d == oc) mat_id = 2;
    return d;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float spec (vec3 rd, vec3 n, vec3 l)
{
    vec3 h = normalize(l-rd);
    return pow(max(dot(n,h), 0.), 20.);
}

// courtesy of Alkama
float pales (vec2 uv, float speed, float number)
{
    uv *= rot(-time*speed);
    return floor(smoothstep(0.1, 0.2,cos(atan(uv.y, uv.x)*number)));
}

vec3 ynotes (vec2 uv, float size, int note)
{
    uv *= size;
    float r = 0.2;
    float s = 0.08;
    float triUp = smoothstep(s,s*1.1,max(-uv.y, dot(vec2(abs(uv.x),uv.y), normalize(trir))));
    float triRight = smoothstep(s,s*1.1,max(-uv.x, dot(vec2(uv.x,abs(uv.y)), normalize(trir.yx))));
    float triLeft = smoothstep(s,s*1.1,max(uv.x, dot(vec2(-uv.x,abs(uv.y)), normalize(trir.yx))));
    float direction;
    if (note == 1) direction = triUp;
    if (note == 2) direction = triRight;
    if (note == 3) direction = triLeft;
    float mask = (1.-smoothstep(r,r*1.1, length(uv)))*direction;

    return vec3(0.9,0.8,0.)*mask;
}

vec3 bnotes (vec2 uv, float size)
{
    uv *= size;
    float mask = 1.-smoothstep(0.2,0.2*1.1, length(uv));
    return vec3(0.1,0.4,0.95) * mask;
}

vec3 background (vec2 uv)
{
    vec2 uu = uv;
    vec3 back = mix(vec3(0.3,0.25, 0.1),
                    vec3(0.9,0.9,0.45),
                    pales(uv+vec2(0.,-0.2), 0.3, 5.)*0.5+pales(uv+vec2(0.,-0.2),-0.5, 10.)*0.7
                   );
    uv.y += 0.25;
    back *= smoothstep(0.2,0.21,max(abs(uv.x*.45),abs(uv.y*1.4)));
    uv.x -= 0.15;
    back += bnotes(uv+vec2(0.5,0.07), 5.);
    back += ynotes(uv+vec2(0.4,-0.07), 5., 1);
    back += ynotes(uv+vec2(0.25,-0.02), 5., 3);
    back += ynotes(uv+vec2(0.1,0.03), 5., 2);
    back += ynotes(uv+vec2(-0.05,-0.02), 5., 3);
    back += ynotes(uv+vec2(-0.2,0.03), 5., 2);

    return clamp(back,0.,1.);
}


void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
