    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.0,-0.3,-3.),
        rd = normalize(vec3(uv,1.)),
        p = ro,
        l = normalize(vec3(1.,2., -2.)),
        specl = normalize(vec3(-1.5,5.,-5.)),
        col = background(uv);

    float shad,d=0.; 
    bool hit = false;

    for (float i=0.; i<64.; i++)
    {
        d = SDF(p);
        if (d<0.01)
        {
            hit = true;
            shad = i/64.;
            break;
        }
        p += d*rd*0.6;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        float lighting = floor(max(dot(n,l),0.)*3.)/3.;
        if (mat_id == 2) 
        {
            col = mix(vec3(0.,0.05,0.2),vec3(0.5,0.8,1.),lighting);
        }
        if (mat_id == 1)
        {
            col = vec3(lighting+0.15);
        }
        col += smoothstep(0.4,0.5,spec(rd, n, specl));
    }
    fragColor = vec4(sqrt(clamp(col,0.,1.)), 1.);
}

// https://www.shadertoy.com/view/4dfGDj
// FXAA by Dave Hoskins, FXAA algorithm from NVIDIA :)
// http://developer.download.nvidia.com/assets/gamedev/files/sdk/11/FXAA_WhitePaper.pdf

#define FXAA_SPAN_MAX	8.0
#define FXAA_REDUCE_MUL 1.0/8.0
#define FXAA_REDUCE_MIN 1.0/128.0

//========================================================================================
float Hash( float n )
{
    return fract(sin(n)*43758.5453123);
}

//========================================================================================
float Noise( in vec2 x )
{

    vec2 p = floor(x);
    vec2 f = fract(x);

   	f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0;
    float res = mix(mix( Hash(n+  0.0), Hash(n+  1.0),f.x),
                    mix( Hash(n+ 57.0), Hash(n+ 58.0),f.x),f.y);
    return res;
}

//========================================================================================
vec3 hsv(in float h) 
{
	if (h > .45 && h < .55) return vec3(1.0);
	vec3 rgb = clamp((abs(fract(h + vec3(3, 2, 1) / 3.0) * 6.0 - 3.0) - 1.0), 0.0 , 1.0);
	return sqrt(rgb);
}

//========================================================================================
vec3 old(vec2 p)
{
	float t= sin(iTime*.03)*1.5;
	mat2 m = mat2(cos(t), sin(t), -sin(t), cos(t));
	p*= m;
	vec2 b = vec2(.6, .6);
	vec2 d = abs(p) - b;
  	float f = min(max(d.x, d.y),0.0) + length(max(d,0.0));
	float pix = Noise(p*20.0)*.29;
	if (f < pix && f > -pix) return hsv(Noise(p*33.0));
	else return vec3(0.0, 0.0, .0);
}

vec3 Box(vec2 uv)
{
float time1 = iTime/2.0;
    float scale = 5.0;
    vec2 col = (uv.yx*scale*(1.0+sin(uv.x+time1/10.)/2.0));
    vec2 col2;
    for(float i = 1.0; i < 4.0; i++){
        uv += ceil(col+tan((col.x+col.y))/5.0);
        col -= sin(uv.x+time1/10.);
        col2 = (col2+col)/(i*i);
    }

    // Output to screen
    return vec3(col.y,col2.x,0.5);

}



