void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

	if (abs(uv.x) < 3.0/iResolution.x) 
	{
		// Centre line...
		fragColor = vec4(0.2, 0.0, 0.2, 1.0);
		return;
	}
	if (uv.x < 0.0) 
	{
		// No FXAA on left...
		uv.x = -uv.x;
		fragColor = vec4(Box(uv), 1.0);
		return;
	}
	vec2 add = vec2(1.0) / iResolution.xy;

	vec3 rgbNW = Box(uv+vec2(-add.x, -add.y));
	vec3 rgbNE = Box(uv+vec2( add.x, -add.y));
	vec3 rgbSW = Box(uv+vec2(-add.x,  add.y));
	vec3 rgbSE = Box(uv+vec2( add.x,  add.y));
	vec3 rgbM  = Box(uv);

	vec3 luma	 = vec3(0.299, 0.587, 0.114);
	float lumaNW = dot(rgbNW, luma);
	float lumaNE = dot(rgbNE, luma);
	float lumaSW = dot(rgbSW, luma);
	float lumaSE = dot(rgbSE, luma);
	float lumaM  = dot(rgbM,  luma);

	float lumaMin = min(lumaM, min(min(lumaNW, lumaNE), min(lumaSW, lumaSE)));
	float lumaMax = max(lumaM, max(max(lumaNW, lumaNE), max(lumaSW, lumaSE)));

	vec2 dir;
	dir.x = -((lumaNW + lumaNE) - (lumaSW + lumaSE));
	dir.y =  ((lumaNW + lumaSW) - (lumaNE + lumaSE));


	float dirReduce = max(
		(lumaNW + lumaNE + lumaSW + lumaSE) * (0.25 * FXAA_REDUCE_MUL), FXAA_REDUCE_MIN);

	float rcpDirMin = 1.0/(min(abs(dir.x), abs(dir.y)) + dirReduce);


	dir = min(vec2( FXAA_SPAN_MAX,  FXAA_SPAN_MAX),
		  max(vec2(-FXAA_SPAN_MAX, -FXAA_SPAN_MAX),
		  dir * rcpDirMin)) * add;


	vec3 rgbA = (1.0/2.0) * (Box(uv + dir * (1.0/3.0 - 0.5)) +
							 Box(uv + dir * (2.0/2.0 - 0.5)));

	vec3 rgbB = rgbA * (1.0/2.0) + (1.0/4.0) *
		(Box(uv.xy + dir * (0.0/3.0 - 0.5)) +
		 Box(uv.xy + dir * (3.0/3.0 - 0.5)));

	float lumaB = dot(rgbB, luma);
	if((lumaB < lumaMin) || (lumaB > lumaMax))
	{
		fragColor.xyz=rgbA;
	}else
	{
		fragColor.xyz=rgbB;
	}
}

vec2 gradient(vec2 cell)
{
	vec3 p3  = fract(vec3(cell.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    float theta = 6.2831853072 * fract((p3.x + p3.y) * p3.z);
    return vec2(cos(theta), sin(theta));
}

vec3 perlin2(vec2 r) //Returns perlin value + gradient
{
    //Useful vector
    vec2 zo = vec2(0.0, 1.0);

    //Coordinates & vertices
    vec2 v00 = floor(r);
    vec2 v01 = v00 + zo.xy;
    vec2 v10 = v00 + zo.yx;
    vec2 v11 = v00 + zo.yy;
    vec2 xy = r - v00; //Local coordinate

    //Get gradients
    vec2 g00 = gradient(v00);
    vec2 g01 = gradient(v01);
    vec2 g10 = gradient(v10);
    vec2 g11 = gradient(v11);

    //Get influence values
    float G00 = dot(xy, g00);
    float G01 = dot(r - v01, g01);
    float G10 = dot(r - v10, g10);
    float G11 = dot(r - v11, g11);
    vec4 G = vec4(G00, G01, G10, G11);

    //Calculate noise value
    vec2 W = xy*xy*xy*(10.0+xy*(6.0*xy-15.0)); //Weighted local coordinates
    vec2 C = G.xy + W.x*(G.zw - G.xy);
    float P = C.x + W.y*(C.y - C.x);
    P = P * 0.7071067812 + 0.5;

    //First derivatives (vectors)
    vec2 delW = 30.0*xy*xy*(xy*(xy-2.0)+1.0);
    vec2 delG0 = g00 + W.x*(g10-g00) + vec2(delW.x*(G10-G00), 0.0);
    vec2 delG1 = g01 + W.x*(g11-g01) + vec2(delW.x*(G11-G01), 0.0);    
    vec2 delP = delG0 + W.y*(delG1-delG0) + vec2(0.0, delW.y*(C.y-C.x));

    return vec3(P, delP);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; //Normalized pixel
    vec3 noisedata = vec3(0.0);
    float sTot = 0.0;
    vec3 rs = vec3(5.0 * uv * vec2(1.0, iResolution.y/iResolution.x), 1.0);
    for (int i = 0; i < 4; i++) {
        rs.xy *= 1.5;
        rs.z *= 0.7;
        rs.xy += vec2(1.5, 1.8);
        sTot += rs.z;
        noisedata += perlin2(rs.xy) * rs.z;
    }
    noisedata /= sTot;
    noisedata.x *= 1.2; //Since statistically the values become increasingly uniform
    vec2 delP = noisedata.yz;
    float P = noisedata.x;

    //Lighting
    vec3 normal = normalize(vec3(-delP, 1.0)); //Normal 
    vec3 col = vec3(0.8,0.8,0.6); //Colour
    vec3 light = normalize(vec3(cos(iTime),sin(iTime),0.5)); //Direction to light
    col *= 0.5*dot(light, normal)+0.5;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

