float dfSeg( vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float blackbody(float wl, float T){
    float h = 6.62607004e-34; // Planck constant
    float k = 1.38064852e-23; // Boltzmann constant
    float c = 299792458.;// Speed of light
   	wl*=1e-9;
    return  2.*h*(c*c)/(wl*wl*wl*wl*wl*(exp(h*c/(T*wl*k))-1.));
}

vec3 bbXYZ(float T){
    int samples = 25;
    float mi = 300.,ma = 800.;
    float st = (ma-mi)/float(samples);
    vec3 ret = vec3(0);
    vec3 tmp = vec3(0);
    for(float l = mi ; l < ma ; l += st){
    	vec3 XYZ = lambdatoXYZ(l);
        float bb = blackbody(l,T);
        XYZ*=bb;
        ret+=st*(XYZ+tmp)/2.;
        tmp = XYZ;
    }
    return ret;
}

