#define PI 3.14159

#define FUNK_SPEED 2.0
#define SCROLL_SPEED 3.0

//Transform a value that to something between [-1,1].
//A wrapping function gone horribly wrong.
float weirdWrapFunk(float val){

    //My mini algo only works for positive numbers.
    //Multiply by multiples of range (2) to make number
    //positive without affecting result.
    if(val < 0.0){
    	float factor = abs(floor(val));
        val = val + (2.0 * factor);
    }

    //f(x) = x - floor((x-minx)/(max-min)) - 1
    float res = (val) - floor((val+1.0)/2.0) - 1.0;

    return res;
}

//Check if in sine wave.
//Expects uv to be in [-1,1]
bool checkInSine(vec2 uv){
    //Sine wave = Asine(wt + 0)

    //get x in radians
    float x = (uv.x + 1.0) * (PI);

    float expected = sin(x);
    float actual = uv.y;

    //Apply weird transform, then wrap to [-1,1]
    expected = weirdWrapFunk(expected + (iTime * (SCROLL_SPEED)));
    expected = mod(expected, 2.0) - 1.0;
    actual = weirdWrapFunk(actual + (iTime * FUNK_SPEED));
    actual = mod(actual, 2.0) - 1.0;


    return abs(expected - actual) < 0.1;
}

