#define MAX_STEPS 300
#define MAX_DIST 75.
#define SURF_DIST .001

//abs(sin(tan(p.z)*sin(p.y/p.x)/tan(iTime)))

float GetDist(vec3 p)
{
	vec4 s = vec4(cos(iTime), 0., sin(iTime) *2.+ 2.5, abs(sin(tan(p.z)*sin(p.y/p.x)/tan(iTime))));

    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = sin(length(p.xy)) + cos(p.z /p.y + iTime * 3.) +1.;

    return min(sphereDist, planeDist);

}

float RayMarch(vec3 ro, vec3 rd)
{
	float dO = 0.;
    for(int i = 0; i < MAX_STEPS; i++)
    {
    	vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p)
{
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
    	GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx)    
    );

    return normalize(n);
}


float GetLight(vec3 p)
{
    vec3 lightPos = vec3(0., 3., .0);
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float d = RayMarch(p+n*SURF_DIST*5., l);

  	float dif = dot(l, n);

    if(d < length(lightPos - p))
       dif *= smoothstep(0., .7, d);

	return dif;	
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    //sound
    int tx = int((uv.x + 1.)*512.0/2.);
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

   	vec3 col = vec3(uv.x, uv.y, 1.);

    vec3 ro = vec3(0.,.1, -10);
    vec3 lp = vec3(0., 0., 2.);
    vec3 f = normalize(lp - ro);
    vec3 r = cross(f, vec3(.0, -1., .0));
    vec3 u = cross(f, r);

    float zoom = 2.;

    vec3 i = (ro + f * zoom) + uv.x * r + uv.y*u;

    vec3 rd = normalize(i - ro);

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float l = GetLight(p);
    col = l * vec3(.5,.6,.4);
	col *= sin(uv.x/sin(uv.x/sin(iTime)*uv.y*30.)) * tan(sin(uv.y/uv.x*-300.*sin(iTime))) + .2;
    col.g /= abs(cos(iTime/2.)* uv.x/uv.y/abs(sin(iTime))) + .5;
    col.r /= abs(sin(iTime/4.)* uv.y/uv.x/abs(cos(iTime)))+ .5;
    col.b /= ((wave * wave)-.5)*6.+sin(cos(iTime*uv.x*uv.x)/(uv.y*uv.y*tan(iTime)));



    fragColor = vec4(col,1.0);
}

