const float pi = 3.141592;
const float tau = 2.0*pi;

//#define TIME 0.5
#define TIME iTime/2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cells = 50.0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv * cells;

    vec2 offset = fract(p);
    vec2 cell = floor(p);
    vec2 ro = vec2(20, 20);
    vec2 rd = normalize(vec2(sin(TIME), cos(TIME)));

    vec2 hyp = p - ro; //hypotenuse
    float theta = acos(dot(hyp, rd) / length(hyp)); //angle between ray and p

    float d = length(hyp) * sin(theta);

    bool posT = theta <= tau/4.0 || theta > 3.0*tau/4.0;
    float ray = posT ? 1.0-smoothstep(d, 0.00, 0.007) : 0.0;



    float phi = mod(atan(rd.y, rd.x), tau);//angle between ray and horizontal
        //voxel intersection algorithm
    float x = floor(ro.x);
    float y = floor(ro.y);
    float stepX = rd.x > 0.0 ? 1.0 : -1.0;
    float stepY = rd.y > 0.0 ? 1.0 : -1.0;

    float tMaxX, tMaxY;
    if (phi < tau/4.0 || phi >= 3.0*tau/4.0)
    	tMaxX = (1.0 - fract(ro.x)) / cos(phi);
	else
        tMaxX = fract(ro.x) / cos(pi - phi);

    if (phi < tau/2.0)
    	tMaxY = (1.0 - fract(ro.y)) / sin(phi);
    else
        tMaxY = fract(ro.y) / sin(tau - phi);

    float tDeltaX = stepX * 1.0 / cos(phi);
    float tDeltaY = stepY * 1.0 / sin(phi);
        vec4 currentCell = vec4(0,0,0,0);

    int maxTrace = 50;
    if (cell == vec2(x,y)) currentCell += vec4(1.0 / float(maxTrace), 1.0 / float(maxTrace), 0, 0);

    for (int i = 0; i < maxTrace-1; i++) {
        if (tMaxX < tMaxY) {
            tMaxX += tDeltaX;
            x += stepX;
        } else {
            tMaxY += tDeltaY;
            y += stepY;
        }
        if (cell == vec2(x,y)) currentCell += vec4(float(i+2) / float(maxTrace), float(i+2) / float(maxTrace), 0, 0);
    }

    float t =tMaxY + 2.0*tDeltaY*stepY;
    vec2 point = ro + t*rd;

    float target = smoothstep(0.1, 0.0, length(p-point));

    int index = int(cell.y*6.0 + cell.x );
    bool inBounds =  cell.x < 6.0 && cell.y < 6.0;

    vec4 cellColor = inBounds ? (data[index] ? vec4(1, 0, 0, 0) : vec4(0,0,0,0)) : vec4(0,0,0,0);



    //fragColor = cellColor + vec4(0, target, target, 0) + 0.7*(vec4(ray) + vec4(cell / cells, 0, 0));
    fragColor = ray + currentCell + vec4(cell / cells, 0, 0);
}

