float sdRoundedBox( vec2 p, vec2 b, vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}


float drawBox(vec2 uv, vec2 p, vec2 s)
{
    return step(1.0,1.0-sdRoundedBox(uv - p, vec2(.23,.35) * s * 0.7, vec4(0.1) * length(s * 0.7)));
}

