    float sharpness = pow(asin(sin(iTime*2.5))/PI+1.,8.);
    float offset = sin(iTime*0.65)*0.25+0.25;

    float dist = rounded_square(uv, radius, sharpness, offset).x;
    fragColor = vec4(shadeDistance(dist), 1.);

