float DE(vec3 p) {


    float scale = 4.435;




    float dist =sdBox(p- vec3(0.0), vec3(1.0) );


	for (int i = 0; i < MENGER_ITERATIONS; i++) {

        dist = max(dist, sdCrossRepScale(p, scale)+sdCrossRepScalev2(p, scale+0.0125));
		scale *= 3.0;
	}




	return dist;
}

