#define time iTime
#define PI 3.141592
#define TAU (2.*PI)
#define ratio vec2(1.,0.9)
#define dt (mod(time*PI/5.,TAU))

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

float hash21(vec2 x)
{return fract(sin(dot(x,vec2(12.4,23.1)))*1245.4);}

vec2 hash22(vec2 v) 
{ return fract(sin(vec2(v.x*v.x, v.x*v.y)*245.314)*44.129);}


// reference for animation curves: https://easings.net/
float easeInOutCirc(float x)
{
    return x < 0.5 ? 
        (1. - sqrt(1. - pow(2. * x, 2.))) / 2. : 
    (sqrt(1. - pow(-2. * x + 2., 2.)) + 1.) / 2.;
}

vec3 scale_grid(vec2 uv, float detail)
{
    uv *=detail;

    vec2 ga = mod(uv, ratio)-ratio*0.5;
    vec2 gb = mod(uv-ratio*0.5, ratio)-ratio*0.5;
    vec2 guv = (dot(ga,ga)<dot(gb,gb))? ga:gb;
    float sdist = max(abs(guv).x, dot(abs(guv),normalize(ratio)));
    vec2 gid = uv-guv;
    return vec3(gid,sdist);
}


mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z))) + length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float prim1 (vec3 p)
{
    return cyl(p.xzy, 0.5+sin(p.y*1.5)*0.4, 1.);
}

#define anim(ti,u) easeInOutCirc(sin(length(u)+ti)*0.5+0.5)

vec3 new_p;
float g1 = 0.;
float SDF (vec3 p)
{ 
    p.y += sin(dt)*0.1;
    new_p = p;
    vec2 cuv = vec2(atan(p.z,p.x),p.y);
    vec3 sg = scale_grid(cuv, 2.+anim(dt,p));
    float mask = sg.z*anim(dt,p);
    mask = clamp(1.-mask,0.,1.);
    float d = mix(prim1(p), length(p)-.7, mask);
    g1 += 0.003/(0.003+d*d);
    return d;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float spec (vec3 rd, vec3 n, vec3 l)
{
    vec3 h = normalize(l-rd);
    return pow(max(dot(h,n),0.),20.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash21(uv);

    // formula by alkama <3
    vec3 bg = vec3(pow(
        			smoothstep(0.045*abs(floor(uv*80.).y),
                               .0,
                               length((fract(uv*80.)+vec2(sin(dt),cos(sin(dt))))-hash22(floor(uv*80.)))),
        			10.)
                  );

    vec3 ro = vec3(0.,0.,-2.7),
        rd = normalize(vec3(uv,1.)),
        p = ro,
        l = vec3(1.,2.,-3.),
        col = bg;

    float shad; bool hit=false;

    for(float i=0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            hit = true;
            shad = i/64.;
            break;
        }
        d *= 0.6+dither*0.1;
        p += d*rd;
    }
    if (hit)
    {
        vec3 n = getnorm(p);
        col = vec3(shad);
        col += spec(rd, n, l);
    }
    float glow_col = clamp(length(new_p.xy*0.5),0.,1.);
    col = mix(col, col+g1*vec3(glow_col,glow_col*0.4,0.)*texNoise(uv*0.5).r, anim(dt,uv));

    fragColor = vec4(clamp(col,0.,1.),1.);
}

float circle(vec2 uv, float radius, float blur,  vec2 pos)
{

    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(.5 * ratio, .5);
    vec2 wingPos = vec2(.5 * ratio, .4);
	vec2 windowPos = vec2(.5 * ratio, .6);
    vec2 finPos = vec2(.5 * ratio, .7 );

    float dist = distance(shapePos, uv) * 2.0;

    vec2 uv1 = uv;
    uv1 -= shapePos;

    float angle = atan(uv1.y, uv1.x) + iTime;

    float back = circle(uv, .23, 0., shapePos);
    float cap  = circle(uv, .06, 0., shapePos);
    float window = circle(uv, .18, 0., windowPos);

    float wings = rect(uv, wingPos, vec2(1.3, .15));
    float fin = rect(uv, finPos, vec2(.07, .3));

    float radius = sin((angle+iTime*10.)*4.);

    radius *= .6;

    float value = 1.-smoothstep(radius, radius +.1, dist);

    vec3 color;
    color = mix(vec3(.5058, .7686, .9411), vec3(.5215, .0705, .0705),fin);
    color = mix(vec3(.6509, .9686, .9686), color,  window);
    color = mix(vec3(.5215, .0705, .0705), color, back);
    color = mix(color, vec3(.5215, .0705, .0705), wings);
    color = mix(color, vec3(.2), value);
    color = mix(vec3(.9), color, cap);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

vec3 colors [5];
float points [5];

void initia(){
    colors[0]=vec3(1.5,0.,0.6);
    colors[1]=vec3(0.,1.,1.);
    colors[1]=vec3(0.0,1.0,0.);
    colors[3]=vec3(1.0,1.0,0.);
    colors[4]=vec3(1.0,0.0,0.);
    points[0]=0.2;
    points[1]=0.15;
    points[2]=0.5;
    points[1]=.5;
    points[4]=1.5;
}
vec3 gradian(vec3 c1,vec3 c2,float a){
    return vec3(c1.x+a*(c2.x-c1.x),
                c1.y+a*(c2.y-c1.y),
                c1.z+a*(c2.z-c1.z));
}

vec3 heat4(float weight){
    if(weight<=points[0]){
        return colors[0];
    }
    if(weight>=points[4]){
        return colors[4];
    }
    for(int i=1;i<5;i++){
        if(weight<points[i]){
           float a=(weight-points[i-2])/(points[i]-points[i-1]);
            return gradian(colors[i-1],colors[i],a);
        }
    }
}


float d(vec2 a, vec2 b) {
   return  pow(max(0.0, 1.0 - distance(a, b) / (0.6)), 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    initia();
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 4.0 - vec2(2.0);
   uv.x *= iResolution.x / iResolution.y;

    float totalWeight = 0.0;
    for (float i = 0.0; i < 117.0; ++i) {

    	totalWeight += 0.5*d(uv, vec2(
            sin(1.0*iTime * 1.6 + float(i))*2. + 2.*sin(i * i), 
            cos(1.0*iTime * 1.4 + float(i *1.5))*2.
        ));
    }


	fragColor = vec4(heat4(totalWeight), 1.3);
}

