vec2 distIgSegmentIt( vec2 xy, float size, vec2 dimensions )
{

//. Ambifix is a "notation" I (@Yambam) invented, my goal is to make learning and making use of maths more intuitive.
//: Comments like this these are statements in a hypothetical shader language based on ambifix, with the values omitted.

    //if (xy.x<0.-1.) return vec2(1000.,-1.);
    xy.x = clamp(xy.x,-1.,2.); //clamp((xy.x/size+1.)/(1./size+1.),0.,1.);
    xy.y = clamp(xy.y,0.,1.); //clamp(xy.y,-size,1.+size);

    float s = dimensions.y*round(xy.x*dimensions.x)+floor(xy.y*dimensions.y);
    //float s = round(xy.x*dimensions.x)+(dimensions.x+1.)*floor(xy.y*dimensions.y);
    float px = round(xy.x*dimensions.x)/dimensions.x;
    float py = floor(xy.y*dimensions.y)/dimensions.y;
    //4.00*clamp(-.2+1.4*iMouse.x/iResolution.x,0.,1.)
    vec2 offset =   vec2(xy.x,           xy.y-py)
                  - vec2(clamp(px,0.,1.),clamp(xy.y-py,0.+size,1./dimensions.y-size));
    float d = 2.*abs(offset.x)+1.*abs(offset.y);
    //float d = 2.*length(offset);

    //Old code:
    //float d =  4.00*(abs(xy.x   -clamp(px,0.,1.))
    //          +1.00* abs(xy.y-py-clamp(xy.y-py,0.+size,1./dimensions.y-size)));
//. //float d =           (-        (clamp  ) -) (abs)
//: //          (+ (-  -) (- (-  -) (clamp  ) -) (abs) +)

    //aligned for comparison of GLSL ES infix code versus (hypothetical) ambifix code
    //
   ///                   xy.x                   px                   0.,1.
   ///                   xy.y    py             xy.y    py           0.,.5-size
   //a  float d =   (abs(               - clamp(            ,                   )        )
   //a             + abs(      -        - clamp(      -     ,                   )        ));
//.//a  float d =                      (-                     (clamp            ) -) (abs)
//://a            (+          (-    -) (-            (-    -) (clamp            ) -) (abs) +)
   ///                   xy.x                   px                   0.,1.
   ///                   xy.y    py             xy.y    py           0.,.5-size
    //
	//

    return vec2(d,s);
}

float colorRefreshRateArtifact( vec2 uv )
{
    return clamp(.8+.2*(.5+.5*sin(TAU*(5./2.*iTime+uv.y+1./16.*uv.x))*sin(TAU*(7./2.*iTime+uv.y+1./16.*uv.x))),0.,1.);
}

vec2 digIt( vec2 xy, int state, vec2 margin, float size, vec2 dimensions )
{
    vec2 segmented = vec2(size,-1.);
    vec2 segmentedBest = segmented;
    vec2 space = -margin;
    vec2 xyf = space +(vec2(1,1)-2.*space)*xy;
    vec2 xyfR = vec2(xyf.y,xyf.x);
    float diag = sqrt(.5); //1.; //
    float sFactor = (dimensions.x+1.)*dimensions.y;

    if (xyf.x<0.-.5*size
    ||  xyf.y<0.-.5*size
    ||  xyf.x>1.+.5*size
    ||  xyf.y>1.+.5*size) return segmentedBest;


    /*xysym = vec2(abs(xy.y-.5)+abs(xy.x-.5),abs(abs(xy.y-.5)-abs(xy.x-.5)));
	//xysym = vec2((xysym.x-xysym.y)/4.+.5,(xysym.x+xysym.y)/4.+.5);
    segmented = distIgSegmentIt(vec2(xysym.x,xysym.y),size,dimensions);
    if (segmented.x<=segmentedBest.x)
        segmentedBest = segmented+float(abs(xyf.x)>abs(xyf.y));*/

    segmented = distIgSegmentIt(xyf,size,dimensions);
    if (segmented.x<=segmentedBest.x)
        segmentedBest = segmented;
    segmented = distIgSegmentIt(xyfR,size,dimensions);
    if (segmented.x<=segmentedBest.x)
        segmentedBest = segmented+sFactor*vec2(0.,1.);

    vec2 xyrot = xy;
    xyrot -= vec2(.5,.5);
    xyrot *= vec2(sign(xy.x-.5)*sign(xy.y-.5),1.);
    xyrot = .5*(vec2( xyrot.x,-xyrot.y)
               +vec2(-xyrot.y,-xyrot.x));
	xyrot += vec2(.5,.5);

    segmented = distIgSegmentIt(space+(vec2(1,1)-2.*space)*vec2(xyrot.x,xyrot.y),size,dimensions);
    segmented = vec2(segmented.x/diag,segmented.y-(sFactor-dimensions.y)/2.);
    if (segmented.y>=0.
    &&  segmented.y<dimensions.y
    &&  segmented.x<=segmentedBest.x)
        segmentedBest = vec2(segmented.x,sFactor*2.+segmented.y+(sFactor-2.*dimensions.x)*max(0.,sign(xy.x-.5)*sign(xy.y-.5)));

    return segmentedBest; // /vec2(1.,4.*sFactor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    /*
     * Display customization
     */

    vec2 margin = vec2(.15,.15);
    float size = .15;
    float digitCount = 3.;
    float skew = .1; //.1*sin(TAU/6.*iTime);
    vec2 dimensions = vec2(2.,2.);

    //// A variety of display colors you can choose from

    vec3 colTheme = vec3(1.,.15,0.); //<-- Red
    //vec3 colTheme = vec3(0.,1.,0.);  //<-- Green
    //vec3 colTheme = vec3(1.,.8,0.);  //<-- Yellow
    //vec3 colTheme = vec3(.8,1.,0.);  //<-- Neon yellowish green
    //vec3 colTheme = vec3(1.,.6,0.);  //<-- Orange
    vec3 colBg = vec3(0,0,0);

    float a = -atan(skew); //asin(skew);
    mat2 m = mat2(cos(a),-sin(a),
                  sin(a), cos(a));
    float skewNew = cos(a)-sin(a);

    vec2 ch = fragCoord/iResolution.x;
    //vec2 ch = (fragCoord+vec2(0.,iResolution.y/2.))/iResolution.x;
    ch *= (digitCount-1.+skewNew); //digitCount; //
    ch = m * ch;

    ch *= distance(vec2(0.,0.),vec2(1.,skew));
    vec3 col;
    ch.y -= skew*ch.x;

    /* 
     * Color gradient
     */
    //vec3 colTheme = (1.-ch.y)*vec3(1,.4,.4)+ch.y*vec3(.4,.4,1);
    //vec3 colBg = vec3(1,1,1);

    float aa = iResolution.x/200.; //30.*iMouse.x/iResolution.x;
    int i = int(floor(ch.x));
    float crra = colorRefreshRateArtifact(uv);
    float t;

    if (ch.x<0.
    ||  ch.x>digitCount) col = colBg;
    else
    {
        ch.x -= floor(ch.x);
        vec2 doughdge = digIt(ch,0,margin,size,dimensions);
        if (doughdge.y>=0.)
        {
            //if (((digits[0])&(1<<int(doughdge.y)))>0) //0-65536
            //if (((1<<int(16./iResolution.x*iMouse.x))&(1<<int(doughdge.y)))>0) //0-65536
            if ((int(iDate.w)&(1<<int(doughdge.y)))>0) //0-65536
            //if ((int(iDate.w)&(1<<int(doughdge.y)))>0) //0-65536
            //if (((int(iDate.w)>>(16*i))&(1<<int(doughdge.y)))>0) //0-65536
        		t = (.5+.5/15.*doughdge.y)*crra;
            else
        		t = .15*crra;
            t *= smoothstep(0.,1.,clamp(1.-10.*doughdge.x,0.,1./aa)*aa);
            //t *= smoothstep(0.,1.,1.-8.*doughdge.x);
            //t *= (1.-pow(2.,-2.+30.*doughdge.x));
            t = clamp(t,0.,1.);
        }
        else
            t = 0.;

        col = (1.-t)*colBg+t*colTheme;
    }

    fragColor = vec4(col,1.);
}

