float sdTorus( vec4 p, vec2 t )
{
  vec3 q = vec3(length(p.xw)-t.x, p.y, p.z);
  return length(q)-t.y;
}


float terrain(vec4 pos) {
    return snoise(pos*2.);
    //return .5-length(pos-.5);
    //return -sdTorus(pos-.5, vec2(.4,.2));
}

float intersection(vec4 b0, vec4 b1, vec4 ro, vec4 rid) {
    float tx1 = (b0.x - ro.x)*rid.x;
    float tx2 = (b1.x - ro.x)*rid.x;
    float tmin = min(tx1, tx2);
    float tmax = max(tx1, tx2);
    float ty1 = (b0.y - ro.y)*rid.y;
    float ty2 = (b1.y - ro.y)*rid.y;
    tmin = max(tmin, min(ty1, ty2));
    tmax = min(tmax, max(ty1, ty2));
    float tz1 = (b0.z - ro.z)*rid.z;
    float tz2 = (b1.z - ro.z)*rid.z;
    tmin = max(tmin, min(tz1, tz2));
    tmax = min(tmax, max(tz1, tz2));
    float tw1 = (b0.w - ro.w)*rid.w;
    float tw2 = (b1.w - ro.w)*rid.w;
    tmin = max(tmin, min(tw1, tw2));
    tmax = min(tmax, max(tw1, tw2));
    return (tmax >= tmin && tmin > 0.) ? tmin : -1.;
}

vec4 computeNormal(vec4 inter, vec4 center) {
  vec4 a = inter - center;
  a = abs(a);
  return vec4(greaterThanEqual(a.xyzw, max(a.yzwx, max(a.zwxy, a.wxyz))));
}

#define GRID_SIZE 16.

vec4 trace(vec4 ro, vec4 rd, inout float t) {
    vec4 rid = 1./rd;
    if (ro != clamp(ro, 0., 1.)) {
        float tmin = intersection(vec4(0), vec4(1), ro, rid);
        if (tmin == -1.) {return vec4(0.);}
        ro = ro + rd*(tmin+1e-4);
        t += tmin;
    }
    vec4 grid = floor( ro*GRID_SIZE );
    vec4 grid_step = sign( rd );
	vec4 corner = max( grid_step, vec4(0) );

    vec4 inv = vec4( 1.0 ) / rd;
	vec4 ratio = ( grid + corner - ro*GRID_SIZE ) * inv;
	vec4 ratio_step = grid_step * inv;

    vec4 mask = computeNormal(ro, vec4(.5));

    for (int i=0; i<32; i++) {
        if (grid != clamp(grid, 0., GRID_SIZE-1.)) {return vec4(0.);}
        if (terrain(vec4(grid)/GRID_SIZE) > 0.) {
            t += dot( ratio - ratio_step, mask )/GRID_SIZE;
            return mask*grid_step;
        }

        mask = vec4(lessThanEqual(ratio.xyzw,min(ratio.yzwx, min(ratio.zwxy, ratio.wxyz))));
		grid  += grid_step  * mask;
		ratio += ratio_step * mask;
    }
    return vec4(0.);
}

