vec3 rotateXY(vec3 v, float a) {
	float c = cos(a), s = sin(a);
	return mat3(
		c, s, 0,
		-s, c, 0,
		0, 0, 1
	)*v;
}
vec3 rotateXZ(vec3 v, float a) {
	float c = cos(a), s = sin(a);
	return mat3(
		c, 0, s,
		0, 1, 0,
		-s, 0, c
	)*v;
}
vec3 rotateYZ(vec3 v, float a) {
	float c = cos(a), s = sin(a);
	return mat3(
		1, 0, 0,
		0, c, s,
		0, -s, c
	)*v;
}

